\section{The long-time existence of the twisted K\"ahler-Ricci flow with weak initial data}
\setcounter{equation}{0}

In this section, we prove the long-time existence and uniqueness of the twisted K\"ahler-Ricci flow $(\ref{TKRF1})$ by following Song-Tian's arguments in \cite{JSGT}. For further consideration in the next section, we shall pay attention to the estimates which are independent of $\varepsilon$. In the following arguments, for the sake of brevity, we only consider the flow $(\ref{TKRF1})$ in the case of $\lambda=1$  (i.e. $\mu=\beta$), where $\beta\in (0,1)$. Our arguments are also valid for any $\lambda$, only if the coefficient $\beta$ before $\omega_\varepsilon(t)$ in the case of $\lambda=1$ is replaced by $\mu=1-(1-\beta)\lambda$. We denote
 \begin{eqnarray*}
 F=\frac{(\omega_{0}+\sqrt{-1}\partial\bar{\partial}\varphi_{0})^{n}}{\omega_{0}^{n}}\in L^{p}(M,\omega_{0}^{n}).
 \end{eqnarray*}
Recall that $C^{\infty}(M)$ is dense in $L^{p}(M,\omega_{0}^{n})$. Therefore there exists a sequence of positive functions $F_{j}\in C^{\infty}(M)$ such that $\int_{M}F_{j}\omega_{0}^{n}=\int_{M}\omega_{0}^{n}$ and
\begin{eqnarray*}
\lim\limits_{j\rightarrow\infty}\|F_{j}-F\|_{L^{p}(M)}=0.
\end{eqnarray*}
By considering the complex Monge-Amp\`ere equation
\begin{eqnarray}
(\omega_{0}+\sqrt{-1}\partial\bar{\partial}\varphi_{0,j})^{n}=F_{j}\omega_{0}^{n}
\end{eqnarray}
and using the stability theorem in \cite{K00} (see also \cite{SDZZ} or \cite{VGAZ1}), we have
\begin{eqnarray}\label{00000}
\lim\limits_{j\rightarrow\infty}\|\varphi_{0,j}-\varphi_{0}\|_{L^{\infty}(M)}=0,
\end{eqnarray}
where $\varphi_{0,j}\in PSH(M,\omega_0)\cap C^\infty(M)$ satisfy
$\sup\limits_{M}(\varphi_0-\varphi_{0,j})=\sup\limits_{M}(\varphi_{0,j}-\varphi_{0})$.



Let $\omega_{\varphi_{0,j}}=\omega_0+\sqrt{-1}\partial\overline{\partial}\varphi_{0,j}$. We prove the long-time existence of the twisted K\"ahler-Ricci flow $(\ref{TKRF1})$ by using a sequence of smooth twisted K\"ahler-Ricci flows
\begin{eqnarray}\label{TKRF2}
\begin{cases}
  \frac{\partial \omega_{\varepsilon,j}(t)}{\partial t}=-Ric(\omega_{\varepsilon,j}(t))+\beta\omega_{\varepsilon,j}(t)+\theta_{\varepsilon}.\\
  \\
  \omega_{\varepsilon,j}(t)|_{t=0}=\omega_{\varphi_{0,j}}\\
  \end{cases}
\end{eqnarray}

Since the twisted K\"ahler-Ricci flow preserves the K\"ahler class, we can write the flow $(\ref{TKRF2})$ as the parabolic Monge-Amp\'ere equation on potentials,
\begin{eqnarray}\label{CMAE1}
\begin{cases}
  \frac{\partial \varphi_{\varepsilon,j}(t)}{\partial t}=\log\frac{(\omega_{0}+\sqrt{-1}\partial\bar{\partial}\varphi_{\varepsilon,j}(t))^{n}}{\omega_{0}^{n}}+F_{0}+\beta\varphi_{\varepsilon,j}(t)
  +\log(\varepsilon^{2}+|s|_{h}^{2})^{1-\beta},\\
  \\
  \varphi_{\varepsilon,j}(0)=\varphi_{0,j}\\
  \end{cases}
\end{eqnarray}
where $F_{0}$ satisfies $-Ric(\omega_{0})+\omega_{0}=\sqrt{-1}\partial\overline{\partial}F_{0}$, $\frac{1}{V}\int_{M}e^{-F_{0}}dV_{0}=1$ and $dV_{0}=\frac{\omega_{0}^{n}}{n!}$. By using the function
\begin{eqnarray}\chi(\varepsilon^{2}+|s|_h^2)=\frac{1}{\beta}\int_{0}^{|s|_h^2}\frac{(\varepsilon^{2}+r)^{\beta}-
\varepsilon^{2\beta}}{r}dr
\end{eqnarray}
which was given by F. Campana, H. Guenancia and M. P$\breve{a}$un in \cite{CGP}, we can rewrite the flow (\ref{CMAE1}) as
\begin{eqnarray}\label{CMAE2}
\begin{cases}
  \frac{\partial \phi_{\varepsilon,j}(t)}{\partial t}=\log\frac{(\omega_{\varepsilon}+\sqrt{-1}\partial\bar{\partial}\phi_{\varepsilon,j}(t))^{n}}{\omega_{\varepsilon}^{n}}+F_{\varepsilon}+
  \beta(\phi_{\varepsilon,j}(t)+k\chi(\varepsilon^2+|s|_h^2)),\\
  \\
  \phi_{\varepsilon,j}(0)=\varphi_{0,j}-k\chi(\varepsilon^2+|s|_h^2):=\phi_{\varepsilon,0,j}\\
  \end{cases}
\end{eqnarray}
where $\phi_{\varepsilon,j}(t)=\varphi_{\varepsilon,j}(t)-k\chi(\varepsilon^2+|s|_h^2)$, $\omega_{\varepsilon}=\omega_{0}+\sqrt{-1}k\partial\overline{\partial}\chi(\varepsilon^{2}+|s|_{h}^{2})$, $F_{\varepsilon}=F_{0}+\log(\frac{\omega_{\varepsilon}^{n}}{\omega_{0}^{n}}\cdot(\varepsilon^{2}+|s|_{h}^{2})^{1-\beta})$. We know that $\chi(\varepsilon^2+|s|_h^2)$ and $F_{\varepsilon}$ are uniformly bounded (see $(15)$ and $(25)$ in \cite{CGP}).

\medskip

\begin{pro}\label{200} For any $T>0$, there exists constant $C$ depending only on $\|\varphi_0\|_{L^\infty(M)}$, $\beta$, $n$, $\omega_{0}$ and $T$ such that for any $t\in[0,T]$, $\varepsilon>0$ and $j\in\mathbb{N}^+$,
\begin{eqnarray}\|\phi_{\varepsilon,j}(t)\|_{L^\infty(M)}\leq C.
\end{eqnarray}
Furthermore, for any $j, k$, we have
\begin{eqnarray}\label{201}
\|\phi_{\varepsilon,j}(t)-\phi_{\varepsilon,k}(t)\|_{L^\infty([0,T]\times M)}\leq e^{\beta T}\|\varphi_{0,j}-\varphi_{0,k}\|_{L^\infty(M)}.
\end{eqnarray}
In particular, $\{\varphi_{\varepsilon,j}(t)\}$ satisfies
\begin{eqnarray}\label{2001}\lim\limits_{j.k\rightarrow\infty}\|\varphi_{\varepsilon,j}(t)-\varphi_{\varepsilon,k}(t)\|_{L^{\infty}([0,T]\times M)}=0.
\end{eqnarray}

\end{pro}

\medskip

{\bf Proof:}\ \ From equation $(\ref{CMAE2})$, we have
\begin{eqnarray*}
\frac{\partial e^{-\beta t}\phi_{\varepsilon,j}(t)}{\partial t}&=&e^{-\beta t}\log\frac{(e^{-\beta t}\omega_{\varepsilon}+\sqrt{-1}\partial\bar{\partial}e^{-\beta t}\phi_{\varepsilon,j}(t))^{n}}{(e^{-\beta t}\omega_{\varepsilon})^{n}}\\
&\ &+e^{-\beta t}(F_{\varepsilon}+k\beta\chi(\varepsilon^2+|s|_h^2))\\
&\leq&e^{-\beta t}\log\frac{(e^{-\beta t}\omega_{\varepsilon}+\sqrt{-1}\partial\bar{\partial}e^{-\beta t}\phi_{\varepsilon,j}(t))^{n}}{(e^{-\beta t}\omega_{\varepsilon})^{n}}+Ce^{-\beta t},
\end{eqnarray*}
which is equivalent to
\begin{eqnarray*}
\frac{\partial }{\partial t}\big(e^{-\beta t}(\phi_{\varepsilon,j}(t)+\frac{C}{\beta})\big)\leq e^{-\beta t}\log\frac{\big(e^{-\beta t}\omega_{\varepsilon}+\sqrt{-1}\partial\bar{\partial}e^{-\beta t}(\phi_{\varepsilon,j}(t)+\frac{C}{\beta})\big)^{n}}{(e^{-\beta t}\omega_{\varepsilon})^{n}},
\end{eqnarray*}
where constant $C$ depends only on $\|\varphi_0\|_{L^\infty(M)}$, $\beta$, $n$ and $\omega_{0}$.

For any $\delta>0$, we denote $\tilde{\phi}_{\varepsilon,j}(t)=e^{-\beta t}(\phi_{\varepsilon,j}(t)+\frac{C}{\beta})-\delta t$. Let $(t_0,x_0)$ be the maximum point of $\tilde{\phi}_{\varepsilon,j}(t)$ on $[0,T]\times M$. If $t_0>0$, by maximum principle, we have
\begin{eqnarray*}
0&\leq&\frac{\partial }{\partial t}\big(e^{-\beta t}(\phi_{\varepsilon,j}(t)+\frac{C}{\beta})\big)(t_0,x_0)-\delta\\
&\leq& e^{-\beta t}\log\frac{\big(e^{-\beta t}\omega_{\varepsilon}+\sqrt{-1}\partial\bar{\partial}\tilde{\phi}_{\varepsilon,j}(t)\big)^{n}}{(e^{-\beta t}\omega_{\varepsilon})^{n}}(t_0,x_0)-\delta\\
&\leq&-\delta,
\end{eqnarray*}
which is impossible. Hence $t_0=0$, then
\begin{eqnarray*}
\phi_{\varepsilon,j}(t)\leq e^{\beta t}\sup\limits_{M}\phi_{\varepsilon,j}(0)+\delta Te^{\beta T}+\frac{C}{\beta}(e^{\beta T}-1).
\end{eqnarray*}
Let $\delta\rightarrow0$, we obtain
\begin{eqnarray}\label{202}
\phi_{\varepsilon,j}(t)\leq e^{\beta t}\sup\limits_{M}\phi_{\varepsilon,j}(0)+\frac{C}{\beta}(e^{\beta T}-1).
\end{eqnarray}
By the same arguments, we can get the lower bound of $\phi_{\varepsilon,j}(t)$
\begin{eqnarray}\label{203}
\phi_{\varepsilon,j}(t)\geq e^{\beta t}\inf\limits_{M}\phi_{\varepsilon,j}(0)-\frac{C}{\beta}(e^{\beta T}-1).
\end{eqnarray}
Combining $(\ref{202})$ and $(\ref{203})$, we have
\begin{eqnarray*}
\|\phi_{\varepsilon,j}(t)\|_{L^\infty(M)}\leq e^{\beta T}\|\phi_{\varepsilon,j}(0)\|_{L^\infty(M)}+\frac{C}{\beta}(e^{\beta T}-1)\leq C,
\end{eqnarray*}
where constant $C$ depends only on $\|\varphi_0\|_{L^\infty(M)}$, $\beta$, $n$, $\omega_{0}$ and $T$.

Let $\psi_{\varepsilon,j,k}(t)=\phi_{\varepsilon,j}(t)-\phi_{\varepsilon,k}(t)$, then $\psi_{\varepsilon,j,k}$ satisfies the following equation
\begin{eqnarray}
\begin{cases}
  \frac{\partial \psi_{\varepsilon,j,k}(t)}{\partial t}=\log\frac{\big(\omega_{\varepsilon}+\sqrt{-1}\partial\bar{\partial}\phi_{\varepsilon,k}(t)+\sqrt{-1}\partial\bar{\partial}\psi_{\varepsilon,j,k}(t)\big)^{n}}
  {(\omega_{\varepsilon}+\sqrt{-1}\partial\bar{\partial}\phi_{\varepsilon,k}(t))^{n}}+
  \beta\psi_{\varepsilon,j,k}(t).\\
  \\
  \psi_{\varepsilon,j,k}(0)=\varphi_{0,j}-\varphi_{0,k}\\
  \end{cases}
\end{eqnarray}
By the same arguments as that in the first part, we have
\begin{eqnarray*}
\|\psi_{\varepsilon,j,k}(t)\|_{L^\infty([0,T]\times M)}\leq e^{\beta T}\|\varphi_{0,j}-\varphi_{0,k}\|_{L^\infty(M)}.
\end{eqnarray*}
Since $\{\varphi_{0,j}\}$ is a Cauchy in $L^\infty$-norm, we conclude
\begin{eqnarray*}\lim\limits_{j.k\rightarrow\infty}\|\varphi_{\varepsilon,j}(t)-\varphi_{\varepsilon,k}(t)\|_{L^\infty([0,T]\times M)}=0.
\end{eqnarray*}{\hfill\ensuremath{\square}}

We now prove the uniform equivalence of the volume forms along the complex Monge-Amp\`ere flow $(\ref{CMAE2})$.

\medskip

\begin{lem}\label{204} For any $T>0$, there exists constant $C$ depending only on $\|\varphi_0\|_{L^\infty(M)}$, $n$, $\beta$, $\omega_{0}$ and $T$ such that for any $t\in(0,T]$, $\varepsilon>0$ and $j\in\mathbb{N}^+$,
\begin{eqnarray}\frac{t^n}{C}\leq\frac{(\omega_{\varepsilon}+\sqrt{-1}\partial\bar{\partial}\phi_{\varepsilon,j}(t))^{n}}{\omega_{\varepsilon}^n}\leq e^{\frac{C}{t}}.
\end{eqnarray}
\end{lem}

\medskip

{\bf Proof:}\ \ Let $\Delta_{\varepsilon,j}$ be the Laplacian operator associated to the K\"ahler form $\omega_{\varepsilon,j}(t)=\omega_{\varepsilon}+\sqrt{-1}\partial\bar{\partial}\phi_{\varepsilon,j}(t)$. Straightforward calculations show that
\begin{eqnarray}(\frac{\partial}{\partial t}-\Delta_{\varepsilon,j})\dot{\phi}_{\varepsilon,j}(t)=\beta\dot{\phi}_{\varepsilon,j}(t).
\end{eqnarray}

Let $H_{\varepsilon,j}^+(t)=t\dot{\phi}_{\varepsilon,j}(t)-A\phi_{\varepsilon,j}(t)$, where $A$ is a sufficiently large number (for example $A=\beta T+2$). Then $H_{\varepsilon,j}^+(0)=-A\phi_{\varepsilon,j}(0)$ is uniformly bounded by a constant $C$ depending only on $\|\varphi_0\|_{L^\infty(M)}$, $\beta$, $n$, $\omega_{0}$ and $T$.
\begin{eqnarray}\nonumber(\frac{\partial}{\partial t}-\Delta_{\varepsilon,j})H_{\varepsilon,j}^+(t)&=&(1+\beta t-A)\dot{\phi}_{\varepsilon,j}(t)+A\Delta_{\varepsilon,j}\phi_{\varepsilon,j}(t)\\
&\leqslant&(1+\beta t-A)\dot{\phi}_{\varepsilon,j}(t)+An.
\end{eqnarray}
By the maximum principle, $H_{\varepsilon,j}^+(t)$ is uniformly bounded from above by a constant $C$ depending only on $\|\varphi_0\|_{L^\infty(M)}$, $n$, $\beta$, $\omega_{0}$ and $T$.

Let $H_{\varepsilon,j}^-(t)=\dot{\phi}_{\varepsilon,j}(t)+\phi_{\varepsilon,j}(t)-n\log t$. Then $H^-(t)$ tends to $+\infty$ as $t\rightarrow0^+$ and
\begin{eqnarray}(\frac{\partial}{\partial t}-\Delta_{\varepsilon,j})H_{\varepsilon,j}^-(t)=(\beta+1)\dot{\phi}_{\varepsilon,j}(t)+tr_{\omega_{\varepsilon,j}(t)}\omega_\varepsilon-n-\frac{n}{t}.
\end{eqnarray}

Assume that $(t_0,x_0)$ is the minimum point of $H_{\varepsilon,j}^-(t)$ on $[0,T]\times M$. We conclude that $t_0>0$ and there exists constant $C_1$, $C_2$ and $C_3$ such that
\begin{eqnarray}\label{2002}\nonumber(\frac{\partial}{\partial t}-\Delta_{\varepsilon,j})H_{\varepsilon,j}^-(t)|_{(t_0,x_0)}&\geq& \big(C_1(\frac{\omega_{\varepsilon}^n}{\omega^{n}_{\varepsilon,j}(t)})^{\frac{1}{n}}+C_2\log\frac{\omega^n_{\varepsilon,j}(t)}{\omega_{\varepsilon}^n}
-\frac{C_3}{t}\big)|_{(t_0,x_0)}\\
&\geq&\big(\frac{C_1}{2}(\frac{\omega_{\varepsilon}^n}{\omega^{n}_{\varepsilon,j}(t)})^{\frac{1}{n}}
-\frac{C_3}{t}\big)|_{(t_0,x_0)},
\end{eqnarray}
where constant $C_1$ depends only on $n$, $C_2$ depends only on $\beta$ and $C_3$ depends only on $n$, $\omega_{0}$, $\|\varphi_0\|_{L^\infty(M)}$, $\beta$ and $T$. In inequality $(\ref{2002})$, without loss of generality, we assume that $\frac{\omega_{\varepsilon}^n}{\omega^{n}_{\varepsilon,j}(t)}>1$ and $\frac{C_1}{2}(\frac{\omega_{\varepsilon}^n}{\omega^{n}_{\varepsilon,j}(t)})^{\frac{1}{n}}+C_2\log\frac{\omega^n_{\varepsilon,j}(t)}
{\omega_{\varepsilon}^n}\geq0$ at $(t_0,x_0)$. By the maximum principle, we have
\begin{eqnarray}\omega^{n}_{\varepsilon,j}(t_0,x_0)\geq C_4t^n \omega_{\varepsilon}^n(x_0),
\end{eqnarray}
where $C_4$ independent of $\varepsilon$ and $j$. Then it easily follows that $H_{\varepsilon,j}^-(t)$ is bounded from below by a constant $C$ depending only on $\|\varphi_0\|_{L^\infty(M)}$, $n$, $\beta$, $\omega_{0}$ and $T$.{\hfill\ensuremath{\square}}

\medskip

In the following lemma, we prove the uniform equivalence of the metrics along the twisted K\"ahler-Ricci flow $(\ref{TKRF2})$.

\medskip

\begin{lem}\label{205} For any $T>0$, there exists constant $C$ depending only on $\|\varphi_0\|_{L^\infty(M)}$, $n$, $\beta$, $\omega_{0}$ and $T$ such that for any $t\in(0,T]$, $\varepsilon>0$ and $j\in\mathbb{N}^+$,
\begin{eqnarray}e^{-\frac{C}{t}}\omega_\varepsilon\leq\omega_{\varepsilon,j}(t)\leq e^{\frac{C}{t}}\omega_\varepsilon.
\end{eqnarray}
\end{lem}

\medskip

{\bf Proof:}\ \ Let
\begin{eqnarray}\Psi_{\varepsilon,\rho}=B\frac{1}{\rho}\int_{0}^{|s|_h^2}\frac{(\varepsilon^{2}+r)^{\rho}-
\varepsilon^{2\rho}}{r}dr
\end{eqnarray}
be the uniform bound function introduced by H. Guenancia and M. P$\breve{a}$un in \cite{GP1}. By choosing suitable $B$ and $\rho$, and following the arguments in \cite{JWLXZ} (see section $2$ in \cite{JWLXZ}), we have
\begin{eqnarray}\nonumber&\ &(\frac{\partial}{\partial t}-\Delta_{\varepsilon,j})(t\log tr_{\omega_{\varepsilon}}\omega_{\varepsilon,j}(t)+t\Psi_{\varepsilon,\rho})\\
&\leq&\log tr_{\omega_{\varepsilon}}\omega_{\varepsilon,j}(t)+Ctr_{\omega_{\varepsilon,j}(t)}\omega_{\varepsilon}+C,
\end{eqnarray}
where constant $C$ depends only on $n$, $\beta$, $\omega_{0}$ and $T$.

Let $H_{\varepsilon,j}(t)=t\log tr_{\omega_{\varepsilon}}\omega_{\varepsilon,j}(t)+t\Psi_{\varepsilon,\rho}-A\phi_{\varepsilon,j}(t)$, $A$ be a sufficiently large constant and $(t_0,x_0)$ be the maximum point of $H_{\varepsilon,j}(t)$ on $[0,T]\times M$. We need only consider $t_0>0$. By the inequality
\begin{eqnarray}\label{206}
tr_{\omega_{\varepsilon}}\omega_{\varepsilon,j}(t)\leq\frac{1}{(n-1)!}(tr_{\omega_{\varepsilon,j}(t)}\omega_{\varepsilon})^{n-1}\frac{\omega^n_{\varepsilon,j}(t)}{\omega_{\varepsilon}^n},
\end{eqnarray}
we conclude that
\begin{eqnarray}\nonumber(\frac{\partial}{\partial t}-\Delta_{\varepsilon,j})H_{\varepsilon,j}(t)&\leq&\log tr_{\omega_{\varepsilon}}\omega_{\varepsilon,j}(t)+Ctr_{\omega_{\varepsilon,j}(t)}\omega_{\varepsilon}-A\dot{\phi}_{\varepsilon,j}(t)\\\nonumber
&\ &+C+An-Atr_{\omega_{\varepsilon,j}(t)}\omega_{\varepsilon}\\\nonumber
&\leq&\log tr_{\omega_{\varepsilon}}\omega_{\varepsilon,j}(t)-\frac{A}{2}tr_{\omega_{\varepsilon,j}(t)}\omega_{\varepsilon}-A\log\frac{\omega^n_{\varepsilon,j}(t)}{\omega_{\varepsilon}^n}+C\\\nonumber
&\leq&(n-1)\log tr_{\omega_{\varepsilon,j}(t)}\omega_{\varepsilon}-\frac{A}{2}tr_{\omega_{\varepsilon,j}(t)}\omega_{\varepsilon}-(A-1)\log\frac{\omega^n_{\varepsilon,j}(t)}{\omega_{\varepsilon}^n}+C,
\end{eqnarray}
where constant $C$ depends only on $\|\varphi_0\|_{L^\infty(M)}$, $n$, $\beta$, $\omega_{0}$ and $T$.

Without loss of generality, we assume that
$-\frac{A}{4}tr_{\omega_{\varepsilon,j}(t)}\omega_{\varepsilon}+(n-1)\log tr_{\omega_{\varepsilon,j}(t)}\omega_{\varepsilon}\leq0$ at $(t_0,x_0)$. Then at $(t_0,x_0)$, by Lemma \ref{204}, we have
\begin{eqnarray}(\frac{\partial}{\partial t}-\Delta_{\varepsilon,j})H_{\varepsilon,j}(t)\leq-\frac{A}{4}tr_{\omega_{\varepsilon,j}(t)}\omega_{\varepsilon}-C\log t+C.
\end{eqnarray}
By the maximum principle, at $(t_0,x_0)$,
\begin{eqnarray}tr_{\omega_{\varepsilon,j}(t)}\omega_{\varepsilon}\leq C\log \frac{1}{t}+C.
\end{eqnarray}
By using inequality $(\ref{206})$, at $(t_0,x_0)$,
\begin{eqnarray}tr_{\omega_{\varepsilon}}\omega_{\varepsilon,j}(t)\leq C(\log \frac{1}{t}+1)^{n-1}e^{\frac{C}{t}}\leq e^{\frac{2C}{t}},
\end{eqnarray}
where constant $C$ depends only on $\|\varphi_0\|_{L^\infty(M)}$, $n$, $\beta$, $\omega_{0}$ and $T$. Hence we have
\begin{eqnarray}\label{207}tr_{\omega_{\varepsilon}}\omega_{\varepsilon,j}(t)\leq e^{\frac{C}{t}}
\end{eqnarray}
for some constant $C$ depending only on $\|\varphi_0\|_{L^\infty(M)}$, $n$, $\beta$, $\omega_{0}$ and $T$.

Furthermore, by inequality $(\ref{206})$ again, we know
\begin{eqnarray}\label{208}tr_{\omega_{\varepsilon,j}(t)}\omega_{\varepsilon}\leq e^{\frac{C}{t}},
\end{eqnarray}
where constant $C$ depends only on $\|\varphi_0\|_{L^\infty(M)}$, $n$, $\beta$, $\omega_{0}$ and $T$. From $(\ref{207})$ and $(\ref{208})$, we prove the lemma.{\hfill\ensuremath{\square}}

\medskip

By Lemma \ref{205} and the fact that $\omega_\varepsilon>\gamma \omega_0$ for some uniform constant $\gamma$ (see inequality $(24)$ in \cite{CGP}), we have
\begin{eqnarray}e^{-\frac{C}{t}}\omega_0\leq\omega_{\varepsilon,j}(t)\leq C_\varepsilon e^{\frac{C}{t}}\omega_0,
\end{eqnarray}
on $(0,T]\times M$, where $C$ is a uniform constant and $C_\varepsilon$ depends on $\varepsilon$. We next prove the Calabi's $C^3$-estimates. Denote
\begin{eqnarray}S_{\varepsilon,j}=|\nabla_{\omega_0}\omega_{\varepsilon,j}(t)|^{2}_{\omega_{\varphi_{\varepsilon,j}(t)}}=
g_{\varepsilon,j}^{i\bar{m}}g_{\varepsilon,j}^{k\bar{l}}g_{\varepsilon,j}^{p\bar{q}}\nabla_{0 i}(g_{\varepsilon,j}) _{k\bar{q}}\overline{\nabla}_{0 m}(g_{\varepsilon,j})_{ p\bar{l}}.
\end{eqnarray}

\medskip

\begin{lem}\label{209} For any $T>0$ and $\varepsilon>0$, there exist constants $C_\varepsilon$ and $C$ such that for any $t\in(0,T]$ and $j\in\mathbb{N}^+$,
\begin{eqnarray}S_{\varepsilon,j}\leq C_\varepsilon e^{\frac{C}{t}},
\end{eqnarray}
where constant $C$ depends only on $\|\varphi_0\|_{L^\infty(M)}$, $n$, $\beta$, $\omega_{0}$ and $T$, and constant $C_\varepsilon$ depends in addition on $\varepsilon$.
\end{lem}

\medskip

{\bf Proof:}\ \ By the similar arguments in \cite{LW} or \cite{JWLXZ} and choosing sufficiently large $\alpha$ and $\beta$, we have
\begin{eqnarray}(\frac{\partial}{\partial t}-\Delta_{\varepsilon,j})(e^{-\frac{2\alpha}{t}}S_{\varepsilon,j})&\leq& C_\varepsilon e^{-\frac{\alpha}{t}}S_{\varepsilon,j}+C_\varepsilon,\\
(\frac{\partial}{\partial t}-\Delta_{\varepsilon,j})(e^{-\frac{2\gamma}{t}}tr_{\omega_0}\omega_{\varepsilon,j}(t))&\leq& C_\varepsilon -C_\varepsilon^{-1}e^{-\frac{3\gamma}{t}}S_{\varepsilon,j}.
\end{eqnarray}
By choosing $A_\varepsilon=C_\varepsilon(C_\varepsilon+1)$ and $\alpha=3\gamma$,
\begin{eqnarray}(\frac{\partial}{\partial t}-\Delta_{\varepsilon,j})(e^{-\frac{2\alpha}{t}}S_{\varepsilon,j}+A_\varepsilon e^{-\frac{2\gamma}{t}}tr_{\omega_0}\omega_{\varepsilon,j}(t))\leq - e^{-\frac{3\gamma}{t}}S_{\varepsilon,j}+C_\varepsilon.
\end{eqnarray}
By the maximum principle, we have
\begin{eqnarray}S_{\varepsilon,j}\leq C_\varepsilon e^{\frac{C}{t}}\ \ \ \ on\ \ \ (0,T]\times M
\end{eqnarray}
for some constant $C$ depending only on $\|\varphi_0\|_{L^\infty(M)}$, $n$, $\beta$, $\omega_{0}$ and $T$, and constant $C_\varepsilon$ depending in addition on $\varepsilon$.{\hfill\ensuremath{\square}}

\medskip

By using the Schauder regularity theory and equation $(\ref{CMAE1})$, we get the high order estimates of $\varphi_{\varepsilon,j}(t)$.
\medskip

\begin{pro}\label{210} For any $0<\delta<T<\infty$, $\varepsilon>0$ and $k\geq0$, there exists constant $C_{\varepsilon,\delta,T,k}$ depending only on $\delta$, $T$, $\varepsilon$, $k$, $n$, $\beta$, $\omega_{0}$ and $\|\varphi_0\|_{L^\infty(M)}$, such that for any $j\in\mathbb{N}^+$,
\begin{eqnarray}\|\varphi_{\varepsilon,j}(t)\|_{C^{k}\big([\delta,T]\times M\big)}\leq C_{\varepsilon,\delta,T,k}.
\end{eqnarray}
\end{pro}

\medskip

By $(\ref{2001})$, for any $T>0$, $\varphi_{\varepsilon,j}(t)$ converges to $\varphi_\varepsilon(t)\in L^\infty([0,T]\times M)$ uniformly in $L^\infty([0,T]\times M)$. For any $0<\delta<T<\infty$ and $\varepsilon>0$, $\varphi_{\varepsilon,j}(t)$ is uniformly bounded (depends on $\varepsilon$) in $C^{\infty}([\delta,T]\times M)$. Therefore $\varphi_{\varepsilon,j}(t)$ converges to $\varphi_\varepsilon(t)$ in $C^\infty([\delta,T]\times M)$. Hence for any $\varepsilon>0$, $\varphi_\varepsilon(t)\in C^\infty((0,\infty)\times M)$.

\medskip

\begin{pro}\label{211} For any $\varepsilon>0$, $\varphi_\varepsilon(t)\in C^0([0,\infty)\times M)$ and
\begin{eqnarray}\label{212}\lim\limits_{t\rightarrow0^+}\|\varphi_{\varepsilon}(t)-\varphi_{0}\|_{L^{\infty}(M)}=0.
\end{eqnarray}
\end{pro}

\medskip

{\bf Proof:}\ \ For any $(t,z)\in(0,T]\times M$,
\begin{eqnarray}\nonumber|\varphi_{\varepsilon}(t,z)-\varphi_{0}(z)|&\leq&|\varphi_{\varepsilon}(t,z)-\varphi_{\varepsilon,j}(t,z)|
+|\varphi_{\varepsilon,j}(t,z)-\varphi_{0,j}(z)|\\
&\ &+|\varphi_{0,j}(z)-\varphi_{0}(z)|.
\end{eqnarray}
Since $\varphi_{\varepsilon,j}(t)$ is a Cauchy sequence in $L^\infty([0,T]\times M)$,
\begin{eqnarray}\lim\limits_{j\rightarrow\infty}\|\varphi_{\varepsilon}(t,z)-\varphi_{\varepsilon,j}(t,z)\|_{L^{\infty}([0,T]\times M)}=0.
\end{eqnarray}
From $(\ref{00000})$, we have
\begin{eqnarray}
\lim\limits_{j\rightarrow\infty}\|\varphi_{0,j}(z)-\varphi_{0}(z)\|_{L^{\infty}(M)}=0,
\end{eqnarray}
For any $\epsilon>0$, there exists $N$ such that for any $j>N$,
\begin{eqnarray*}\sup\limits_{[0,T]\times M}|\varphi_{\varepsilon}(t,z)-\varphi_{\varepsilon,j}(t,z)|&<&\frac{\epsilon}{3},\\
\sup\limits_{M}|\varphi_{0,j}(z)-\varphi_{0}(z)|&<&\frac{\epsilon}{3}.
\end{eqnarray*}
On the other hand, fix such $j$, there exists $0<\delta<T$ such that
\begin{eqnarray}\sup\limits_{[0,\delta]\times M}|\varphi_{\varepsilon,j}(t,z)-\varphi_{0,j}|<\frac{\epsilon}{3}.
\end{eqnarray}
Combining the above estimates together, for any $t\in[0,\delta]$ and $z\in M$,
\begin{eqnarray}|\varphi_{\varepsilon}(t,z)-\varphi_{0}(z)|<\epsilon.
\end{eqnarray}
This completes the proof of the lemma.{\hfill\ensuremath{\square}}

\medskip

\begin{pro} \label{214}$\varphi_\varepsilon(t)$ is the unique solution to the parabolic Monge-Amp\`ere equation
\begin{eqnarray}\label{CMAE3}
\begin{cases}
  \frac{\partial \varphi_{\varepsilon}(t)}{\partial t}=\log\frac{(\omega_{0}+\sqrt{-1}\partial\bar{\partial}\varphi_{\varepsilon}(t))^{n}}{\omega_{0}^{n}}+F_{0}\\
  \ \ \ \ \ \ \ \ \ \ +\beta\varphi_{\varepsilon}(t)
  +\log(\varepsilon^{2}+|s|_{h}^{2})^{1-\beta},\ \ \ \ (0,\infty)\times M\\
  \varphi_{\varepsilon}(0)=\varphi_{0}\\
  \end{cases}
\end{eqnarray}
in the space of $C^0\big([0,\infty)\times M\big)\cap C^\infty\big((0,\infty)\times M\big)$.
\end{pro}

\medskip

{\bf Proof:}\ \ By proposition \ref{211}, we only need to prove the uniqueness. Suppose there exists another solution $\tilde{\varphi}_\varepsilon(t)\in C^0\big([0,\infty)\times M\big)\cap C^\infty\big((0,\infty)\times M\big)$ to the Monge-Amp\`ere equation $(\ref{CMAE3})$.

Let $\psi_\varepsilon(t)=\tilde{\varphi}_\varepsilon(t)-\varphi_\varepsilon(t)$. Then
\begin{eqnarray}
\begin{cases}
  \frac{\partial \psi_{\varepsilon}(t)}{\partial t}=\log\frac{\big(\omega_{0}+\sqrt{-1}\partial\bar{\partial}\varphi_{\varepsilon}(t)+\sqrt{-1}\partial\bar{\partial}\psi_{\varepsilon}(t)\big)^{n}}
  {(\omega_{0}+\sqrt{-1}\partial\bar{\partial}\varphi_{\varepsilon}(t))^{n}}+
  \beta\psi_{\varepsilon}(t).\\
  \\
  \psi_{\varepsilon}(0)=0\\
  \end{cases}
\end{eqnarray}
For any $T>0$, by the same arguments as that in the proof of Proposition \ref{200}, we have
\begin{eqnarray*}
\|\psi_{\varepsilon}(t)\|_{L^\infty([0,T]\times M)}\leq e^{\beta T}\|\psi_{\varepsilon}(0)\|_{L^\infty(M)}=0.
\end{eqnarray*}
Hence $\psi_{\varepsilon}(t)=0$, that is $\tilde{\varphi}_\varepsilon(t)=\varphi_\varepsilon(t)$.{\hfill\ensuremath{\square}}

\medskip

By the similar arguments as that in \cite{JSGT}, we prove the uniqueness theorems of the twisted K\"ahler-Ricci flow.

\medskip

\begin{thm}  Let $M$ be a Fano manifold with complex dimension $n$, $\omega_{0}\in c_{1}(M)$ be a smooth K\"ahler metric on $M$ and $\hat{\omega}\in c_{1}(M)$ be a K\"ahler current which admits $L^{p}$ density with respect to $\omega_{0}^{n}$ for some $p>1$ and $\int_{M}\hat{\omega}^{n}=\int_{M}\omega_{0}^{n}$. Then there exists a unique solution $\omega_\varepsilon(t)\in C^\infty\big((0,\infty)\times M\big)$ to the twisted K\"ahler-Ricci flow $(\ref{TKRF1})$ with initial data $\hat{\omega}$ in the following sense.

\medskip

$(1)$ $\frac{\partial \omega_{\varepsilon}(t)}{\partial t}=-Ric(\omega_{\varepsilon}(t))+\beta\omega_{\varepsilon}(t)+\theta_{\varepsilon}$ on $(0,\infty)\times M$;

\medskip

$(2)$   There exists $\varphi_\varepsilon(t)\in C^0\big([0,\infty)\times M\big)\cap C^\infty\big((0,\infty)\times M\big)$ such that $\omega_\varepsilon(t)=\omega_{0}+\sqrt{-1}\partial\bar{\partial}\varphi_{\varepsilon}(t)$ and
\begin{eqnarray}\lim\limits_{t\rightarrow0^+}\|\varphi_{\varepsilon}(t)-\varphi_{0}\|_{L^{\infty}(M)}=0,
\end{eqnarray}
where $\varphi_{0}\in\mathcal{E}_{p}(M,\omega_{0})$ is a metric potential of $\hat{\omega}$ with respect to $\omega_{0}$. In particular, $\omega_\varepsilon(t)$ converges in the sense of distribution to $\hat{\omega}$ as $t\rightarrow0$.
\end{thm}

\medskip

{\bf Proof:}\ \ From Proposition \ref{214}, we know that there exists a solution $\omega_{\varepsilon}(t)=\omega_0+\sqrt{-1}\partial\bar{\partial}\varphi_{\varepsilon}(t)$ to the twisted K\"ahler-Ricci flow $(\ref{TKRF1})$ with initial data $\hat{\omega}$, where $\varphi_{\varepsilon}(t)\in C^0\big([0,\infty)\times M\big)\bigcap C^\infty\big((0,\infty)\times M\big)$ satisfies
\begin{eqnarray}\lim\limits_{t\rightarrow0^+}\|\varphi_{\varepsilon}(t)-\varphi_{0}\|_{L^{\infty}(M)}=0
\end{eqnarray}
for some metric potential $\varphi_{0}\in\mathcal{E}_{p}(M,\omega_{0})$ of $\hat{\omega}$ with respect to $\omega_{0}$. Suppose that there is another solution $\tilde{\omega}_{\varepsilon}(t)=\omega_0+\sqrt{-1}\partial\bar{\partial}\tilde{\varphi}_{\varepsilon}(t)$ to the twisted K\"ahler-Ricci flow $(\ref{TKRF1})$ with initial data $\hat{\omega}$. Then $\tilde{\varphi}_{\varepsilon}(t)\in C^0\big([0,\infty)\times M\big)\bigcap C^\infty\big((0,\infty)\times M\big)$ satisfies
\begin{eqnarray}\label{215}\nonumber\frac{\partial \tilde{\varphi}_{\varepsilon}(t)}{\partial t}=\log\frac{(\omega_{0}+\sqrt{-1}\partial\bar{\partial}\tilde{\varphi}_{\varepsilon}(t))^{n}}{\omega_{0}^{n}}+F_{0}
 +\beta\tilde{\varphi}_{\varepsilon}(t)+\log(\varepsilon^{2}+|s|_{h}^{2})^{1-\beta}+f_\varepsilon(t)\\
\end{eqnarray}
on $(0,\infty)\times M$ for a smooth function $f_{\varepsilon}(t)$ on $(0,\infty)$ and 
\begin{eqnarray*}\lim\limits_{t\rightarrow0^{+}}\|\tilde{\varphi}_{\varepsilon}(t)-\tilde{\varphi}_{0}\|_{L^{\infty}(M)}=0,
\end{eqnarray*}
where $\tilde{\varphi}_{0}\in\mathcal{E}_{p}(M,\omega_{0})$ is also a metric potential of $\hat{\omega}$ with respect to $\omega_{0}$. At the same time, we have $\varphi_{0}=\tilde{\varphi}_{0}+\tilde{C}$. 

Let $\hat{\varphi}(t)=\tilde{\varphi}(t)+\tilde{C}e^{\beta t}$. It is obvious that $\hat{\varphi}_{\varepsilon}(t)\in C^0\big([0,\infty)\times M\big)\bigcap C^\infty\big((0,\infty)\times M\big)$ is a solution to equation $(\ref{215})$ and satisfies
\begin{eqnarray*}\lim\limits_{t\rightarrow0^{+}}\|\hat{\varphi}_{\varepsilon}(t)-\varphi_{0}\|_{L^{\infty}(M)}=0.
\end{eqnarray*}

Now we consider the function $\psi_\varepsilon(t)=\hat{\varphi}_\varepsilon(t)-\varphi_\varepsilon(t)$.
\begin{eqnarray}\label{216}
\begin{cases}
  \frac{\partial \psi_{\varepsilon}(t)}{\partial t}=\log\frac{\big(\omega_{0}+\sqrt{-1}\partial\bar{\partial}\varphi_{\varepsilon}(t)+\sqrt{-1}\partial\bar{\partial}\psi_{\varepsilon}(t)\big)^{n}}
  {(\omega_{0}+\sqrt{-1}\partial\bar{\partial}\varphi_{\varepsilon}(t))^{n}}+\beta\psi_{\varepsilon}(t)+f_\varepsilon(t).\\
  \\
  \psi_{\varepsilon}(0)=0\\
  \end{cases}
\end{eqnarray}
For any $0<t_1<t_2<\infty$, by the same arguments as that in the proof of Proposition \ref{200}, we have
\begin{eqnarray*}
\sup\limits_{M}\psi_\varepsilon(t_2)&\leq&e^{\beta(t_2-t_1)}\sup\limits_{M}\psi_\varepsilon(t_1)+\int_{t_1}^{t_2}e^{\beta(t_2-t)}f_\varepsilon(t)dt,\\
\inf\limits_{M}\psi_\varepsilon(t_2)&\geq&e^{\beta(t_2-t_1)}\inf\limits_{M}\psi_\varepsilon(t_1)+\int_{t_1}^{t_2}e^{\beta(t_2-t)}f_\varepsilon(t)dt.
\end{eqnarray*}
Therefore, we obtain
\begin{eqnarray*}
\inf\limits_{M}\psi_\varepsilon(t_2)&\geq&\sup\limits_{M}\psi_\varepsilon(t_2)-e^{\beta(t_2-t_1)}(\sup\limits_{M}\psi_\varepsilon(t_1)-\inf\limits_{M}\psi_\varepsilon(t_1)).
\end{eqnarray*}
Let $t_1\rightarrow0^+$, we have
\begin{eqnarray*}
\inf\limits_{M}\psi_\varepsilon(t_2)&\geq&\sup\limits_{M}\psi_\varepsilon(t_2).
\end{eqnarray*}
By equation $(\ref{216})$, $\psi_\varepsilon(t)=\int_{0}^{t}e^{\beta(t-s)}f_\varepsilon(s)ds$. Hence $\tilde{\omega}_{\varepsilon}(t)=\omega_{\varepsilon}(t)$. {\hfill\ensuremath{\square}}


\section{The long-time existence of the conical K\"ahler-Ricci flow with weak initial data}
\setcounter{equation}{0}

In this section, we study the long-time existence of the conical K\"ahler-Ricci flow $(\ref{TCKRF1})$ by the smooth approximation of the twisted K\"ahler-Ricci flows. We also prove the uniqueness of the conical K\"ahler-Ricci flow $(\ref{TCKRF1})$.

By Proposition \ref{200}, Lemma \ref{205} and Proposition \ref{210}, we conclude that for any $T>0$, there exists constants $C_1$ and $C_{2}$ depending only on $\|\varphi_0\|_{L^\infty(M)}$, $\beta$, $n$, $\omega_{0}$ and $T$, such that for any $\varepsilon>0$,
\begin{eqnarray}\label{219}&\ &\|\phi_{\varepsilon}(t)\|_{L^\infty([0,T]\times M)}\leq C_1,\\
\label{220}&\ &e^{-\frac{C_2}{t}}\omega_\varepsilon\leq\omega_{\varepsilon}(t)\leq e^{\frac{C_2}{t}}\omega_\varepsilon\ \ on\ (0,T]\times M.
\end{eqnarray}

We first prove the local uniform Calabi's $C^3$-estimate and curvature estimate along the flow $(\ref{TKRF2})$. Our proofs are similar as that in \cite{JWLXZ} (see section $2$ in \cite{JWLXZ} or section $3$ in \cite{MSBW}), but we need some new arguments to handle the weak initial data case.

\medskip

\begin{lem} For any $T>0$ and $B_r(p)\subset\subset M\setminus D$, there exist constants $C$, $C'$ and $C''$ such that for any $\varepsilon>0$ and $j\in\mathbb{N}^+$,
\begin{eqnarray*}S_{\varepsilon,j}&\leq&\frac{C'}{r^{2}}e^{\frac{C}{t}},\\
|Rm_{\varepsilon,j}|_{\omega_{\varepsilon,j}(t)}^{2}&\leq&\frac{C''}{r^{4}}e^{\frac{C}{t}}
\end{eqnarray*}
on $(0,T]\times B_{\frac{r}{2}}(p)$, where constants $C$, $C'$ and $C''$ depend only on $\|\varphi_0\|_{L^\infty(M)}$, $n$, $\beta$, $T$, $\omega_0$ and $dist_{\omega_0}(B_r(p),D)$.\end{lem}

\medskip

{\bf Proof:}\ \ By Lemma \ref{205}, there exists uniform constat $C$ depending only on $\|\varphi_0\|_{L^\infty(M)}$, $n$, $\beta$, $T$, $\omega_0$ and $dist_{\omega_0}(B_r(p),D)$, such that
\begin{eqnarray}e^{-\frac{C}{t}}\omega_0\leq\omega_{\varepsilon,j}(t)\leq e^{\frac{C}{t}}\omega_0,\ \ on\ \ B_r(p)\times (0,T].
\end{eqnarray}

Let $r=r_0>r_1>\frac{r}{2}$ and $\psi$ be a nonnegative $C^\infty$ cut-off function that is identically equal to $1$ on $\overline{B_{r_{1}(p)}}$ and vanishes outside $B_r(p)$. We may assume that
\begin{eqnarray}|\partial\psi|_{\omega_{0}}^{2}\leq\frac{C}{r^{2}}\ \ \ and\ \ \ |\sqrt{-1}\partial\bar{\partial}\psi|_{\omega_{0}}\leq\frac{C}{r^{2}}.\end{eqnarray}
Straightforward calculations show that
\begin{eqnarray}(\frac{\partial}{\partial t}-\Delta_{\varepsilon,j})(\psi^2S_{\varepsilon,j})\leq \frac{C}{r^2} e^{\frac{C}{t}}S_{\varepsilon,j}+Ce^{\frac{C}{t}}.
\end{eqnarray}
By choosing sufficiently large $\alpha$, $\gamma$ and $A$, we get
 \begin{eqnarray*}&\ &(\frac{\partial}{\partial t}-\Delta_{\varepsilon,j})(e^{-\frac{2\alpha}{t}}\psi^2S_{\varepsilon,j}+Ae^{-\frac{2\gamma}{t}}tr_{\omega_0}\omega_{\varepsilon,j}(t))\\
 &\leq& \frac{C}{r^2} e^{-\frac{\alpha}{t}}S_{\varepsilon,j}+C-Ae^{-\frac{3\gamma}{t}}S_{\varepsilon,j}+Ae^{-\frac{\gamma}{t}}\\
 &\leq& -\frac{1}{r^2}e^{-\frac{3\gamma}{t}}S_{\varepsilon,j}+\frac{C}{r^2},
\end{eqnarray*}
where $\alpha=3\gamma$, $A=\frac{C+1}{r^{2}}$, constat $C$ depends only on $\|\varphi_0\|_{L^\infty(M)}$, $n$, $\beta$, $T$ $\omega_0$ and $dist_{\omega_{0}}(B_r(p),D)$. By the maximum principle, we conclude that
\begin{eqnarray*}S_{\varepsilon,j}&\leq&\frac{C'}{r^{2}}e^{\frac{6\gamma}{t}}\ \ \ on\ \ (0,T]\times B_{\frac{r}{2}}(p).
\end{eqnarray*}

Now we prove that $|Rm_{\varepsilon,j}|^{2}_{\omega_{\varepsilon,j}(t)}$ is uniformly bounded. Through computation, there exist uniform constants $C$ such that
\begin{eqnarray*}\label{3.20.9}&\ &(\frac{d}{dt}-\Delta_{\varepsilon,j})|Rm_{\varepsilon,j}|^{2}_{\omega_{\varepsilon,j}(t)}\\
&\leq& C|Rm_{\varepsilon,j}|^{3}_{\omega_{\varepsilon,j}(t)}+Ce^{\frac{C}{t}}|Rm_{\varepsilon,j}|^{2}_{\omega_{\varepsilon,j}(t)}+Ce^{\frac{C}{t}}|Rm_{\varepsilon,j}|_{\omega_{\varepsilon,j}(t)}+C e^{\frac{C}{t}}S_{\varepsilon,j}^{\frac{1}{2}}|Rm_{\varepsilon,j}|_{\omega_{\varepsilon,j}(t)}\\
&\ &+Ce^{\frac{C}{t}}S_{\varepsilon,j}|Rm_{\varepsilon,j}|_{\omega_{\varepsilon,j}(t)}-|\nabla_{\varepsilon,j}Rm_{\varepsilon,j}|^{2}_{\omega_{\varepsilon,j}(t)}-|\overline
{\nabla}_{\varepsilon,j}Rm_{\varepsilon,j}|_{\omega_{\varepsilon,j}(t)}^{2}+Ce^{\frac{C}{t}}\\ \nonumber
&\leq&C(|Rm_{\varepsilon,j}|^{3}_{\omega_{\varepsilon,j}(t)}+e^{\frac{\tau}{t}}+\frac{1}{r^{2}}e^{\frac{\tau}{t}}|Rm_{\varepsilon,j}|_{\omega_{\varepsilon,j}(t)})-|\nabla_{\varepsilon,j}
Rm_{\varepsilon,j}|_{\omega_{\varepsilon,j}(t)}^{2}-|\overline{\nabla}_{\varepsilon,j}Rm_{\varepsilon,j}|_{\omega_{\varepsilon,j}(t)}^{2}.
\end{eqnarray*}

Next, we show that $|Rm_{\varepsilon,j}|^{2}_{\omega_{\varepsilon,j}(t)}$ is uniformly bounded. We fix a smaller radius $r_{2}$ satisfying $r_{1}>r_{2}>\frac{r}{2}$. Let $\rho$ be a cut-off function identically equal to $1$ on $\overline{B_{r_{2}}}(p)$ and identically equal to $0$ outside $B_{r_{1}}$. We also let $\rho$ satisfy

nexttext:
at $(t_{0},x_{0})$. This implies that $e^{-\frac{2\delta}{t}}|Rm_{\varepsilon,j}|_{\omega_{\varepsilon,j}(t)}^{2}\leq\frac{C}{r^{2}}$ at this point, where $C$ depends only on $\|\varphi_0\|_{L^\infty(M)}$, $n$, $\beta$, $T$, $dist_{\omega_{0}}(B_r(p),D)$, $\|\theta\|_{C^2(B_{r}(p))}$ and $\omega_0$. Following that we conclude that $F_{\varepsilon,j}$ is bounded by $\frac{C}{r^{2}}$ at $(t_{0},x_{0})$. Hence on $[0,T]\times \overline{B_{r_{2}}(p)}$, we obtain
\begin{eqnarray}\label{3.20.21}|Rm_{\varepsilon,j}|_{\omega_{\varepsilon,j}(t)}^{2}\leq\frac{C}{r^{4}}e^{\frac{2\delta+\tau}{t}},\end{eqnarray}
where $C$, $\delta$ and $\tau$ depend only on $\|\varphi_0\|_{L^\infty(M)}$, $n$, $\beta$, $T$, $dist_{\omega_{0}}(B_r(p),D)$ and $\omega_0$.{\hfill\ensuremath{\square}}

\medskip

By using the standard parabolic Schauder regularity theory \cite{GLIE}, we obtain the following proposition.

\medskip

\begin{pro}\label{217} For any $0<\delta<T<\infty$, $k\in\mathbb{N}^{+}$ and $B_r(p)\subset\subset M\setminus D$, there exists constant $C_{\delta,T,k,p,r}$ depends only on $\|\varphi_0\|_{L^\infty(M)}$, $n$, $\beta$, $\delta$, $k$, $T$, $dist_{\omega_{0}}(B_r(p),D)$ and $\omega_0$, such that for any $\varepsilon>0$ and $j\in\mathbb{N}^+$,
\begin{eqnarray}\|\varphi_{\varepsilon,j}(t)\|_{C^{k}\big([\delta,T]\times B_r(p)\big)}\leq C_{\delta,T,k,p,r}.
\end{eqnarray}
\end{pro}

\medskip

Through a further observation to equation $(\ref{CMAE3})$, we prove the monotonicity of $\varphi_\varepsilon(t)$ with respect to $\varepsilon$.

\medskip

\begin{pro}\label{218} For any $(t,x)\in [0,T]\times M$, $\varphi_\varepsilon(t,x)$ is monotone decreasing as $\varepsilon\rightarrow0$.
\end{pro}

\medskip

{\bf Proof:}\ \ For any $\varepsilon_1<\varepsilon_2$, let $\psi_{1,2}(t)=\varphi_{\varepsilon_1}(t)-\varphi_{\varepsilon_2}(t)$. Then
\begin{eqnarray}
\begin{cases}
  \frac{\partial \psi_{1,2}(t)}{\partial t}=\log\frac{\big(\omega_{0}+\sqrt{-1}\partial\bar{\partial}\varphi_{\varepsilon_2}(t)+\sqrt{-1}\partial\bar{\partial}\psi_{1,2}(t)\big)^{n}}
  {(\omega_{0}+\sqrt{-1}\partial\bar{\partial}\varphi_{\varepsilon_2}(t))^{n}}\\
  \ \ \ \ \ \ \ \ \ \ \ \ \ \ +\beta\psi_{1,2}(t)+(1-\beta)\log\frac{(\varepsilon_1^2+|s|_h^2)}{(\varepsilon_1^2+|s|_h^2)}.\\

  \psi_{1,2}(0)=0\\
  \end{cases}
\end{eqnarray}

Since $\log\frac{(\varepsilon_1^2+|s|_h^2)}{(\varepsilon_1^2+|s|_h^2)}<0$, we have
\begin{eqnarray}\nonumber&\ &\frac{\partial }{\partial t}(e^{-\beta t}\psi_{1,2}(t))\\
&\leq&e^{-\beta t}\log\frac{\big(e^{-\beta t}\omega_{0}+\sqrt{-1}\partial\bar{\partial}e^{-\beta t}\varphi_{\varepsilon_2}(t)+\sqrt{-1}\partial\bar{\partial}e^{-\beta t}\psi_{1,2}(t)\big)^{n}}
  {(e^{-\beta t}\omega_{0}+\sqrt{-1}\partial\bar{\partial}e^{-\beta t}\varphi_{\varepsilon_2}(t))^{n}}.
\end{eqnarray}

Let $\tilde{\psi}_{1,2}(t)=e^{-\beta t}\psi_{1,2}(t)-\delta t$ with $\delta>0$ and $(t_0, x_0)$ be the maximum point of $\tilde{\psi}_{1,2}(t)$ on $[0,T]\times M$. If $t_0>0$, by maximum principle, at this point, we have
\begin{eqnarray}0\leq\frac{\partial }{\partial t}\tilde{\psi}_{1,2}(t)=\frac{\partial }{\partial t}(e^{-\beta t}\psi_{1,2}(t))-\delta\leq-\delta
\end{eqnarray}
which is impossible, hence $t_0=0$. So for any $(t,x)\in [0,T]\times M$,
\begin{eqnarray}\psi_{1,2}(t,x)\leq e^{\beta t}\sup\limits_{M}\psi_{1,2}(0,x)+Te^{\beta T}\delta=Te^{\beta T}\delta.
\end{eqnarray}
Let $\delta\rightarrow0$, we conclude that $\varphi_{\varepsilon_1}(t,x)\leq\varphi_{\varepsilon_2}(t,x)$.{\hfill\ensuremath{\square}}

\medskip

For any $[\delta, T]\times K\subset\subset (0,\infty)\times M\setminus D$ and $k\geq0$, $\|\varphi_{\varepsilon,j}(t)\|_{C^{k}([\delta,T]\times K)}$ is uniformly bounded by Proposition \ref{217}. Let $j$ approximate to $\infty$, we obtain that $\|\varphi_{\varepsilon}(t)\|_{C^{k}([\delta,T]\times K)}$ is uniformly bounded. Then let $\delta$ approximate to $0$, $T$ approximate to $\infty$ and $K$ approximate to $M\setminus D$, by diagonal rule, we get a sequence $\{\varepsilon_i\}$, such that $\varphi_{\varepsilon_i}(t)$ converges in $C^\infty_{loc}$ topology on $(0,\infty)\times (M\setminus D)$ to a function $\varphi(t)$ that is smooth on $C^\infty\big((0,\infty)\times(M\setminus D)\big)$ and satisfies equation
\begin{eqnarray}\label{223}  \frac{\partial \varphi(t)}{\partial t}=\log\frac{(\omega_{0}+\sqrt{-1}\partial\bar{\partial}\varphi(t))^{n}}{\omega_{0}^{n}}+F_{0}+\beta\varphi(t)
  +\log|s|_{h}^{2(1-\beta)}
\end{eqnarray}
on $(0,\infty)\times (M\setminus D)$. Since $\varphi_\varepsilon(t)$ is monotone decreasing as $\varepsilon\rightarrow0$, we conclude that $\varphi_{\varepsilon}(t)$ converges in $C^\infty_{loc}$ topology on $(0,\infty)\times (M\setminus D)$ to $\varphi(t)$. Combining the above arguments with
$(\ref{219})$ and $(\ref{220})$, for any $T>0$, we have
\begin{eqnarray}\label{221}&\ &\|\varphi(t)\|_{L^\infty\big((0,T]\times (M\setminus D)\big)}\leq C_1,\\
\label{222}&\ &e^{-\frac{C_2}{t}}\omega_\beta\leq\omega(t)\leq e^{\frac{C_2}{t}}\omega_\beta\ \ on\ (0,T]\times (M\setminus D),
\end{eqnarray}
where $\omega(t)=\omega_0+\sqrt{-1}\partial\overline{\partial}\varphi(t)$, constants $C_1$ and $C_{2}$ depend only on $\|\varphi_0\|_{L^\infty(M)}$, $\beta$, $n$, $\omega_{0}$ and $T$.


\medskip


\begin{pro}\label{2188} For any $t>0$, $\varphi(t)$ is H\"older continuous on $M$ with respect to the metric $\omega_0$.
\end{pro}

\medskip

{\bf Proof:}\ \ We assume that $t\in[\delta, T]$ for some $\delta$ and $T$ satisfying $0<\delta<T<\infty$. By $(\ref{222})$ we have
\begin{eqnarray}C^{-1}\omega_\beta\leq\omega(t)\leq C\omega_\beta\ \ on\ [\delta,T]\times  (M\setminus D),
\end{eqnarray}
where constant $C$ depends only on $\|\varphi_0\|_{L^\infty(M)}$, $\beta$, $T$, $n$, $\omega_{0}$ and $\delta$. Combining this estimate and the fact that
$\log\frac{\omega_\beta^n|s|_h^{2(1-\beta)}}{\omega_0^n}$ is bounded uniformly on $M\setminus D$, we obtain
\begin{eqnarray}\|\log\frac{\omega^{n}(t)|s|_h^{2(1-\beta)}}{\omega_0^n}\|_{L^\infty\big([\delta,T]\times (M\setminus D)\big)}\leq C
\end{eqnarray}
for some uniform constant $C$ independent of $t$. Therefore, $\|\frac{\partial \varphi(t)}{\partial t}\|_{L^\infty\big([\delta,T]\times (M\setminus D)\big)}$ is uniformly bounded by equation $(\ref{223})$ and estimate $(\ref{221})$. We rewrite equation $(\ref{223})$ as
\begin{eqnarray}\label{224}(\omega_{0}+\sqrt{-1}\partial\bar{\partial}\varphi(t))^{n}=e^{\frac{\partial \varphi(t)}{\partial t}-F_{0}-\beta\varphi(t)}\frac{\omega_{0}^{n}}{|s|_{h}^{2(1-\beta)}}.
\end{eqnarray}
The function on the right side of equation $(\ref{224})$ is $L^p$ integrable with respect to $\omega_0^{n}$ for some $p>1$. By S. Kolodziej's $L^p$ estimates \cite{K}, we know that $\varphi(t)$ is H\"older continuous on $M$ with respect to $\omega_0$ for any $t>0$.{\hfill\ensuremath{\square}}

\medskip

Next, by using the monotonicity of $\varphi_{\varepsilon}(t)$ with respect to $\varepsilon$ and constructing auxiliary function, we prove the continuity of $\varphi(t)$ as $t\rightarrow0^{+}$.

\medskip


\begin{pro}\label{101} $\varphi(t)\in C^0([0,\infty)\times M)$ and
\begin{eqnarray}\label{102}\lim\limits_{t\rightarrow0^+}\|\varphi(t)-\varphi_{0}\|_{L^{\infty}(M)}=0.
\end{eqnarray}
\end{pro}

\medskip

{\bf Proof:}\ \ Through the above arguments, we only need prove limit $(\ref{102})$. By the monotonicity of $\varphi_{\varepsilon}(t)$ with respect to $\varepsilon$, for any $(t,z)\in(0,T]\times M$, we have
\begin{eqnarray}\label{2210}\nonumber\varphi(t,z)-\varphi_0(z)&\leq&\varphi_{\varepsilon_{1}}(t,z)-\varphi_0(z)\\\nonumber
&\leq&|\varphi_{\varepsilon_1}(t,z)-\varphi_{\varepsilon_1,j}(t,z)|
+|\varphi_{\varepsilon_1,j}(t,z)-\varphi_{0,j}(z)|\\
&\ &+|\varphi_{0,j}(z)-\varphi_{0}(z)|.
\end{eqnarray}
Since $\varphi_{\varepsilon_1,j}(t)$ is a Cauchy sequence in $L^\infty([0,T]\times M)$,
\begin{eqnarray}\lim\limits_{j\rightarrow\infty}\|\varphi_{\varepsilon_1}(t,z)-\varphi_{\varepsilon_1,j}(t,z)\|_{L^{\infty}([0,T]\times M)}=0.
\end{eqnarray}
From $(\ref{00000})$, we have
\begin{eqnarray}
\lim\limits_{j\rightarrow\infty}\|\varphi_{0,j}(z)-\varphi_{0}(z)\|_{L^{\infty}(M)}=0,
\end{eqnarray}
For any $\epsilon>0$, there exists $N$ such that for any $j>N$,
\begin{eqnarray*}\sup\limits_{[0,T]\times M}|\varphi_{\varepsilon_1}(t,z)-\varphi_{\varepsilon_1,j}(t,z)|&<&\frac{\epsilon}{3},\\
\sup\limits_{M}|\varphi_{0,j}(z)-\varphi_{0}(z)|&<&\frac{\epsilon}{3}.
\end{eqnarray*}
Fix such $j$, there exists $0<\delta_1<T$ such that
\begin{eqnarray}\sup\limits_{[0,\delta_1]\times M}|\varphi_{\varepsilon_1,j}(t,z)-\varphi_{0,j}|<\frac{\epsilon}{3}.
\end{eqnarray}
Combining the above estimates together, for any $t\in(0,\delta_1]$ and $z\in M$,
\begin{eqnarray}\varphi(t,z)-\varphi_{0}(z)<\epsilon.
\end{eqnarray}

On the other hand, by S. Kolodziej's results \cite{K000}, there exists a smooth solution $u_{\varepsilon,j}$ to the equation
\begin{eqnarray}(\omega_{0}+\sqrt{-1}\partial\bar{\partial}u_{\varepsilon,j})^{n}=e^{-F_{0}-\beta\varphi_{0,j}+\hat{C}}\frac{\omega_{0}^{n}}
{(\varepsilon^2+|s|_{h}^{2})^{(1-\beta)}},
\end{eqnarray}
and $u_{\varepsilon,j}$ satisfies \begin{eqnarray}\|u_{\varepsilon,j}\|_{L^\infty(M)}\leq C,
\end{eqnarray}
where $\hat{C}$ is a uniform normalization constant, constant $C$ depends only on $\|\varphi_0\|_{L^\infty(M)}$, $\beta$ and $F_0$.

We define function
\begin{eqnarray}\psi_{\varepsilon,j}(t)=(1-te^{\beta t})\varphi_{0,j}+te^{\beta t}u_{\varepsilon,j}+h(t)e^{\beta t},
\end{eqnarray}
where
\begin{eqnarray}\nonumber h(t)&=&-t\|\varphi_{0,j}\|_{L^\infty(M)}-t\|u_{\varepsilon,j}\|_{L^\infty(M)}+n(t\log t-t)e^{-\beta t}\\
&\ &+\beta n\int_0^te^{-\beta s}s\log s ds+\hat{C}t
\end{eqnarray}
and $h(0)=0$. Straightforward calculations show that
\begin{eqnarray*}\frac{\partial}{\partial t}\psi_{\varepsilon,j}(t)-\beta\psi_{\varepsilon,j}(t)&=&-\beta \varphi_{0,j}-e^{\beta t}\varphi_{0,j}+e^{\beta t}u_{\varepsilon,j}+e^{\beta t}\frac{\partial}{\partial t}h(t)\\
&=&-\beta \varphi_{0,j}-e^{\beta t}\varphi_{0,j}+e^{\beta t}u_{\varepsilon,j}-e^{\beta t}\|\varphi_{0,j}\|_{L^\infty(M)}-e^{\beta t}\|u_{\varepsilon,j}\|_{L^\infty(M)}\\
&\ &+n\log t-\beta n(t\log t-t)+\beta nt\log t+\hat{C}\\
&\leq& -\beta \varphi_{0,j}+n\log t+n\beta t+\hat{C}.
\end{eqnarray*}
Therefore, we have
\begin{eqnarray*}e^{\frac{\partial}{\partial t}\psi_{\varepsilon,j}(t)-\beta\psi_{\varepsilon,j}(t)}\omega_0^n\leq
t^ne^{n\beta t}e^{-\beta \varphi_{0,j}+\hat{C}}\omega_0^n.
\end{eqnarray*}
When $t$ is sufficiently small,
\begin{eqnarray*}\omega_0+\sqrt{-1}\partial\overline{\partial}\psi_{\varepsilon,j}(t)&=&(1-te^{\beta t})(\omega_0+\sqrt{-1}\partial\overline{\partial}\varphi_{0,j})+te^{\beta t}(\omega_0+\sqrt{-1}\partial\overline{\partial}u_{\varepsilon,j})\\
&\geq&te^{\beta t}(\omega_0+\sqrt{-1}\partial\overline{\partial}u_{\varepsilon,j}).
\end{eqnarray*}
Combining the above inequalities,
\begin{eqnarray*}(\omega_0+\sqrt{-1}\partial\overline{\partial}\psi_{\varepsilon,j}(t))^n&\geq&t^ne^{n\beta t}(\omega_0+\sqrt{-1}\partial\overline{\partial}u_{\varepsilon,j})^n\\
&=&t^ne^{n\beta t}e^{-F_{0}-\beta\varphi_{0,j}+\hat{C}}\frac{\omega_{0}^{n}}
{(\varepsilon^2+|s|_{h}^{2})^{(1-\beta)}}\\
&\geq&e^{-F_{0}+\frac{\partial}{\partial t}\psi_{\varepsilon,j}(t)-\beta\psi_{\varepsilon,j}(t)}\frac{\omega_{0}^{n}}
{(\varepsilon^2+|s|_{h}^{2})^{(1-\beta)}}.
\end{eqnarray*}
This equation is equivalent to
\begin{eqnarray}
