\begin{thm}\label{228} Let $\varphi_i(t)\in C^0\big([0,\infty)\times M\big)\bigcap C^\infty\big((0,\infty)\times(M\setminus D)\big)$ $(i=1,2)$ be two  long-time solutions to the parabolic Monge-Amp\`ere equation
\begin{eqnarray}
  \frac{\partial \varphi_i(t)}{\partial t}=\log\frac{(\omega_{0}+\sqrt{-1}\partial\bar{\partial}\varphi_i(t))^{n}}{\omega_{0}^{n}}+F_{0}+\beta\varphi_i(t)
  +\log|s|_{h}^{2(1-\beta)}
\end{eqnarray}
on $(0,\infty)\times (M\setminus D)$. If $\varphi_i$ $(i=1,2)$ satisfy
\begin{itemize}
  \item For any $0<\delta<T<\infty$, there exists uniform constant $C$ such that
\begin{eqnarray*}C^{-1}\omega_\beta\leq \omega_{0}+\sqrt{-1}\partial\bar{\partial}\varphi_i(t)\leq C\omega_\beta
\end{eqnarray*}
on $[\delta,T]\times (M\setminus D)$;
  \item On $[\delta, T]$, there exist constant $\alpha>0$ and $C^{*}$ such that $\varphi_i(t)$ is $C^{\alpha}$ on $M$ with respect to $\omega_{0}$ and $\| \frac{\partial\varphi_i(t)}{\partial t}\|_{L^{\infty}(M\setminus D)}\leqslant C^{*}$;
  \item $\lim\limits_{t\rightarrow0^{+}}\|\varphi_i(t)-\varphi_{0}\|_{L^{\infty}(M)}=0$.
  \end{itemize}
Then $\varphi_1=\varphi_2$.
\end{thm}

\medskip

{\bf Proof:}\ \ We apply Jeffres' trick \cite{TJEF} in the parabolic case. For any $0<t_1<T<\infty$ and $a>0$. Let $\phi_1(t)=\varphi_1(t)+a|s|_h^{2q}$, where $0<q<1$ is determined later. The evolution of $\phi_1$ is
\begin{eqnarray*}
  \frac{\partial \phi_1(t)}{\partial t}=\log\frac{(\omega_{0}+\sqrt{-1}\partial\bar{\partial}\varphi_1(t))^{n}}{\omega_{0}^{n}}+F_{0}+\beta\phi_1(t)
  -a\beta|s|_h^{2q}+\log|s|_{h}^{2(1-\beta)}.
\end{eqnarray*}
Denote $\psi(t)=\phi_1(t)-\varphi_2(t)$ and $\hat{\Delta}=\int_0^1 g_{s\varphi_1+(1-s)\varphi_2}^{i\bar{j}}\frac{\partial^2}{\partial z^i\partial\bar{z}^j}ds$, $\psi(t)$ evolves along the following equation
\begin{eqnarray*}
  \frac{\partial \psi(t)}{\partial t}=\hat{\Delta}\psi(t)-a\hat{\Delta}|s|_h^{2q}+\beta\psi(t)
  -a\beta|s|_h^{2q}.
\end{eqnarray*}
By the equivalence of the metrics and the equation
\begin{eqnarray*}
\sqrt{-1}\partial\overline{\partial}|s|_h^{2q}=q^2|s|_h^{2q}\sqrt{-1}\partial\log|s|_h^{2}\wedge\overline{\partial}\log|s|_h^{2}
+q|s|_h^{2q}\sqrt{-1}\partial\overline{\partial}\log|s|_h^{2},
\end{eqnarray*}
we obtain the estimate
\begin{eqnarray*}
\hat{\Delta}|s|_h^{2q}&\geq &q|s|_h^{2q}g_{s\varphi_1+(1-s)\varphi_2}^{i\bar{j}}(\frac{\partial^2}{\partial z^i\partial\bar{z}^j}\log|s|_h^{2})\\
&=&-q|s|_h^{2q}g_{s\varphi_1+(1-s)\varphi_2}^{i\bar{j}}g_{0,i\bar{j}}\\
&\geq&-Cq|s|_h^{2q}g_{\beta}^{i\bar{j}}g_{0,i\bar{j}}\\
&\geq&-C
\end{eqnarray*}
on $M\setminus D$, where constant $C$ independent of $a$, and we apply the fact that $\omega_\beta\geq\gamma\omega_0$ on $M\setminus D$ for some constant $\gamma$. Then we obtain
\begin{eqnarray*}
  \frac{\partial \psi(t)}{\partial t}\leq\hat{\Delta}\psi(t)+\beta\psi(t)+aC.
\end{eqnarray*}

Let $\tilde{\psi}=e^{-\beta (t-t_1)}\psi+\frac{aC}{\beta}e^{-\beta (t-t_1)}-\epsilon (t-t_1)$. By choosing suitable $0<q<1$, we can  assume that the space maximum of $\tilde{\psi}$ on $[t_1,T]\times M$ is attained away from $D$. Let $(t_0,x_0)$ be the maximum point. If $t_0>t_1$, by the maximum principle, at $(t_0,x_0)$, we have
\begin{eqnarray*}
 0\leq (\frac{\partial }{\partial t}-\hat{\Delta})\tilde{\psi}(t)\leq -\epsilon,
\end{eqnarray*}
which is impossible, hence $t_0=t_1$. Then for $(t,x)\in [t_1,T]\times M$, we obtain
\begin{eqnarray*}
\psi(t,x)&\leq& e^{\beta T}\|\varphi_1(t_1,x)-\varphi_2(t_1,x)\|_{L^\infty(M)}\\
&\ &+aCe^{\beta T}+\epsilon Te^{\beta T}
\end{eqnarray*}
Let $a\rightarrow0$ and then $t_1\rightarrow0^+$, we get
\begin{eqnarray*}
\varphi_1(t)-\varphi_2(t)\leq \epsilon Te^{\beta T}.
\end{eqnarray*}
It shows that $\varphi_1(t)\leq\varphi_2(t)$ after we let $\epsilon\rightarrow0$. By the same reason we have $\varphi_2(t)\leq\varphi_1(t)$, then we prove that $\varphi_1(t)=\varphi_2(t)$.{\hfill\ensuremath{\square}}

\medskip

\begin{thm}\label{229} $\omega_{\varphi(t)}=\omega_{0}+\sqrt{-1}\partial\bar{\partial}\varphi(t)$ is the unique long-time solution to the conical K\"ahler-Ricci flow $(\ref{TCKRF1})$.
\end{thm}

\medskip

{\bf Proof:}\ \ Suppose there is another solution $\omega_{\phi(t)}=\omega_0+\sqrt{-1}\partial\bar{\partial}\phi(t)$ to the conical K\"ahlre-Ricci flow $(\ref{TCKRF1})$. It is easy to see that 
\begin{eqnarray}\label{20150618}
\frac{\partial\phi(t)}{\partial t}=\log\frac{(\omega_{0}+\sqrt{-1}\partial\bar{\partial}\phi(t))^{n}}{\omega_{0}^{n}}+F_{0}+\beta\phi(t)
  +\log|s|_{h}^{2(1-\beta)}+f(t)
\end{eqnarray}
on $(0,\infty)\times (M\setminus D)$ for a smooth function $f(t)$ defined on $(0,\infty)$, and $\phi(t)\in C^0\big([0,\infty)\times M\big)\bigcap C^\infty\big((0,\infty)\times(M\setminus D)\big)$ satisfies
\begin{itemize}
  \item For any $0<\delta<T<\infty$, there exists uniform constant $C$ such that
\begin{eqnarray*}C^{-1}\omega_\beta\leq \omega_{0}+\sqrt{-1}\partial\bar{\partial}\phi(t)\leq C\omega_\beta\ \ \ \ on\ \ \ \ [\delta,T]\times (M\setminus D);
\end{eqnarray*}

  \item On $[\delta, T]$, there exist constant $\alpha>0$ and $C$ such that $\phi(t)$ is $C^{\alpha}$ on $M$ with respect to $\omega_{0}$ and $\| \frac{\partial\phi(t)}{\partial t}\|_{L^{\infty}(M\setminus D)}\leqslant C$;
  \item $\lim\limits_{t\rightarrow0^{+}}\|\phi(t)-\varphi_{0}\|_{L^{\infty}(M)}=0$.
  \end{itemize}

For any $0<t_1<T<\infty$ and $a>0$. Let $\psi(t)=\phi(t)+a|s|_h^{2q}-\varphi(t)$, where $0<q<1$ is determined later. Then
\begin{eqnarray*}
  \frac{\partial \psi(t)}{\partial t}=\hat{\Delta}\psi(t)-a\hat{\Delta}|s|_h^{2q}+\beta\psi(t)
  -a\beta|s|_h^{2q}+f(t).
\end{eqnarray*}
By the same arguments as that in the proof of Proposition \ref{228}, for any $(t,x)\in [t_1,T]\times M$, we have
\begin{eqnarray*}
\psi(t,x)&\leq& e^{\beta (t-t_1)}\|\phi(t_1,x)-\varphi(t_1,x)\|_{L^\infty(M)}\\
&\ &+aCe^{\beta (t-t_1)}+\epsilon (t-t_1)e^{\beta (t-t_1)}\\
&\ &+e^{\beta (t-t_1)}\int_{t_1}^te^{-\beta (s-t_1)}f(s)ds
\end{eqnarray*}
Let $a\rightarrow0$, we obtain
\begin{eqnarray*}
\phi(t)-\varphi(t)&\leq& e^{\beta (t-t_1)}\|\phi(t_1,x)-\varphi(t_1,x)\|_{L^\infty(M)}\\
&\ &+\epsilon (t-t_1)e^{\beta (t-t_1)}+e^{\beta (t-t_1)}\int_{t_1}^te^{-\beta (s-t_1)}f(s)ds.
\end{eqnarray*}
By the similar arguments, we can obtain
\begin{eqnarray*}
\varphi(t)-\phi(t)&\leq& e^{\beta (t-t_1)}\|\phi(t_1,x)-\varphi(t_1,x)\|_{L^\infty(M)}\\
&\ &+\epsilon (t-t_1)e^{\beta (t-t_1)}-e^{\beta (t-t_1)}\int_{t_1}^te^{-\beta (s-t_1)}f(s)ds.
\end{eqnarray*}
Therefore, for any $t>t_1>0$, we have
\begin{eqnarray*}
\inf\limits_{M}(\phi(t)-\varphi(t))&\geq& \sup\limits_{M}(\phi(t)-\varphi(t))-2e^{\beta (t-t_1)}\|\phi(t_1,x)-\varphi(t_1,x)\|_{L^\infty(M)}\\
&\ &-2\epsilon (t-t_1)e^{\beta (t-t_1)}\\
&\geq& \sup\limits_{M}(\phi(t)-\varphi(t))-2e^{\beta T}\|\phi(t_1,x)-\varphi(t_1,x)\|_{L^\infty(M)}\\
&\ &-2\epsilon Te^{\beta T}.
\end{eqnarray*}
Let $t_1\rightarrow0^+$ and then $\epsilon\rightarrow0$, we conclude that $\phi(t)=\varphi(t)+e^{\beta t}\int_{0}^te^{-\beta s}f(s)ds$. Then
$\omega_{\phi(t)}=\omega_{\varphi(t)}$ on $(0,\infty)\times (M\setminus D)$. {\hfill\ensuremath{\square}}


\section{The convergence of the conical K\"ahler-Ricci flow with weak initial data}
\setcounter{equation}{0}

In this section, we study the convergence of the conical K\"ahler-Ricci flow (\ref{TCKRF1}) with positive twisted first Chern class. Our discussion is very similar as that in \cite{JWLXZ}, but we need new arguments on estimates of the twisted Ricci potential $u_{\varepsilon}(t)$ and the term $|\dot{\varphi}_{\varepsilon}|$ when we handle the weak initial data case.

  Without loss of generality, we assume $\lambda=1$ (i.e. $\mu=\beta$). We first prove the uniform Perelman's estimates along the twisted K\"ahler-Ricci flow
\begin{eqnarray}\label{TKRF3}
\begin{cases}
  \frac{\partial \omega_{\varepsilon}(t)}{\partial t}=-Ric(\omega_{\varepsilon}(t))+\beta\omega_{\varepsilon}(t)+\theta_{\varepsilon}.\\
  \\
  \omega_{\varepsilon}(t)|_{t=0}=\omega_{\varphi_{0}}\\
  \end{cases}
  \end{eqnarray}
By the same argument as Proposition $4.1$ in \cite{JWLXZ}, we have

\medskip

\begin{pro}\label{1.8.1} $t^2(R(g_{\varepsilon,j}(t))-tr_{g_{\varepsilon,j}(t)}\theta_{\varepsilon})$ is uniformly bounded from below along the twisted K\"ahler-Ricci flow $(\ref{TKRF2})$, i.e. there exists a uniform constant $C$, such that

prevtext:
at $(t_{0},x_{0})$. This implies that $e^{-\frac{2\delta}{t}}|Rm_{\varepsilon,j}|_{\omega_{\varepsilon,j}(t)}^{2}\leq\frac{C}{r^{2}}$ at this point, where $C$ depends only on $\|\varphi_0\|_{L^\infty(M)}$, $n$, $\beta$, $T$, $dist_{\omega_{0}}(B_r(p),D)$, $\|\theta\|_{C^2(B_{r}(p))}$ and $\omega_0$. Following that we conclude that $F_{\varepsilon,j}$ is bounded by $\frac{C}{r^{2}}$ at $(t_{0},x_{0})$. Hence on $[0,T]\times \overline{B_{r_{2}}(p)}$, we obtain
\begin{eqnarray}\label{3.20.21}|Rm_{\varepsilon,j}|_{\omega_{\varepsilon,j}(t)}^{2}\leq\frac{C}{r^{4}}e^{\frac{2\delta+\tau}{t}},\end{eqnarray}
where $C$, $\delta$ and $\tau$ depend only on $\|\varphi_0\|_{L^\infty(M)}$, $n$, $\beta$, $T$, $dist_{\omega_{0}}(B_r(p),D)$ and $\omega_0$.{\hfill\ensuremath{\square}}

\medskip

By using the standard parabolic Schauder regularity theory \cite{GLIE}, we obtain the following proposition.

\medskip

\begin{pro}\label{217} For any $0<\delta<T<\infty$, $k\in\mathbb{N}^{+}$ and $B_r(p)\subset\subset M\setminus D$, there exists constant $C_{\delta,T,k,p,r}$ depends only on $\|\varphi_0\|_{L^\infty(M)}$, $n$, $\beta$, $\delta$, $k$, $T$, $dist_{\omega_{0}}(B_r(p),D)$ and $\omega_0$, such that for any $\varepsilon>0$ and $j\in\mathbb{N}^+$,
\begin{eqnarray}\|\varphi_{\varepsilon,j}(t)\|_{C^{k}\big([\delta,T]\times B_r(p)\big)}\leq C_{\delta,T,k,p,r}.
\end{eqnarray}
\end{pro}

\medskip

Through a further observation to equation $(\ref{CMAE3})$, we prove the monotonicity of $\varphi_\varepsilon(t)$ with respect to $\varepsilon$.

\medskip

\begin{pro}\label{218} For any $(t,x)\in [0,T]\times M$, $\varphi_\varepsilon(t,x)$ is monotone decreasing as $\varepsilon\rightarrow0$.
\end{pro}

\medskip

{\bf Proof:}\ \ For any $\varepsilon_1<\varepsilon_2$, let $\psi_{1,2}(t)=\varphi_{\varepsilon_1}(t)-\varphi_{\varepsilon_2}(t)$. Then
\begin{eqnarray}
\begin{cases}
  \frac{\partial \psi_{1,2}(t)}{\partial t}=\log\frac{\big(\omega_{0}+\sqrt{-1}\partial\bar{\partial}\varphi_{\varepsilon_2}(t)+\sqrt{-1}\partial\bar{\partial}\psi_{1,2}(t)\big)^{n}}
  {(\omega_{0}+\sqrt{-1}\partial\bar{\partial}\varphi_{\varepsilon_2}(t))^{n}}\\
  \ \ \ \ \ \ \ \ \ \ \ \ \ \ +\beta\psi_{1,2}(t)+(1-\beta)\log\frac{(\varepsilon_1^2+|s|_h^2)}{(\varepsilon_1^2+|s|_h^2)}.\\

  \psi_{1,2}(0)=0\\
  \end{cases}
\end{eqnarray}

Since $\log\frac{(\varepsilon_1^2+|s|_h^2)}{(\varepsilon_1^2+|s|_h^2)}<0$, we have
\begin{eqnarray}\nonumber&\ &\frac{\partial }{\partial t}(e^{-\beta t}\psi_{1,2}(t))\\
&\leq&e^{-\beta t}\log\frac{\big(e^{-\beta t}\omega_{0}+\sqrt{-1}\partial\bar{\partial}e^{-\beta t}\varphi_{\varepsilon_2}(t)+\sqrt{-1}\partial\bar{\partial}e^{-\beta t}\psi_{1,2}(t)\big)^{n}}
  {(e^{-\beta t}\omega_{0}+\sqrt{-1}\partial\bar{\partial}e^{-\beta t}\varphi_{\varepsilon_2}(t))^{n}}.
\end{eqnarray}

Let $\tilde{\psi}_{1,2}(t)=e^{-\beta t}\psi_{1,2}(t)-\delta t$ with $\delta>0$ and $(t_0, x_0)$ be the maximum point of $\tilde{\psi}_{1,2}(t)$ on $[0,T]\times M$. If $t_0>0$, by maximum principle, at this point, we have
\begin{eqnarray}0\leq\frac{\partial }{\partial t}\tilde{\psi}_{1,2}(t)=\frac{\partial }{\partial t}(e^{-\beta t}\psi_{1,2}(t))-\delta\leq-\delta
\end{eqnarray}
which is impossible, hence $t_0=0$. So for any $(t,x)\in [0,T]\times M$,
\begin{eqnarray}\psi_{1,2}(t,x)\leq e^{\beta t}\sup\limits_{M}\psi_{1,2}(0,x)+Te^{\beta T}\delta=Te^{\beta T}\delta.
\end{eqnarray}
Let $\delta\rightarrow0$, we conclude that $\varphi_{\varepsilon_1}(t,x)\leq\varphi_{\varepsilon_2}(t,x)$.{\hfill\ensuremath{\square}}

\medskip

For any $[\delta, T]\times K\subset\subset (0,\infty)\times M\setminus D$ and $k\geq0$, $\|\varphi_{\varepsilon,j}(t)\|_{C^{k}([\delta,T]\times K)}$ is uniformly bounded by Proposition \ref{217}. Let $j$ approximate to $\infty$, we obtain that $\|\varphi_{\varepsilon}(t)\|_{C^{k}([\delta,T]\times K)}$ is uniformly bounded. Then let $\delta$ approximate to $0$, $T$ approximate to $\infty$ and $K$ approximate to $M\setminus D$, by diagonal rule, we get a sequence $\{\varepsilon_i\}$, such that $\varphi_{\varepsilon_i}(t)$ converges in $C^\infty_{loc}$ topology on $(0,\infty)\times (M\setminus D)$ to a function $\varphi(t)$ that is smooth on $C^\infty\big((0,\infty)\times(M\setminus D)\big)$ and satisfies equation
\begin{eqnarray}\label{223}  \frac{\partial \varphi(t)}{\partial t}=\log\frac{(\omega_{0}+\sqrt{-1}\partial\bar{\partial}\varphi(t))^{n}}{\omega_{0}^{n}}+F_{0}+\beta\varphi(t)
  +\log|s|_{h}^{2(1-\beta)}
\end{eqnarray}
on $(0,\infty)\times (M\setminus D)$. Since $\varphi_\varepsilon(t)$ is monotone decreasing as $\varepsilon\rightarrow0$, we conclude that $\varphi_{\varepsilon}(t)$ converges in $C^\infty_{loc}$ topology on $(0,\infty)\times (M\setminus D)$ to $\varphi(t)$. Combining the above arguments with
$(\ref{219})$ and $(\ref{220})$, for any $T>0$, we have
\begin{eqnarray}\label{221}&\ &\|\varphi(t)\|_{L^\infty\big((0,T]\times (M\setminus D)\big)}\leq C_1,\\
\label{222}&\ &e^{-\frac{C_2}{t}}\omega_\beta\leq\omega(t)\leq e^{\frac{C_2}{t}}\omega_\beta\ \ on\ (0,T]\times (M\setminus D),
\end{eqnarray}
where $\omega(t)=\omega_0+\sqrt{-1}\partial\overline{\partial}\varphi(t)$, constants $C_1$ and $C_{2}$ depend only on $\|\varphi_0\|_{L^\infty(M)}$, $\beta$, $n$, $\omega_{0}$ and $T$.


\medskip


\begin{pro}\label{2188} For any $t>0$, $\varphi(t)$ is H\"older continuous on $M$ with respect to the metric $\omega_0$.
\end{pro}

\medskip

{\bf Proof:}\ \ We assume that $t\in[\delta, T]$ for some $\delta$ and $T$ satisfying $0<\delta<T<\infty$. By $(\ref{222})$ we have
\begin{eqnarray}C^{-1}\omega_\beta\leq\omega(t)\leq C\omega_\beta\ \ on\ [\delta,T]\times  (M\setminus D),
\end{eqnarray}
where constant $C$ depends only on $\|\varphi_0\|_{L^\infty(M)}$, $\beta$, $T$, $n$, $\omega_{0}$ and $\delta$. Combining this estimate and the fact that
$\log\frac{\omega_\beta^n|s|_h^{2(1-\beta)}}{\omega_0^n}$ is bounded uniformly on $M\setminus D$, we obtain
\begin{eqnarray}\|\log\frac{\omega^{n}(t)|s|_h^{2(1-\beta)}}{\omega_0^n}\|_{L^\infty\big([\delta,T]\times (M\setminus D)\big)}\leq C
\end{eqnarray}
for some uniform constant $C$ independent of $t$. Therefore, $\|\frac{\partial \varphi(t)}{\partial t}\|_{L^\infty\big([\delta,T]\times (M\setminus D)\big)}$ is uniformly bounded by equation $(\ref{223})$ and estimate $(\ref{221})$. We rewrite equation $(\ref{223})$ as
\begin{eqnarray}\label{224}(\omega_{0}+\sqrt{-1}\partial\bar{\partial}\varphi(t))^{n}=e^{\frac{\partial \varphi(t)}{\partial t}-F_{0}-\beta\varphi(t)}\frac{\omega_{0}^{n}}{|s|_{h}^{2(1-\beta)}}.
\end{eqnarray}
The function on the right side of equation $(\ref{224})$ is $L^p$ integrable with respect to $\omega_0^{n}$ for some $p>1$. By S. Kolodziej's $L^p$ estimates \cite{K}, we know that $\varphi(t)$ is H\"older continuous on $M$ with respect to $\omega_0$ for any $t>0$.{\hfill\ensuremath{\square}}

\medskip

Next, by using the monotonicity of $\varphi_{\varepsilon}(t)$ with respect to $\varepsilon$ and constructing auxiliary function, we prove the continuity of $\varphi(t)$ as $t\rightarrow0^{+}$.

\medskip


\begin{pro}\label{101} $\varphi(t)\in C^0([0,\infty)\times M)$ and
\begin{eqnarray}\label{102}\lim\limits_{t\rightarrow0^+}\|\varphi(t)-\varphi_{0}\|_{L^{\infty}(M)}=0.
\end{eqnarray}
\end{pro}

\medskip

{\bf Proof:}\ \ Through the above arguments, we only need prove limit $(\ref{102})$. By the monotonicity of $\varphi_{\varepsilon}(t)$ with respect to $\varepsilon$, for any $(t,z)\in(0,T]\times M$, we have
\begin{eqnarray}\label{2210}\nonumber\varphi(t,z)-\varphi_0(z)&\leq&\varphi_{\varepsilon_{1}}(t,z)-\varphi_0(z)\\\nonumber
&\leq&|\varphi_{\varepsilon_1}(t,z)-\varphi_{\varepsilon_1,j}(t,z)|
+|\varphi_{\varepsilon_1,j}(t,z)-\varphi_{0,j}(z)|\\
&\ &+|\varphi_{0,j}(z)-\varphi_{0}(z)|.
\end{eqnarray}
Since $\varphi_{\varepsilon_1,j}(t)$ is a Cauchy sequence in $L^\infty([0,T]\times M)$,
\begin{eqnarray}\lim\limits_{j\rightarrow\infty}\|\varphi_{\varepsilon_1}(t,z)-\varphi_{\varepsilon_1,j}(t,z)\|_{L^{\infty}([0,T]\times M)}=0.
\end{eqnarray}
From $(\ref{00000})$, we have
\begin{eqnarray}
\lim\limits_{j\rightarrow\infty}\|\varphi_{0,j}(z)-\varphi_{0}(z)\|_{L^{\infty}(M)}=0,
\end{eqnarray}
For any $\epsilon>0$, there exists $N$ such that for any $j>N$,
\begin{eqnarray*}\sup\limits_{[0,T]\times M}|\varphi_{\varepsilon_1}(t,z)-\varphi_{\varepsilon_1,j}(t,z)|&<&\frac{\epsilon}{3},\\
\sup\limits_{M}|\varphi_{0,j}(z)-\varphi_{0}(z)|&<&\frac{\epsilon}{3}.
\end{eqnarray*}
Fix such $j$, there exists $0<\delta_1<T$ such that
\begin{eqnarray}\sup\limits_{[0,\delta_1]\times M}|\varphi_{\varepsilon_1,j}(t,z)-\varphi_{0,j}|<\frac{\epsilon}{3}.
\end{eqnarray}
Combining the above estimates together, for any $t\in(0,\delta_1]$ and $z\in M$,
\begin{eqnarray}\varphi(t,z)-\varphi_{0}(z)<\epsilon.
\end{eqnarray}

On the other hand, by S. Kolodziej's results \cite{K000}, there exists a smooth solution $u_{\varepsilon,j}$ to the equation
\begin{eqnarray}(\omega_{0}+\sqrt{-1}\partial\bar{\partial}u_{\varepsilon,j})^{n}=e^{-F_{0}-\beta\varphi_{0,j}+\hat{C}}\frac{\omega_{0}^{n}}
{(\varepsilon^2+|s|_{h}^{2})^{(1-\beta)}},
\end{eqnarray}
and $u_{\varepsilon,j}$ satisfies \begin{eqnarray}\|u_{\varepsilon,j}\|_{L^\infty(M)}\leq C,
\end{eqnarray}
where $\hat{C}$ is a uniform normalization constant, constant $C$ depends only on $\|\varphi_0\|_{L^\infty(M)}$, $\beta$ and $F_0$.

We define function
\begin{eqnarray}\psi_{\varepsilon,j}(t)=(1-te^{\beta t})\varphi_{0,j}+te^{\beta t}u_{\varepsilon,j}+h(t)e^{\beta t},
\end{eqnarray}
where
\begin{eqnarray}\nonumber h(t)&=&-t\|\varphi_{0,j}\|_{L^\infty(M)}-t\|u_{\varepsilon,j}\|_{L^\infty(M)}+n(t\log t-t)e^{-\beta t}\\
&\ &+\beta n\int_0^te^{-\beta s}s\log s ds+\hat{C}t
\end{eqnarray}
and $h(0)=0$. Straightforward calculations show that
\begin{eqnarray*}\frac{\partial}{\partial t}\psi_{\varepsilon,j}(t)-\beta\psi_{\varepsilon,j}(t)&=&-\beta \varphi_{0,j}-e^{\beta t}\varphi_{0,j}+e^{\beta t}u_{\varepsilon,j}+e^{\beta t}\frac{\partial}{\partial t}h(t)\\
&=&-\beta \varphi_{0,j}-e^{\beta t}\varphi_{0,j}+e^{\beta t}u_{\varepsilon,j}-e^{\beta t}\|\varphi_{0,j}\|_{L^\infty(M)}-e^{\beta t}\|u_{\varepsilon,j}\|_{L^\infty(M)}\\
&\ &+n\log t-\beta n(t\log t-t)+\beta nt\log t+\hat{C}\\
&\leq& -\beta \varphi_{0,j}+n\log t+n\beta t+\hat{C}.
\end{eqnarray*}
Therefore, we have
\begin{eqnarray*}e^{\frac{\partial}{\partial t}\psi_{\varepsilon,j}(t)-\beta\psi_{\varepsilon,j}(t)}\omega_0^n\leq
t^ne^{n\beta t}e^{-\beta \varphi_{0,j}+\hat{C}}\omega_0^n.
\end{eqnarray*}
When $t$ is sufficiently small,
\begin{eqnarray*}\omega_0+\sqrt{-1}\partial\overline{\partial}\psi_{\varepsilon,j}(t)&=&(1-te^{\beta t})(\omega_0+\sqrt{-1}\partial\overline{\partial}\varphi_{0,j})+te^{\beta t}(\omega_0+\sqrt{-1}\partial\overline{\partial}u_{\varepsilon,j})\\
&\geq&te^{\beta t}(\omega_0+\sqrt{-1}\partial\overline{\partial}u_{\varepsilon,j}).
\end{eqnarray*}
Combining the above inequalities,
\begin{eqnarray*}(\omega_0+\sqrt{-1}\partial\overline{\partial}\psi_{\varepsilon,j}(t))^n&\geq&t^ne^{n\beta t}(\omega_0+\sqrt{-1}\partial\overline{\partial}u_{\varepsilon,j})^n\\
&=&t^ne^{n\beta t}e^{-F_{0}-\beta\varphi_{0,j}+\hat{C}}\frac{\omega_{0}^{n}}
{(\varepsilon^2+|s|_{h}^{2})^{(1-\beta)}}\\
&\geq&e^{-F_{0}+\frac{\partial}{\partial t}\psi_{\varepsilon,j}(t)-\beta\psi_{\varepsilon,j}(t)}\frac{\omega_{0}^{n}}
{(\varepsilon^2+|s|_{h}^{2})^{(1-\beta)}}.
\end{eqnarray*}
This equation is equivalent to
\begin{eqnarray}
\begin{cases}
  \frac{\partial}{\partial t}\psi_{\varepsilon,j}(t)\leq \log \frac{(\omega_0+\sqrt{-1}\partial\overline{\partial}\psi_{\varepsilon,j}(t))^n}{\omega_{0}^{n}}+\beta\psi_{\varepsilon,j}(t)\\
  \ \ \ \ \ \ \ \ \ \ \ \ \ \ +F_{0}+\log
(\varepsilon^2+|s|_{h}^{2})^{(1-\beta)}.\\
  \psi_{\varepsilon,j}(0)=\varphi_{0,j}\\
  \end{cases}
\end{eqnarray}

Let $\tilde{\psi}_{\varepsilon,j}(t)=\varphi_{\varepsilon,j}(t)-\psi_{\varepsilon,j}(t)$, then
\begin{eqnarray}
\begin{cases}
  \frac{\partial}{\partial t}\tilde{\psi}_{\varepsilon,j}(t)\geq \log
   \frac{(\omega_0+\sqrt{-1}\partial\overline{\partial}\psi_{\varepsilon,j}(t)+\sqrt{-1}\partial\overline{\partial}\tilde{\psi}_{\varepsilon,j}(t))^n}
  {(\omega_0+\sqrt{-1}\partial\overline{\partial}\psi_{\varepsilon,j}(t))^n}+\beta\tilde{\psi}_{\varepsilon,j}(t).\\
  \\
  \tilde{\psi}_{\varepsilon,j}(0)=0\\
  \end{cases}
\end{eqnarray}
By the similar arguments as that in the proof of Proposition \ref{218}, for any $(t,z)\in[0,T]\times M$,
\begin{eqnarray}\tilde{\psi}_{\varepsilon,j}(t,z)\geq0,
\end{eqnarray}
That is,  for any $(t,z)\in[0,T]\times M$
\begin{eqnarray}\nonumber\varphi_{\varepsilon,j}(t,z)-\varphi_{0,j}(z)&\geq&-te^{\beta t}\varphi_{0,j}+te^{\beta t}u_{\varepsilon,j}+h(t)e^{\beta t}\\
&\geq&-Cte^{\beta t}+h(t)e^{\beta t} ,
\end{eqnarray}
where constant constant $C$ depends only on $\|\varphi_0\|_{L^\infty(M)}$, $\beta$ and $F_0$. Let $j\rightarrow\infty$ and then $\varepsilon\rightarrow0$, we have
\begin{eqnarray}\varphi(t,z)-\varphi_0(z)\geq-Cte^{\beta t}+h(t)e^{\beta t}.
\end{eqnarray}
There exists $\delta_2$ such that for any $t\in[0,\delta_2]$,
\begin{eqnarray}-Cte^{\beta t}+h(t)e^{\beta t}>-\epsilon.
\end{eqnarray}
Let $\delta=\min(\delta_1,\delta_2)$, then for any $t\in(0,\delta]$ and $z\in M$,
\begin{eqnarray}-\epsilon<\varphi(t,z)-\varphi_{0}(z)<\epsilon.
\end{eqnarray}
This completes the proof of the proposition.{\hfill\ensuremath{\square}}

\begin{thm}\label{225} $\omega_{\varphi(t)}=\omega_{0}+\sqrt{-1}\partial\bar{\partial}\varphi(t)$ is a long-time solution to the conical K\"ahler-Ricci flow $(\ref{TCKRF1})$.
\end{thm}

{\bf Proof:}\ \ We should only prove that $\omega(t)$ satisfies equation $(\ref{TCKRF1})$ in the sense of currents on $[0,\infty)\times M$.

Let $\eta=\eta(t,x)$ be a smooth $(n-1,n-1)$-form with compact support in $(0,\infty)\times M$. Without loss of generality, we assume that its compact support included in $(\delta,T)$ ($0<\delta<T<\infty$). On $[\delta,T]\times M$, by $(\ref{219})$ and $(\ref{220})$, $\log\frac{\omega_{\varepsilon}^{n}(t)(\varepsilon^{2}+|s|_{h}^{2})^{1-\beta}}{\omega_{0}^{n}}$ and $\varphi_{\varepsilon}$ are uniformly bounded by constants depending only on $\|\varphi_0\|_{L^\infty(M)}$, $n$, $\beta$, $\delta$ and $T$. On $[\delta,T]$, we have
\begin{eqnarray}\label{201503201}\nonumber&\ &\int_{M}\frac{\partial\omega_{\varepsilon}(t)}{\partial t}\wedge\eta=\int_{M}\sqrt{-1}\partial\bar{\partial}\frac{\partial\varphi_{\varepsilon}(t)}{\partial t}\wedge\eta\\\nonumber
&=&\int_{M}\sqrt{-1}\partial\bar{\partial}\big(\log\frac{\omega_{\varepsilon}^{n}(\varepsilon^{2}+|s|_{h}^{2})^{1-\beta}}
{\omega_{0}^{n}}+F_{0}+\beta\varphi_{\varepsilon}(t)\big)\wedge\eta\\\nonumber
&=&\int_{M}\log\big(\log\frac{\omega_{\varepsilon}^{n}(\varepsilon^{2}+|s|_{h}^{2})^{1-\beta}}
{\omega_{0}^{n}}+F_{0}+\beta\varphi_{\varepsilon}(t)\big)
  \sqrt{-1}\partial\bar{\partial}\eta\\\nonumber
&\xrightarrow{\varepsilon\rightarrow 0}&\int_{M}(\log\frac{\omega_{\varphi(t)}^{n}}{\omega_{0}^{n}}+F_{0}+\beta\varphi(t)
  +\log|s|_{h}^{2(1-\beta)})\sqrt{-1}\partial\bar{\partial}\eta\\\nonumber
&=&\int_{M}\sqrt{-1}\partial\bar{\partial}(\log\frac{\omega_{\varphi(t)}^{n}}{\omega_{0}^{n}}+F_{0}+\beta\varphi(t)
  +\log|s|_{h}^{2(1-\beta)})\wedge\eta\\
&=&\int_{M} (-Ric (\omega_{\varphi(t)})+\beta \omega_{\varphi(t)} +2\pi (1-\beta ) [D])\wedge\eta.
\end{eqnarray}
At the same time, there also holds
\begin{eqnarray}\label{201503202}\nonumber\int_M \omega_{\varphi_{\varepsilon}(t)}\wedge\frac{\partial\eta}{\partial t}&=&\int_M \omega_{0}\wedge\frac{\partial\eta}{\partial t}+\int_M \sqrt{-1}\partial\bar{\partial}\varphi_{\varepsilon}(t)\wedge\frac{\partial\eta}{\partial t}\\\nonumber
&=&\int_M \omega_{0}\wedge\frac{\partial\eta}{\partial t}+\int_M \varphi_{\varepsilon}(t)\sqrt{-1}\partial\bar{\partial}\frac{\partial\eta}{\partial t}\\\nonumber
&\xrightarrow{\varepsilon_{i}\rightarrow 0}&\int_M \omega_0\wedge\frac{\partial\eta}{\partial t}+\int_M \varphi(t)\sqrt{-1}\partial\bar{\partial}\frac{\partial\eta}{\partial t}\\\nonumber
&=&\int_M \omega_0\wedge\frac{\partial\eta}{\partial t}+\int_M \sqrt{-1}\partial\bar{\partial}\varphi(t)\frac{\partial\eta}{\partial t}\\
&=&\int_M \omega_{\varphi(t)}\wedge\frac{\partial\eta}{\partial t}.
\end{eqnarray}

On the other hand, $\varphi_{\varepsilon}(t)$ and $\frac{\partial\varphi_\varepsilon(t)}{\partial t}$ are uniformly bounded on $[\delta,T]\times M$, $\varphi(t)$ and $\frac{\partial\varphi(t)}{\partial t}$ are uniformly bounded on $[\delta,T]\times(M\setminus D)$, therefore
\begin{eqnarray}\label{201503203}\nonumber\frac{\partial}{\partial t}\int_M \omega_{\varphi_{\varepsilon}(t)}\wedge\eta
&=&\int_M \varphi_{\varepsilon}(t)\sqrt{-1}\partial\bar{\partial}\frac{\partial\eta}{\partial t}\\\nonumber
&\ &+\int_M \frac{\partial\varphi_{\varepsilon}(t)}{\partial t} \sqrt{-1}\partial\bar{\partial}\eta+\int_M \omega_0\wedge\frac{\partial\eta}{\partial t}\\\nonumber
&\xrightarrow{\varepsilon\rightarrow 0}&
\int_M \varphi(t)\sqrt{-1}\partial\bar{\partial}\frac{\partial\eta}{\partial t}\\\nonumber
&\ &+\int_M \frac{\partial\varphi}{\partial t} \sqrt{-1}\partial\bar{\partial}\eta+\int_M \omega_0\wedge\frac{\partial\eta}{\partial t}\\
&=&\frac{\partial}{\partial t}\int_M \omega_{\varphi}\wedge\eta.
\end{eqnarray}
Combining equality

with equalities $(\ref{201503201})$-$(\ref{201503203})$, on $[\delta,T]$, we have
\begin{eqnarray}\label{201503204}\nonumber\frac{\partial}{\partial t}\int_M \omega_{\varphi(t)}\wedge\eta&=&\int_{M} \big(-Ric (\omega_{\varphi(t)})+\beta \omega_{\varphi(t)} +2\pi (1-\beta ) [D]\big)\wedge\eta\\
&\ &+\int_M \omega_{\varphi(t)}\wedge\frac{\partial\eta}{\partial t}.
\end{eqnarray}
Integrating form $0$ to $\infty$ on both sides,
\begin{eqnarray*}\int_{M\times(0,\infty)} \frac{\partial \omega_{\varphi(t)}}{\partial t}\wedge\eta~dt
&=&-\int_{M\times(0,\infty)} \omega_{\varphi(t)}\wedge\frac{\partial\eta}{\partial t}~dt=-\int_{0}^{\infty}\int_M \omega_{\varphi(t)}\wedge\frac{\partial\eta}{\partial t}~dt\\
&=&\int_{0}^{\infty}\int_{M} \big(-Ric (\omega_{\varphi(t)})+\beta \omega_{\varphi(t)} +2\pi (1-\beta ) [D]\big)\wedge\eta~dt\\
&=&\int_{M\times(0,\infty)} \big(-Ric (\omega_{\varphi(t)})+\beta \omega_{\varphi(t)} +2\pi (1-\beta ) [D]\big)\wedge\eta~dt.
\end{eqnarray*}
By the arbitrariness of $\eta$, we prove that $\omega_{\varphi(t)}$ satisfies the conical K\"ahler-Ricci flow $(\ref{TCKRF1})$ in the sense of currents on $(0,\infty)\times M$.  {\hfill\ensuremath{\square}}

\medskip

Now we are ready to prove the uniqueness of the parabolic Monge-Amp\`ere equation $(\ref{223})$ starting with $\varphi_0\in\mathcal{E}_{p}(M,\omega_{0})$ for some $p>1$.

\medskip

\begin{thm}\label{228} Let $\varphi_i(t)\in C^0\big([0,\infty)\times M\big)\bigcap C^\infty\big((0,\infty)\times(M\setminus D)\big)$ $(i=1,2)$ be two  long-time solutions to the parabolic Monge-Amp\`ere equation
\begin{eqnarray}
  \frac{\partial \varphi_i(t)}{\partial t}=\log\frac{(\omega_{0}+\sqrt{-1}\partial\bar{\partial}\varphi_i(t))^{n}}{\omega_{0}^{n}}+F_{0}+\beta\varphi_i(t)
  +\log|s|_{h}^{2(1-\beta)}
\end{eqnarray}
on $(0,\infty)\times (M\setminus D)$. If $\varphi_i$ $(i=1,2)$ satisfy
\begin{itemize}
  \item For any $0<\delta<T<\infty$, there exists uniform constant $C$ such that
\begin{eqnarray*}C^{-1}\omega_\beta\leq \omega_{0}+\sqrt{-1}\partial\bar{\partial}\varphi_i(t)\leq C\omega_\beta
\end{eqnarray*}
on $[\delta,T]\times (M\setminus D)$;
  \item On $[\delta, T]$, there exist constant $\alpha>0$ and $C^{*}$ such that $\varphi_i(t)$ is $C^{\alpha}$ on $M$ with respect to $\omega_{0}$ and $\| \frac{\partial\varphi_i(t)}{\partial t}\|_{L^{\infty}(M\setminus D)}\leqslant C^{*}$;
  \item $\lim\limits_{t\rightarrow0^{+}}\|\varphi_i(t)-\varphi_{0}\|_{L^{\infty}(M)}=0$.
  \end{itemize}
Then $\varphi_1=\varphi_2$.
\end{thm}

\medskip

{\bf Proof:}\ \ We apply Jeffres' trick \cite{TJEF} in the parabolic case. For any $0<t_1<T<\infty$ and $a>0$. Let $\phi_1(t)=\varphi_1(t)+a|s|_h^{2q}$, where $0<q<1$ is determined later. The evolution of $\phi_1$ is
\begin{eqnarray*}
  \frac{\partial \phi_1(t)}{\partial t}=\log\frac{(\omega_{0}+\sqrt{-1}\partial\bar{\partial}\varphi_1(t))^{n}}{\omega_{0}^{n}}+F_{0}+\beta\phi_1(t)
  -a\beta|s|_h^{2q}+\log|s|_{h}^{2(1-\beta)}.
\end{eqnarray*}
Denote $\psi(t)=\phi_1(t)-\varphi_2(t)$ and $\hat{\Delta}=\int_0^1 g_{s\varphi_1+(1-s)\varphi_2}^{i\bar{j}}\frac{\partial^2}{\partial z^i\partial\bar{z}^j}ds$, $\psi(t)$ evolves along the following equation
\begin{eqnarray*}
  \frac{\partial \psi(t)}{\partial t}=\hat{\Delta}\psi(t)-a\hat{\Delta}|s|_h^{2q}+\beta\psi(t)
  -a\beta|s|_h^{2q}.
\end{eqnarray*}
By the equivalence of the metrics and the equation
\begin{eqnarray*}
\sqrt{-1}\partial\overline{\partial}|s|_h^{2q}=q^2|s|_h^{2q}\sqrt{-1}\partial\log|s|_h^{2}\wedge\overline{\partial}\log|s|_h^{2}
+q|s|_h^{2q}\sqrt{-1}\partial\overline{\partial}\log|s|_h^{2},
\end{eqnarray*}
we obtain the estimate
\begin{eqnarray*}
\hat{\Delta}|s|_h^{2q}&\geq &q|s|_h^{2q}g_{s\varphi_1+(1-s)\varphi_2}^{i\bar{j}}(\frac{\partial^2}{\partial z^i\partial\bar{z}^j}\log|s|_h^{2})\\
&=&-q|s|_h^{2q}g_{s\varphi_1+(1-s)\varphi_2}^{i\bar{j}}g_{0,i\bar{j}}\\
&\geq&-Cq|s|_h^{2q}g_{\beta}^{i\bar{j}}g_{0,i\bar{j}}\\
&\geq&-C
\end{eqnarray*}
on $M\setminus D$, where constant $C$ independent of $a$, and we apply the fact that $\omega_\beta\geq\gamma\omega_0$ on $M\setminus D$ for some constant $\gamma$. Then we obtain
\begin{eqnarray*}
  \frac{\partial \psi(t)}{\partial t}\leq\hat{\Delta}\psi(t)+\beta\psi(t)+aC.
\end{eqnarray*}

Let $\tilde{\psi}=e^{-\beta (t-t_1)}\psi+\frac{aC}{\beta}e^{-\beta (t-t_1)}-\epsilon (t-t_1)$. By choosing suitable $0<q<1$, we can  assume that the space maximum of $\tilde{\psi}$ on $[t_1,T]\times M$ is attained away from $D$. Let $(t_0,x_0)$ be the maximum point. If $t_0>t_1$, by the maximum principle, at $(t_0,x_0)$, we have
\begin{eqnarray*}
 0\leq (\frac{\partial }{\partial t}-\hat{\Delta})\tilde{\psi}(t)\leq -\epsilon,
\end{eqnarray*}
which is impossible, hence $t_0=t_1$. Then for $(t,x)\in [t_1,T]\times M$, we obtain
\begin{eqnarray*}
\psi(t,x)&\leq& e^{\beta T}\|\varphi_1(t_1,x)-\varphi_2(t_1,x)\|_{L^\infty(M)}\\
&\ &+aCe^{\beta T}+\epsilon Te^{\beta T}
\end{eqnarray*}
Let $a\rightarrow0$ and then $t_1\rightarrow0^+$, we get
\begin{eqnarray*}
\varphi_1(t)-\varphi_2(t)\leq \epsilon Te^{\beta T}.
\end{eqnarray*}
It shows that $\varphi_1(t)\leq\varphi_2(t)$ after we let $\epsilon\rightarrow0$. By the same reason we have $\varphi_2(t)\leq\varphi_1(t)$, then we prove that $\varphi_1(t)=\varphi_2(t)$.{\hfill\ensuremath{\square}}

\medskip

\begin{thm}\label{229} $\omega_{\varphi(t)}=\omega_{0}+\sqrt{-1}\partial\bar{\partial}\varphi(t)$ is the unique long-time solution to the conical K\"ahler-Ricci flow $(\ref{TCKRF1})$.
\end{thm}

\medskip

{\bf Proof:}\ \ Suppose there is another solution $\omega_{\phi(t)}=\omega_0+\sqrt{-1}\partial\bar{\partial}\phi(t)$ to the conical K\"ahlre-Ricci flow $(\ref{TCKRF1})$. It is easy to see that 
\begin{eqnarray}\label{20150618}
\frac{\partial\phi(t)}{\partial t}=\log\frac{(\omega_{0}+\sqrt{-1}\partial\bar{\partial}\phi(t))^{n}}{\omega_{0}^{n}}+F_{0}+\beta\phi(t)
  +\log|s|_{h}^{2(1-\beta)}+f(t)
\end{eqnarray}
on $(0,\infty)\times (M\setminus D)$ for a smooth function $f(t)$ defined on $(0,\infty)$, and $\phi(t)\in C^0\big([0,\infty)\times M\big)\bigcap C^\infty\big((0,\infty)\times(M\setminus D)\big)$ satisfies
\begin{itemize}
  \item For any $0<\delta<T<\infty$, there exists uniform constant $C$ such that
\begin{eqnarray*}C^{-1}\omega_\beta\leq \omega_{0}+\sqrt{-1}\partial\bar{\partial}\phi(t)\leq C\omega_\beta\ \ \ \ on\ \ \ \ [\delta,T]\times (M\setminus D);
\end{eqnarray*}

  \item On $[\delta, T]$, there exist constant $\alpha>0$ and $C$ such that $\phi(t)$ is $C^{\alpha}$ on $M$ with respect to $\omega_{0}$ and $\| \frac{\partial\phi(t)}{\partial t}\|_{L^{\infty}(M\setminus D)}\leqslant C$;
  \item $\lim\limits_{t\rightarrow0^{+}}\|\phi(t)-\varphi_{0}\|_{L^{\infty}(M)}=0$.
  \end{itemize}

For any $0<t_1<T<\infty$ and $a>0$. Let $\psi(t)=\phi(t)+a|s|_h^{2q}-\varphi(t)$, where $0<q<1$ is determined later. Then
\begin{eqnarray*}
  \frac{\partial \psi(t)}{\partial t}=\hat{\Delta}\psi(t)-a\hat{\Delta}|s|_h^{2q}+\beta\psi(t)
  -a\beta|s|_h^{2q}+f(t).
\end{eqnarray*}
By the same arguments as that in the proof of Proposition \ref{228}, for any $(t,x)\in [t_1,T]\times M$, we have
\begin{eqnarray*}
\psi(t,x)&\leq& e^{\beta (t-t_1)}\|\phi(t_1,x)-\varphi(t_1,x)\|_{L^\infty(M)}\\
&\ &+aCe^{\beta (t-t_1)}+\epsilon (t-t_1)e^{\beta (t-t_1)}\\
&\ &+e^{\beta (t-t_1)}\int_{t_1}^te^{-\beta (s-t_1)}f(s)ds
\end{eqnarray*}
Let $a\rightarrow0$, we obtain
\begin{eqnarray*}
\phi(t)-\varphi(t)&\leq& e^{\beta (t-t_1)}\|\phi(t_1,x)-\varphi(t_1,x)\|_{L^\infty(M)}\\
&\ &+\epsilon (t-t_1)e^{\beta (t-t_1)}+e^{\beta (t-t_1)}\int_{t_1}^te^{-\beta (s-t_1)}f(s)ds.
\end{eqnarray*}
By the similar arguments, we can obtain
\begin{eqnarray*}
\varphi(t)-\phi(t)&\leq& e^{\beta (t-t_1)}\|\phi(t_1,x)-\varphi(t_1,x)\|_{L^\infty(M)}\\
&\ &+\epsilon (t-t_1)e^{\beta (t-t_1)}-e^{\beta (t-t_1)}\int_{t_1}^te^{-\beta (s-t_1)}f(s)ds.
\end{eqnarray*}
Therefore, for any $t>t_1>0$, we have
\begin{eqnarray*}
\inf\limits_{M}(\phi(t)-\varphi(t))&\geq& \sup\limits_{M}(\phi(t)-\varphi(t))-2e^{\beta (t-t_1)}\|\phi(t_1,x)-\varphi(t_1,x)\|_{L^\infty(M)}\\
&\ &-2\epsilon (t-t_1)e^{\beta (t-t_1)}\\
&\geq& \sup\limits_{M}(\phi(t)-\varphi(t))-2e^{\beta T}\|\phi(t_1,x)-\varphi(t_1,x)\|_{L^\infty(M)}\\
&\ &-2\epsilon Te^{\beta T}.
\end{eqnarray*}
Let $t_1\rightarrow0^+$ and then $\epsilon\rightarrow0$, we conclude that $\phi(t)=\varphi(t)+e^{\beta t}\int_{0}^te^{-\beta s}f(s)ds$. Then
$\omega_{\phi(t)}=\omega_{\varphi(t)}$ on $(0,\infty)\times (M\setminus D)$. {\hfill\ensuremath{\square}}


