\title{Topological equivalence of holomorphic foliation germs of rank $1$ with isolated singularity in the Poincar\'e domain}


\date{\today}


\author{Thomas Eckl and Michael Loenne}


\keywords{holomorphic foliation germs, isolated singularity, topological equivalence, Poincar\'e domain}


\subjclass{32S65, 58K45}





