\begin{align}
\label{eq:EnReal}
 E_n^{(ij)} &= \frac{1}{2} \int_{\theta_{\rm min}}^{\theta_{\rm max}}
 {\rm d}\vartheta\,\vartheta\: 
 [T_{+n}(\vartheta)\,\xi^{(ij)}_+(\vartheta) +
 T_{-n}(\vartheta)\,\xi^{(ij)}_-(\vartheta)]\;, \\
 \label{eq:BnReal}
 B_n^{(ij)} &= \frac{1}{2} \int_{\theta_{\rm min}}^{\theta_{\rm
     max}}{\rm d}\vartheta\,\vartheta\: 
 [T_{+n}(\vartheta)\,\xi^{(ij)}_+(\vartheta) -
 T_{-n}(\vartheta)\,\xi^{(ij)}_-(\vartheta)]\;,
\end{align}

\begin{equation}
\label{eqCorrectionParameter}
 \tens{C}_{\rm par}=\frac{n_{\rm sim}-n_{\rm obs}-2}{n_{\rm sim}-n_{\rm obs}+n_{\rm par}-1}
 \tens{\hat C}_{\rm par}\;,
\end{equation}

\begin{equation}
 \hat\xi_\pm^{ij}(\vartheta)=\frac{\sum w_a w_b
 \left[\epsilon_{\rm t}^i({\pmb x}_a)\epsilon_{\rm t}^i({\pmb x}_b)
 \pm\epsilon_{\rm x}^i({\pmb x}_a)\epsilon_{\rm x}^i({\pmb x}_b)\right]}
 {\sum w_a w_b}\;,
\end{equation}

