0&smooth&no\\ \hline
n&$I_n$&$A_{n-1}$ \\ \hline
2&$II$&no \\ \hline
3&$III$&$A_{1}$ \\ \hline
4&$IV$&$A_{2}$ \\ \hline
n+6&$I^*_n$&$D_{n+4}$ \\ \hline
8&$IV^*$&$E_{6}$ \\ \hline
9&$III^*$&$E_{7}$ \\ \hline
10&$II^*$&$E_{8}$ \\ \hline
\end{tabular}\\
\end{center}
\end{table}
