In particular,
\begin{eqnarray}\label{a-priori-grad-linear-est}
\left(\partial_t-\Delta_{g_0(t)}\right)\left((1+t)^{-c}\left(\arrowvert h\arrowvert_{g_0(t)}^2+t\arrowvert\nabla^{g_0(t)} h\arrowvert_{g_0(t)}^2\right)\right)\leq 0,\quad t\geq 0.
\end{eqnarray}

To end the argument, we need to prove a mean value inequality in the spirit of Zhang \cite{Zha-Qi-Gra-Est} in a Ricci flow setting. The proof is very close to his, but we reproduce it here for the convenience of the reader and for the consistency of the paper. We must be careful about the time dependence of the constants involved in this mean value inequality.

\begin{prop}[$L^1$ mean value inequality]\label{L^1-mean-inequ}
Let $(M^n,g_0(t))_{t\geq 0}$ be a Type III solution of the Ricci flow with nonnegative Ricci curvature which is noncollapsed, i.e. 
\begin{eqnarray*}
(1+t)\arrowvert{\mathop{\rm Rm}\nolimits}(g_0(t))\arrowvert_{g_0(t)}\leq R_0,\quad{\mathop{\rm Ric}\nolimits}(g_0(t))\geq 0,\quad {\mathop{\rm AVR}\nolimits}(g_0(t))\geq V_0>0,\quad  t\geq0. 
\end{eqnarray*}

 Then any nonnegative subsolution $u$ of the heat equation, i.e.
\begin{eqnarray*}
\partial_tu\leq \Delta_{g_0(t)}u,\quad \mbox{on $M\times (0,+\infty)$,}
\end{eqnarray*}
satisfies, for $r^2< t$, $\theta\in(0,1)$,
\begin{eqnarray*}
\sup_{P\left(x,t,\theta r\right)}u\leq \frac{c(n,V_0,R_0,\theta)}{r^{n+2}}\int_{P(x,t,r)}ud\mu_{g_0(s)}ds,
\end{eqnarray*}
where $P(x,t,r):=\{(y,s)\in M\times[0,+\infty)\quad|\quad s\in (t-r^2,t],\quad y\in B_{g_0(s)}(x,r)\}.$
\end{prop}
\begin{proof}[Proof of proposition \ref{L^1-mean-inequ}]
Let $p\in [1,+\infty)$. Then, 
\begin{eqnarray}\label{sub-sol-power}
\partial_tu^p\leq \Delta_{g_0(t)}u^p,
\end{eqnarray}
 on $M\times (0,+\infty)$. Take any smooth space-time cutoff function $\psi$ and multiply (\ref{sub-sol-power}) by $\psi^2u^p$ and integrate by parts as follows : 
 \begin{eqnarray*}
&&-\int_{t- r^2}^{t'}\int_M\psi^2u^p\Delta_{g_0(s)}u^pd\mu_{g_0(s)}ds\leq -\int_{t-r^2}^{t'}\int_M\psi^2u^p\partial_su^pd\mu_{g_0(s)}ds,\\
&&\int_{t- r^2}^{t'}\int_M\arrowvert\nabla^{g_0(s)}(\psi u^p)\arrowvert^2_{g_0(s)}-\arrowvert\nabla^{g_0(s)}\psi\arrowvert^2_{g_0(s)}u^{2p} d\mu_{g_0(s)}ds\leq\\
&& \int_{t-r^2}^{t'}\int_M\frac{\partial_s(\psi^2\ln d\mu_{g_0(s)})}{2}u^{2p}d\mu_{g_0(s)}ds-\frac{1}{2}\int_M\psi^2u^{2p}d\mu_{g_0(t)},
\end{eqnarray*}
for any $t'\in(t-r^2,t].$
 Since the scalar curvature is nonnegative, $\partial_s(\ln d\mu_s)=-{\mathop{\rm R}\nolimits}_{g_0(s)}\leq 0$, for $s\geq 0$. Hence,
\begin{eqnarray*}
&&\int_{t-r^2}^{t'}\int_M\arrowvert\nabla^{g_0(s)}(\psi u^p)\arrowvert^2_{g_0(s)}d\mu_{g_0(s)}ds+\frac{1}{2}\int_M\psi^2u^{2p}d\mu_{g_0(t)}\leq\\
&&\int_{t-r^2}^{t'}\int_M\left(\frac{\partial_s\psi^2}{2}+\arrowvert\nabla^{g_0(s)}\psi\arrowvert_{g_0(s)}^2\right)u^{2p}d\mu_{g_0(s)}ds.
\end{eqnarray*}
Let $\tau, \sigma\in (0,+\infty)$ such that $\tau+\sigma<r$. In particular, $P(x, \tau,\sigma)\subset P(x,t,\tau+\sigma)\subset P(x,t,r)$.
Now, choose two smooth functions $\phi:\mathbb{R}_+\rightarrow[0,1]$ and $\eta:\mathbb{R}_+\rightarrow[0,1]$ such that
\begin{eqnarray*}
&& {\mathop{\rm supp}\nolimits}(\phi)\subset [\tau,\tau+\sigma],\quad\phi\equiv 1\quad\mbox{in $[0,\tau]$},\quad \phi\equiv 0\quad\mbox{in $[\tau+\sigma,+\infty)$},\quad -c/\sigma\leq \phi'\leq 0,\\
&& {\mathop{\rm supp}\nolimits}(\eta)\subset [t-(\tau+\sigma)^2,+\infty),\quad\eta\equiv 1\quad\mbox{in $[t-\tau^2,+\infty)$},\\
&& \eta\equiv 0\quad\mbox{in $(t-r^2,t-(\tau+\sigma)^2]$},\quad 0\leq \eta'\leq c/\sigma^2.
\end{eqnarray*}
Define $\psi(y,s):=\phi(d_{g_0(s)}(x,y))\eta(s)$, for $(y,s)\in M\times(0,+\infty)$. Then, thanks to lemma \ref{C^0-distance-est-Type-III},
\begin{eqnarray*}
&&\arrowvert\nabla^{g_0(s)}\psi\arrowvert_{g_0(s)}\leq\frac{c(R_0)}{\sigma},\quad \arrowvert\partial_s\psi\arrowvert\leq \frac{c}{\sigma^2}+\frac{c(R_0)}{\sigma{\sqrt{t-(\tau+\sigma)^2}}},
\end{eqnarray*}
for some uniform positive constant $c(R_0)$.
On the other hand, $(M^n,g_0(s))_{s\geq 0}$ satisfies the following Euclidean Sobolev inequality [Chap.$3$, \cite{Sal-Cos-Sob-Boo}] since it has nonnegative Ricci curvature and it is non collapsed :
\begin{eqnarray*}
\left(\int_M(\psi u^p)^{\frac{2n}{n-2}}d\mu_{g_0(s)}\right)^{\frac{n-2}{n}}\leq c(n,V_0)\int_M\arrowvert\nabla^{g_0(s)}(\psi u^p)\arrowvert_{g_0(s)}^2d\mu_{g_0(s)},
\end{eqnarray*}
for any $s\geq 0$.
We could have used a local Sobolev inequality which would amount to the same by using the non collapsing assumption.

Now, by Hlder inequality, for $s\geq 0$,
\begin{eqnarray*}
\int_M (\psi u^p)^{2+\frac{4}{n}}d\mu_{g_0(s)}\leq\left(\int_M (\psi u^p)^{\frac{2n}{n-2}}d\mu_{g_0(s)}\right)^{\frac{n-2}{n}}\left(\int_M (\psi u^p)^2d\mu_{g_0(s)}\right)^{\frac{2}{n}}.
\end{eqnarray*}
Therefore, to sum it up, if $\alpha_n:=1+2/n$,
\begin{eqnarray*}
\int_{P(x,t,\tau)}\left(u^{2p}\right)^{\alpha_n}d\mu_{g_0(s)}ds&\leq&\int_{P(x,t,r)}\left(\psi u^p\right)^{2\alpha_n}d\mu_{g_0(s)}ds\\
&\leq&\int_{t-r^2}^t\left(\int_M (\psi u^p)^{\frac{2n}{n-2}}d\mu_{g_0(s)}\right)^{\frac{n-2}{n}}\left(\int_M (\psi u^p)^2d\mu_{g_0(s)}\right)^{\frac{2}{n}}ds\\
&\leq&c(n,V_0,R_0)\sup_{s\in(t-r^2,t]}\left(\int_M (\psi u^p)^2d\mu_{g_0(s)}\right)^{\frac{2}{n}}\int_{P(x,t,r)}\arrowvert\nabla^{g_0(s)}(\psi u^p)\arrowvert_{g_0(s)}^2d\mu_{g_0(s)}\\
&\leq&c(n,V_0,R_0)\left(\frac{1}{\sigma^2}+\frac{1}{\sigma{\sqrt{t-(\tau+\sigma)^2}}}\right)\left(\int_{P(x,t,\tau+\sigma)}u^{2p}d\mu_{g_0(s)}\right)^{\alpha_n}.
\end{eqnarray*}

Define the following sequences : 
\begin{eqnarray*}
p_i:=\alpha_n^i,\quad \sigma_i:=2^{-1-i}(1-\theta)r,\quad \tau_{-1}:=r,\quad\tau_i:=r-\sum_{j=0}^i\sigma_j,\quad i\geq 0.
\end{eqnarray*}
Then, $\lim_{i\rightarrow +\infty}\tau_i=\theta r$ and, for any $i\geq 0$,
\begin{eqnarray*}
\| u^2\|_{L^{p_{i+1}}(P(x,t,\tau_i))}\leq\left(c(n,V_0,R_0)\left(\frac{1}{\sigma_i^2}+\frac{1}{\sigma_i{\sqrt{t-\tau_{i-1}^2}}}\right)\right)^{\frac{1}{\alpha_n}}\| u^2\|_{L^{p_i}(P(x,t,\tau_{i-1}))},
\end{eqnarray*}
i.e.
\begin{eqnarray*}
\| u\|^2_{L^{\infty}(P(x,t,\theta r))}\leq\Pi_{i=0}^{\infty}\left(c(n,V_0,R_0)\left(\frac{1}{\sigma_i^2}+\frac{1}{\sigma_i{\sqrt{t-\tau_{i-1}^2}}}\right)\right)^{\frac{1}{\alpha_n}}\| u\|^2_{L^{2}(P(x,t,r))}.
\end{eqnarray*}
It remains to estimate the previous infinite product. It turns out that 
\begin{eqnarray*}
&&\sqrt{t-\tau_{i-1}^2}\geq\sqrt{t-(\theta r)^2}\geq c(\theta)\sigma_0\geq c(\theta)\sigma_i,\quad i\geq 0,\\
&& c(\theta)= 2\left(\frac{1+\theta}{1-\theta}\right)^{1/2}.
\end{eqnarray*}
Therefore,
\begin{eqnarray*}
\Pi_{i=0}^{\infty}\left(\frac{1}{\sigma_i^2}+\frac{1}{\sigma_i{\sqrt{t-\tau_{i-1}^2}}}\right)^{\frac{1}{\alpha_n}}&\leq& \max\left\{1,\frac{1}{c(\theta)}\right\}^{1+n/2}\Pi_{i=0}^{+\infty}\left(4^{i+1}(1-\theta)^{-2}r^{-2}\right)^{\alpha_n^{-i}}\\
