\item Finally, the $R_{1}$ term can be handled in the same way after integrating by parts by using proposition \ref{cov-der-bound}.
\end{itemize}
\end{proof}

\begin{lemma}[$L^{2}$ estimate]\label{L-2-non-hom}
Let $R$ be in $Y$. Then any solution of the form $h:=K_L\ast R$ satisfies the following estimate : 
\begin{eqnarray*}
\sup_{(x,t)\in M\times \mathbb{R}_+^*}\sqrt{t}{\hcancel{\|}}  \nabla h\|_{L^2(P(x,\sqrt{t}))}\leq c\| R\|_{Y},
\end{eqnarray*}
for some positive uniform constant $c$.
\end{lemma}
\begin{proof}[Proof of lemma \ref{L-2-non-hom}]
The proof goes along the same lines of the proof of the corresponding estimate for the homogeneous equation : see the proof of theorem \ref{Est-Hom-lin-equ}.

Since $(h(t))_{t\geq 0}$ satisfies $\partial_th=L_th+R$, one has,
\begin{eqnarray*}
\partial_t\arrowvert h\arrowvert_{g_0(t)}^2\leq \Delta_{g_0(t)}\arrowvert h\arrowvert^2_{g_0(t)}-2\arrowvert\nabla^{g_0(t)}h\arrowvert_{g_0(t)}^2+c(n)\arrowvert{\mathop{\rm Rm}\nolimits}(g_0(t))\arrowvert_{g_0(t)}\arrowvert h\arrowvert_{g_0(t)}^2+2< R,h>_{g_0(t)}.
\end{eqnarray*}
Multiplying this inequality by any smooth cut-off function $\psi : M\times\mathbb{R}_+\rightarrow \mathbb{R}_+$ gives, after integrating by parts (in space) and using Young's inequality : 
\begin{eqnarray*}
&&\partial_t\int_M\psi^2\arrowvert h\arrowvert^2_{g_0(t)}d\mu_{g_0(t)}+\int_M\psi^2\arrowvert \nabla^{g_0(t)}h\arrowvert_{g_0(t)}^2d\mu_{g_0(t)}\leq\\
&& c(n)\int_M\left(\arrowvert \nabla^{g_0(t)}\psi\arrowvert_{g_0(t)}^2+\partial_t\psi^2+\psi^2\arrowvert{\mathop{\rm Rm}\nolimits}(g_0(t))\arrowvert_{g_0(t)}\right)\arrowvert h\arrowvert^2_{g_0(t)}d\mu_{g_0(t)}\\
&&+c(n)\int_M\psi^2\arrowvert R_1\arrowvert_{g_0(t)}^2+\arrowvert R_0\arrowvert_{g_0(t)}\psi^2\arrowvert h\arrowvert_{g_0(t)}d\mu_{g_0(t)}.
\end{eqnarray*}
Now, let $(x,t)\in M\times\mathbb{R}_+^*$ and consider the following cutoff function : $\psi_{x,t}(y,s):=\psi(d_{g_0(s)}(x,y)/\sqrt{t}),$ where $\psi:\mathbb{R}_+\rightarrow \mathbb{R}_+ $ is a smooth function such that $\psi_{|[0,1]}\equiv 1$, $\psi_{|[2,+\infty)}\equiv 0$ and $\sup_{\mathbb{R}_+}\arrowvert\psi'\arrowvert\leq c$. $\psi_{x,t}$ is a Lipschitz function satisfying :
\begin{eqnarray*}
&&\arrowvert\nabla^{g_0(s)}\psi_{x,t}\arrowvert_{g_0(s)}\leq \frac{c}{\sqrt{t}},\quad0\leq\partial_s\psi_{x,t}\leq \frac{c}{\sqrt{t}\sqrt{s}},
\end{eqnarray*}
almost everywhere, by lemma \ref{C^0-distance-est-Type-III}. Hence, by integrating in time together with lemma \ref{L-infty-non-hom} : 
\begin{eqnarray*}
&&\int_0^t\int_{B_{g_0(s)}(x,\sqrt{t})}\arrowvert\nabla^{g_0(s)}h\arrowvert^2_{g_0(s)}d\mu_{g_0(s)}ds\leq\\
&& c(n)\left(t^{\frac{n}{2}}+\int_0^{t}\int_{B_{g_0(s)}(x,2\sqrt{t})}\arrowvert{\mathop{\rm Rm}\nolimits}(g_0(s))\arrowvert_{g_0(s)}d\mu_{g_0(s)}ds\right)\| h\|^2_{L^{\infty}(P(x,2\sqrt{t}))}\\
&&+c(n)t^{\frac{n}{2}}\| R\|^2_Y\leq c(n,g_0)t^{\frac{n}{2}}\| R\|^2_Y,
\end{eqnarray*}
since the curvature of $g_0$ has quadratic curvature decay : see the proof of theorem \ref{Est-Hom-lin-equ}.

\end{proof}

\begin{lemma}[$L^{n+4}$ estimate]\label{L-(n+4)-non-hom}
Let $R$ be in $Y$. Then any solution of the form $h:=K_L\ast R$ satisfies the following estimate : 
\begin{eqnarray*}
\sup_{(x,t)\in M\times \mathbb{R}_+^*}\sqrt{t}{\hcancel{\|}}  \nabla h\|_{L^{n+4}(P(x,\sqrt{t})\setminus P(x,\sqrt{t}/2))}\leq c\| R\|_{Y},
\end{eqnarray*}
for some positive uniform constant $c$.
\end{lemma}
\begin{proof}[Proof of lemma \ref{L-(n+4)-non-hom}]
As noticed in \cite{Koc-Lam-Rou}, the method used in the proof of lemma \ref{L-infty-non-hom} to estimate $I_{Q^c(x_0,\sqrt{t_0})}$ for $(x_0,t_0)\in M\times\mathbb{R}_+^*$ implies that 
\begin{eqnarray*}
&&\sqrt{t_0}\sup_{(x,t)\in Q(x_0,\sqrt{t_0})}\int_{Q^c(x_0,\sqrt{t_0})}\left\arrowvert<\nabla^{g_0(t)}_xK_L(x,t,y,s),R_0(y,s)>\right \arrowvert d\mu_{g_0(s)}(y)ds\leq c\|R\|_{Y},\\
&&\sqrt{t_0}\sup_{(x,t)\in Q(x_0,\sqrt{t_0})}\int_{Q^c(x_0,\sqrt{t_0})}\left\arrowvert<\nabla^{g_0(s)}_y\nabla^{g_0(t)}_xK_L(x,t,y,s),R_1(y,s)>\right\arrowvert d\mu_{g_0(s)}(y)ds\leq c\|R\|_{Y}.
\end{eqnarray*}
That is why we assume from now on that the support of $R_0$ and $R_1$ is contained in a parabolic neighborhood of the form $Q_0:=Q(x_0,\sqrt{t_0})$.
\begin{itemize}

\item 

[$R_0$ estimate] 


Consider the following map : 
\begin{eqnarray*}
T_{K_L}(R_0)(x,t):=\int_{M\times \mathbb{R}_+}<\nabla^{g_0(t)}_xK_L(x,t,y,s)1_{s<t},R_0(y,s)>d\mu_{g_0(s)}ds.
\end{eqnarray*}
 In order to apply the Riesz convexity theorem, we show first that $T_{K_L}$ is of type $(1,p)$ and of type $(p',\infty)$ where $p'$ is the conjugate exponent of $p$, as soon as $p\in\left[1,\frac{n+2}{n+1}\right)$.


 $T_{K_L}$ is of type $(p',\infty)$ : indeed, by Hlder's inequality, one has
 \begin{eqnarray*}
\arrowvert T_{K_L}(R_0)\arrowvert(x,t)\leq \|\nabla^{g_0(t)}_xK_L(x,t,\cdot,\cdot)\|_{L^p(M\times (0,t))}\|R_0\|_{L^{p'}(Q_0)},
\end{eqnarray*}
for $(x,t)\in M\times \mathbb{R}_+^*.$
Now, by the Gaussian estimates established in theorem \ref{Gausian-est-scal-heat-ker} together with proposition \ref{cov-der-bound}, one gets by the co-area formula :
\begin{eqnarray*}
\|\nabla^{g_0(t)}_xK_L(x,t,\cdot,\cdot)1_{s<t}\|^p_{L^p(M\times(0,t))}&\leq& c\int_0^t\frac{(t-s)^{n/2}}{(t-s)^{\frac{(n+1)p}{2}}}ds\\
&\leq& ct^{\left(\frac{n}{2}+1\right)\left(1-p\right)+\frac{p}{2}},
\end{eqnarray*}
if $p\in\left[1,\frac{n+2}{n+1}\right)$ where $c$ is independent of time and space. Therefore, 
\begin{eqnarray*}
\|\sqrt{t}T_{K_L}(R_0)\|_{L^{\infty}(Q_0)}\leq c{\hcancel{\|}} tR_0\|_{L^{p'}(Q_0)}.\\
\end{eqnarray*}

$T_{K_L}$ is of type $(1,p)$ : indeed, by Minkowski's integral inequality, one has, if $p\in\left[1,\frac{n+2}{n+1}\right)$,
\begin{eqnarray*}
&&\|T_{K_L}(R_0)\|_{L^p(M\times(0,t_0))}\\
&\leq&\int_{M\times(0,t_0)}\left(\int_{M\times(0,t_0)}\|\nabla^{g_0(t)}_xK_L(x,t,y,s)1_{s<t}\|_g^p d\mu_t(x)dt\right)^{\frac{1}{p}}\arrowvert R_0\arrowvert_{g_0(s)}(y,s)d\mu_{g_0(s)}(y)ds\\
&\leq& ct_0^{\left(\frac{n}{2}+1\right)\left(\frac{1}{p}-1\right)+\frac{1}{2}}\|R_0\|_{L^1(Q_0)}, 
\end{eqnarray*}
which reads : 
\begin{eqnarray*}
{\hcancel{\|}} \sqrt{t_0}T_{K_L}(R_0)\|_{L^p(Q_0)}\leq c {\hcancel{\|}} t_0R_0\|_{L^1(Q_0)}.
\end{eqnarray*}
Therefore, by interpolation, $T_{K_L}$ is of type $(p_t,q_t)$ where $t\in[0,1]$ and $p_t, q_t$ are defined by 
\begin{eqnarray*}
\frac{1}{p_t}:=\frac{1-t}{1}+\frac{t}{p'},\quad \frac{1}{q_t}:=\frac{1-t}{p}+\frac{t}{\infty}.
\end{eqnarray*}
In particular, with $(p_t,q_t):=((n+4)/2, n+4)$ and $p:=\frac{n+4}{n+3}\in\left[1,\frac{n+2}{n+1}\right)$,
\begin{eqnarray*}
{\hcancel{\|}} \sqrt{t_0}T_K(R_0)\|_{L^{n+4}(Q_0)}\leq c {\hcancel{\|}} t_0R_0\|_{L^{\frac{n+4}{2}}(Q_0)}
\end{eqnarray*}




\item 

[$R_1$-estimate]
The proof is based on singular integrals defined on homogeneous spaces as in \cite{Koc-Lam-Rou}. Indeed, by theorem $2.4$ and section $3.2$ of \cite{Koc-Pde-Non-Euc}, the result follows if the following parabolic Riesz operator satisfies suitable estimates that are recalled below. 

Consider $M_+:=M\times \mathbb{R}_+$ endowed with the parabolic distance :
\begin{eqnarray*}
d(x,t,y,s):=\max\{d_{g_0(s)}(x,y),\sqrt{t-s}\},\quad x,y\in M,\quad 0\leq s\leq t.
\end{eqnarray*}
This turns $(M_+,d)$ into a homogeneous space, i.e. a complete metric space with a doubling measure.

Consider the operator $T_{K_L}(R_1):=\nabla^{g_0(\cdot)}(K_L\ast \nabla^{g_0(\cdot)}R_1)$, for $R_1$ with compact support in $Q_0$ as defined above.
This operator is an integral operator whose kernel $\mathcal{K}_L$ is given by the second covariant derivatives of $K_L$. By theorem \ref{Gausian-est-scal-heat-ker} together with proposition \ref{cov-der-bound}, $\mathcal{K}_L$ satisfies on $M_+$, 
\begin{eqnarray*}
&&\|\mathcal{K}_L(x,t,y,s)\|_g\leq cd(x,t,y,s)^{-(n+2)},\\
&&\min\{d(x,t,y,s)^{(n+2)},d(x',t',y',s')^{(n+2)}\|\mathcal{K}_L(x,t,y,s)-\mathcal{K}_L(x',t',y',s')\|_g\leq \\
&&c\frac{d(x,t,x',t')+d(y,s,y',s')}{d(x,t,y,s)+d(x',t',y',s')},
\end{eqnarray*}
for any $(x,t)$, $(y,s)$, $(x',t')$ and $(y',s')$ in $M_+$.

Moreover, by an integration by part on $M_+$, one can check that $T_{K_L}$ is continuous operator on $L^2(Q_0)$, i.e.
\begin{eqnarray*}
\|T_{K_L}(R_1)\|_{L^2(Q_0)}\leq c\|R_1\|_{L^2(Q_0)},
\end{eqnarray*}
where $c$ is a positive constant independent of $Q_0$  and $R_1$.
Therefore, by invoking theorem $2.4$ of \cite{Koc-Pde-Non-Euc}, $T_{K_L}$ is a continuous operator on $L^{n+4}(Q_0)$, which corresponds to the expected estimate after rescaling appropriately by a power of $t_0$.



\end{itemize}


\end{proof}
\end{proof}
To prove theorem \ref{main-theo}, it is now sufficient to invoke lemma \ref{lemma-contraction-map} together with theorem \ref{sec-est-inhom-equ} to show that the map $\Gamma$,
\begin{eqnarray*}
 h\in B_X(0_X,r)\stackrel{\Gamma}{\longmapsto} K_L\ast h(0)+K_L\ast R[h]\in B_X(0_X,r),
\end{eqnarray*}
is well-defined and is a contraction mapping for $r<1$ sufficiently small. Therefore, by the contraction mapping theorem, one gets a locally unique solution to the DeTurck Ricci flow with initial condition $g(0):=g_0+h(0)$.


