\section{Soliton equations}\label{sol-equ-sec}

The next lemma gathers well-known Ricci soliton identities together with the (static) evolution equations satisfied by the curvature tensor.

Recall first that an expanding gradient Ricci soliton is said \textit{normalized} if $\int_Me^{-f}d\mu_g=(4\pi)^{n/2}$ (whenever it makes sense).

\begin{lemma}\label{id-EGS}
Let $(M^n,g,\nabla^g f)$ be a normalized expanding gradient Ricci soliton. Then the trace and first order soliton identities are :
\begin{eqnarray}
&&\Delta_g f = {\mathop{\rm R}\nolimits}_g+\frac{n}{2}, \label{equ:1} \\
&&\nabla^g {\mathop{\rm R}\nolimits}_g+ 2{\mathop{\rm Ric}\nolimits}(g)(\nabla^g f)=0, \label{equ:2} \\
&&\arrowvert \nabla^g f \arrowvert^2+{\mathop{\rm R}\nolimits}_g=f+\mu(g), \label{equ:3}\\
&&{\mathop{\rm div}\nolimits}_g{\mathop{\rm Rm}\nolimits}(g)(Y,Z,T)={\mathop{\rm Rm}\nolimits}(g)(Y,Z, \nabla f,T),\label{equ:4}
\end{eqnarray}
for any vector fields $Y$, $Z$, $T$ and where $\mu(g)$ is a constant called the entropy.\\

The evolution equations for the curvature operator, the Ricci tensor and the scalar curvature are :
\begin{eqnarray}
&& \Delta_f {\mathop{\rm Rm}\nolimits}(g)+{\mathop{\rm Rm}\nolimits}(g)+{\mathop{\rm Rm}\nolimits}(g)\ast{\mathop{\rm Rm}\nolimits}(g)=0,\label{equ:5}\\
&&\Delta_f{\mathop{\rm Ric}\nolimits}(g)+{\mathop{\rm Ric}\nolimits}(g)+2{\mathop{\rm Rm}\nolimits}(g)\ast{\mathop{\rm Ric}\nolimits}(g)=0,\label{equ:6}\\
&&\Delta_f{\mathop{\rm R}\nolimits}_g+{\mathop{\rm R}\nolimits}_g+2\arrowvert{\mathop{\rm Ric}\nolimits}(g)\arrowvert^2=0,\label{equ:7}
\end{eqnarray}
where, if $A$ and $B$ are two tensors, $A\ast B$ denotes any linear combination of contractions of the tensorial product of $A$ and $B$.
\end{lemma}

\begin{proof}
See [Chap.$1$,\cite{Cho-Lu-Ni-I}] for instance.

\end{proof}

\begin{prop}\label{pot-fct-est}
Let $(M^n,g,\nabla^g f)$ be an expanding gradient Ricci soliton.
\begin{itemize}
\item If $(M^n,g,\nabla^g f)$ is non Einstein, if $v:=f+\mu(g)+n/2$,
\begin{eqnarray}
&&\Delta_fv=v,\quad v>\arrowvert\nabla v\arrowvert^2.\label{inequ:1}\\
\end{eqnarray}

\item Assume ${\mathop{\rm Ric}\nolimits}(g)\geq 0$ and assume $(M^n,g,\nabla^g f)$ is normalized. Then $M^n$ is diffeomorphic to $\mathbb{R}^n$ and
\begin{eqnarray}
&&v\geq \frac{n}{2}>0.\label{inequ:2}\\
&&\frac{1}{4}r_p(x)^2+\min_{M}v\leq v(x)\leq\left(\frac{1}{2}r_p(x)+\sqrt{\min_{M}v}\right)^2,\quad \forall x\in M,\label{inequ:3}\\
&&{\mathop{\rm AVR}\nolimits}(g):=\lim_{r\rightarrow+\infty}\frac{{\mathop{\rm Vol}\nolimits} B(q,r)}{r^n}>0,\quad\forall q\in M,\label{inequ:avr}\\
&&-C(n,V_0,R_0)\leq\min_{M}f\leq 0\quad;\quad\mu(g)\geq\max_{M}{\mathop{\rm R}\nolimits}_g\geq 0,\label{inequ:ent}
\end{eqnarray}
where $V_0$ is a positive number such that ${\mathop{\rm AVR}\nolimits}(g)\geq V_0$, $R_0$ is such that $\sup_{M}{\mathop{\rm R}\nolimits}_g\leq R_0$ and $p\in M$ is the unique critical point of $v$.\\

\item Assume ${\mathop{\rm Ric}\nolimits}(g)=\textit{O}(r_p^{-2})$ where $r_p$ denotes the distance function to a fixed point $p\in M$. Then the potential function is equivalent to $r_p^2/4$ (up to order $2$).
\end{itemize}
\end{prop}

For a proof, see \cite{Der-Asy-Com-Egs} and the references therein.

\begin{lemma}[Distance distortions]\label{C^0-distance-est-Type-III}
Let $(M^n,g_0(t))_{t\geq 0}$ be a Type III solution of the Ricci flow with nonnegative Ricci curvature, i.e.
\begin{eqnarray*}
\arrowvert{\mathop{\rm Rm}\nolimits}(g_0(t))\arrowvert_{g_0(t)}\leq \frac{R_0}{1+t},\quad{\mathop{\rm Ric}\nolimits}(g_0(t))\geq 0,\quad t\geq 0.
\end{eqnarray*}
Then, 
\begin{eqnarray*}
\left(\frac{1+s}{1+t}\right)^{c(R_0)}g_0(s)&\leq& g_0(t)\leq g_0(s),\\
d_{g_0(s)}(x,y)-c(R_0)\left(\sqrt{t}-\sqrt{s}\right)\leq d_{g_0(t)}(x,y)&\leq& d_{g_0(s)}(x,y),\quad s\leq t,\quad x,y\in M,
\end{eqnarray*}
for a positive constant $c(R_0)$, and any $0\leq s\leq t$.

In particular, 
\begin{eqnarray*}
-\frac{c(R_0)}{\sqrt{t}}\leq\partial_td_{g_0(t)}(x,y)\leq 0, \quad t>0,\quad x,y \in M,
\end{eqnarray*}
for a positive constant $c(R_0)$.

\end{lemma}
\begin{proof}
The upper bound comes from the fact that the Ricci curvature is nonnegative, hence, $g_0(t)\leq g_0(s)$ for any $s\leq t$ in the sense of symmetric $2$-tensors. 

The lower bound has been proved by Hamilton and a proof can be found for instance in [lemma $8.33$, \cite{Ben}].
\end{proof}




