\title{Reduction in Tension and Stiffening of Lipid Membranes in an Electric Field Revealed by X-ray Scattering}

\author{Arnaud Hemmerle
}
\affiliation{UPR 22/CNRS, Institut Charles Sadron, Universit\'e de Strasbourg, 23 rue du Loess, BP 84047 67034 Strasbourg Cedex 2, France}

\author{Giovanna Fragneto}
\affiliation{Institut Laue-Langevin, 71 av. des Martyrs, BP 156, 38042 Grenoble Cedex, France}

\author{Jean Daillant}
\affiliation{Synchrotron SOLEIL, L'Orme des Merisiers, Saint-Aubin, BP 48, F-91192 Gif-sur-Yvette Cedex, France}

\author{Thierry Charitat\footnote{thierry.charitat@ics-cnrs.unistra.fr}}
