A(^1S_0) &=& -4\sqrt{2}{\kappa}^2\frac{R(m_\chi-A_{\kappa})}{4-R(m_a)^2}\left(1+\frac{v^2}{8}\right)
{\nonumber}\\
&& +8\sqrt{2}{\kappa}^2\frac{R(m_\chi)}{P_\chi}\left[1+v^2\left(\frac{1}{8}
-\frac{1}{2P_\chi}+\frac{\bar\beta_f^2}{3P_\chi^2}\right)\right]{\nonumber}\\
&& +2\sqrt{2}{\kappa}^2 \left(R(m_a)^2-R(m_h)^2\right)\left[1+v^2\left(-\frac{1}{8}
-\frac{1}{2P_\chi}+\frac{\bar\beta_f^2}{3P_\chi^2}\right)\right], \\
A(^3P_1) &=& 8v{\kappa}^2 \bar\beta_f^2 /P_\chi^2~.
\end{eqnarray}
\item   $\chi\chi\to NN$:
\begin{eqnarray}
A(^1S_0) &=& \lambda_N^2 \left[1+v^2\left(-\frac{1}{2P_R}
+\frac{\bar\beta_f^2}{3P_R^2}\right)\right]R(m_N)/P_R{\nonumber}\\
&+& \lambda_N^2 \frac{1}{P_R}\left[1+v^2\left(\frac{1}{4}-\frac{1}{2P_R}
-\frac{\bar\beta_f^2}{6P_R}+\frac{\bar\beta_f^2}{3P_R^2}\right)\right]{\nonumber}\\
&-& \lambda_N^2 \left[1+v^2\left(-\frac{1}{2P_M}
