&+& \lambda_N^2 \frac{1}{P_M}\left[1+v^2\left(\frac{1}{4}-\frac{1}{2P_M}
-\frac{\bar\beta_f^2}{6P_M}+\frac{\bar\beta_f^2}{3P_M^2}\right)\right]{\nonumber}\\
&-& 8\kappa\lambda_N\frac{1}{4-R(m_a)^2+iG_a}\left(1+\frac{v^2}{4}\right).
\end{eqnarray}
\begin{eqnarray}
A(^3P_{00}) &=& {\lambda}_N^2\bar\beta_f\left[-\frac{1}{2}\left(\frac{1}{2P_R}
-\frac{2}{3P_R^2}\right) + \frac{R(m_N)}{P_R^2}-\frac{1}{2}\left(\frac{1}{2P_M}
-\frac{2}{3P_M^2}\right) - \frac{R(m_N)}{P_M^2}\right] {\nonumber}\\
&-& 2\kappa\lambda_N\bar\beta_f\frac{1}{4-R(m_h)^2+iG_h}
+ 2\kappa\lambda_N\bar\beta_f\frac{1}{4-R(m_a)^2+iG_a}
\end{eqnarray}
\begin{eqnarray}
A(^3P_{10}) &=& 0.
\end{eqnarray}
\begin{eqnarray}
A(^3P_{11})({\lambda}') &=& {\lambda}_N^2 {\lambda}'\bar\beta_f\left(-\frac{1}{P_R}+\frac{1}{P_R^2}+\frac{R(m_N)}{P_R^2}\right)
- {\lambda}_N^2 {\lambda}'\bar\beta_f\left(-\frac{1}{P_M}+\frac{1}{P_M^2}-\frac{R(m_N)}{P_M^2}\right).{\nonumber}\\
\end{eqnarray}
\begin{eqnarray}
A(^3P_{20}) &=& {\lambda}_N^2\bar\beta_f \left(\frac{R(m_N)+1}{P_R^2}-\frac{R(m_N)-1}{P_M^2}\right).
\end{eqnarray}
\begin{eqnarray}
A(^3P_{21}) &=& {\lambda}_N^2\bar\beta_f\left(-\frac{1+R(m_N)}{P_R^2}+\frac{1-R(m_N)}{P_M^2}\right).
\end{eqnarray}
