\title{Energy shift and conduction-to-valence band transition mediated by a time dependent potential barrier in graphene}

\author{Andrey Chaves}\email{andrey@fisica.ufc.br}
\author{D. R. da Costa}\email{diego_rabelo@fisica.ufc.br}
\author{G. O. de Sousa} \email{gabrieloliveira@fisica.ufc.br}
\author{J. M. Pereira Jr.}
\author{G. A. Farias} 
