\title{Dissipative Preparation of Antiferromagnetic Order in the Fermi-Hubbard Model}

\author{J. Kaczmarczyk}
\email{jan.kaczmarczyk@ist.ac.at}
\affiliation{Institute of Science and Technology Austria, Am Campus 1, 3400 Klosterneuburg}

\author{H. Weimer}
\email{hweimer@itp.uni-hannover.de}
