\begin{equation}
 f = \epsilon_\alpha^{\prime\ast} \epsilon_\beta 
    \left[ f^{dd}_{\alpha\beta} 
    + \frac{i}{2} \left( k_\gamma f^{dq}_{\alpha\beta\gamma} - k^\prime_\gamma f^{dq\ast}_{\beta\alpha\gamma} \right)
    + \frac{1}{4} k_\gamma^\prime k_\delta f^{qq}_{\alpha\gamma\beta\delta}
    \right]
 \label{f}
\end{equation}

\begin{equation}
F_{\alpha\beta}^{TMI}= i H_\delta \frac{\partial f_{\alpha\beta}}{\partial u_\gamma} \langle u_\gamma u_\delta \rangle 
 \equiv i H_\gamma \frac{\partial f_{\alpha\beta}}{\partial u_\gamma} \langle u_\gamma^2\rangle
\label{tmi}
\end{equation}

\begin{equation}
  \bm f^{PC} = \sum_{\mathcal C} p(\mathcal C) \bm f^{\mathcal C}
  \label{pc}
\end{equation}

\begin{equation}
 \bm f = \bm f^{dq} + \bm f^{TMI} + p(\mathcal P)\bm f^{\mathcal P} + p(\mathcal S)\bm f^{\mathcal S} + p(\mathcal T)\bm f^{\mathcal T}
 \label{f3}
\end{equation}

\begin{equation}
  F \equiv F_{\epsilon\epsilon^\prime} = 
  \bm{\epsilon^\prime} .
  \left( \begin{array}{ccc}
  f_{xxz}H_z & 0 & f_s H_x+f_a L_x\\
  0 & -f_{xxz}H_z & f_s H_y+f_a L_y \\
  f_s H_x-f_a L_x & f_s H_y-f_a L_y & 0
 \end{array} \right)
 . \bm \epsilon
\label{sf}
\end{equation}
