	\title{Distribution and mass of diffuse and dense CO gas in the Milky Way}









\author{Julia Roman-Duval\altaffilmark{1}, Mark Heyer\altaffilmark{2}, Chris Brunt\altaffilmark{3},  Paul Clark\altaffilmark{4}, Ralf Klessen\altaffilmark{5}, Rahul Shetty\altaffilmark{5}}
