\title{Tunneling Planar Hall Effect in Topological Insulators: Spin-Valves and Amplifiers}

\author{Benedikt Scharf}
\affiliation{Department of Physics, University at Buffalo, State University of New York, Buffalo, NY 14260, USA}
\author{Alex Matos-Abiague}
\affiliation{Department of Physics, University at Buffalo, State University of New York, Buffalo, NY 14260, USA}
\author{Jong E. Han}
\affiliation{Department of Physics, University at Buffalo, State University of New York, Buffalo, NY 14260, USA}
\author{Ewelina M. Hankiewicz}
\affiliation{Institute for Theoretical Physics and Astrophysics, University of W\"{u}rzburg, Am Hubland, 97074 W\"{u}rzburg, Germany}
\author{Igor \v{Z}uti\'c}
