& \leq & \frac{1}{2} \, \delta_{T} \, |\vec{b} |^{2}_{0,\infty} \, \mu^{2}_{2} \, h_{T}^{-2} \, \|v_{h} \|^{2}_{0,T} \nonumber \\
& \leq & \frac{c_{0}}{8} \, \text{min} \{ s^{\ast},\gamma_{\ast} \} \| v_{h}\| ^{2}_{0,T}
\label{dis_coer11}
\end{eqnarray}
 First term of (\ref{dis_coer8}) can be estimated as
\begin{eqnarray}
\bigg| \int_{T} c v_{h} \, \delta_{T} \, \vec{b} \cdot (\Pi_{k-1}(\nabla v_{h})-(\nabla v_{h})) \bigg|  & \leq &  \delta_{T} \, \|c \, v_{h} \|_{0,T} \| \vec{b} \cdot (\Pi_{k-1}(\nabla v_{h})-\nabla v_{h}) \|_{0,T}  \nonumber\\
&\leq & \delta_{T} \big[\frac{1}{2} \|c \, v_{h} \|^{2}_{0,T} \nonumber \\
& + & \frac{1}{2} \| \vec{b} \cdot (\Pi_{k-1}(\nabla v_{h})-\nabla v_{h}) \|_{0,T}^{2} \big]
\label{dis_coer12}
\end{eqnarray}

\begin{eqnarray} 
\delta_{T} \, \frac{1}{2} \, \|c \, v_{h} \|^{2}_{0,T} &\leq & \frac{c_{0}}{8} \, \text{min}\{s^{\ast},\gamma_{\ast}\} \|v_{h}\|^{2}_{0.T}
\label{dis_coer13}
\end{eqnarray}
 Using polynomial approximation property (\ref{aprox1}) and local inverse inequality (\ref{dis_coer6}) of VE space we can write
\begin{eqnarray} 
\delta_{T} \, \frac{1}{2} \| \vec{b} \cdot (\Pi_{k-1}(\nabla v_{h})-\nabla v_{h}) \|_{0,T}^{2} &\leq & \frac{1}{2} \, \delta_{T} \, \|\vec{b}\|^{2}_{0,\infty} \, c_{I} \, \| \nabla v_{h} \|^{2}_{0,T}  \nonumber \\
&\leq & \frac{1}{2} \, \delta_{T} \, \| \vec{b}\|^{2}_{0,\infty}  \, c_{I} \, \mu^{2}_{2} \, h^{-2}_{T} \|v_{h}\|^{2}_{0,T} \nonumber \\
& \leq &  \frac{1}{8} \, c_{0} \, \text{min} \{ s^{\ast},\gamma_{\ast} \} \|v_{h}\|^{2}_{0,T}
\label{dis_coer14} 
\end{eqnarray}

Finally we estimate local bilinear form $ A^{T}_{h}(v_{h},v_{h})$
\begin{eqnarray}
A^{T}_{h}(v_{h},v_{h}) & \geq & \frac{3}{4} \, \alpha_{\ast}  \, \epsilon \, |v_{h}|^{2}_{1,T} \nonumber \\
& + &  \frac{3}{8} \, \text{min} \{s^{\ast},\gamma_{\ast} \}  \, c_{0} \, \|v_{h} \|^{2}_{0,T} \nonumber \\
& + & \Gamma_{\ast} \, \delta_{T} \, \|\vec{b} \cdot \nabla v_{h} \|^{2}_{0,T} 
\label{dis_coer15}
\end{eqnarray}

Using local estimation(\ref{dis_coer15}) we can write
\begin{eqnarray}
A_{h}(v_{h},v_{h}) & \geq & \sum_{T} A^{T}_{h}(v_{h},v_{h}) \nonumber \\
                   & \geq & \alpha \sum_{T} \left\{ \epsilon \, |v_{h}|^{2}_{1,T} +c_{0} \, \|v_{h} \|^{2}_{0,T}+\delta_{T} \, \|\vec{b} \cdot \nabla v_{h} \|^{2}_{0,T} \right \} \nonumber \\
                   & \geq & \alpha \, |||v_{h}|||^{2}
                   \label{dis_coer16}
\end{eqnarray}
Positive constant $\alpha$ is defined by $\alpha$:= min $\{ \alpha_{1},\alpha_{2} ,\alpha_{3}\}$ where 
$\alpha_{1} =\frac{3}{4} \, \alpha_{\ast}$, $\alpha_{2}=\frac{3}{8} \, \text{min} \{s^{\ast},\gamma_{\ast} \}  $ and $\alpha_{3}=\Gamma_{\ast}.$
\end{proof}

