 &+& C h^{\text{min}(k-1,s-1)} (\sum_{T} (\frac{h^{2}_{T}}{\epsilon}) |f|^{2}_{s-1,T})^{1/2}
 \end{eqnarray}
\end{proof} 

 \subsection{computation issue \label{sec15}}
 The $L^{2}-$ orthogonal projection operator $\Pi^{0}_{k}$ can be computed using elliptic projection operator  $ \Pi^{\nabla}_{k}$


 If degree $(p)< k-2 $  then $ \int_{T} p v_{h} dT$ is computable.
 If degree $(p)=k-1,k $ then  we consider $\int_{T} p v_{h} dT =\int_{T} p \Pi^{\nabla}_{k}(v_{h}) $

\textbf{diffusion part:} 
\begin{eqnarray}
\int_{T} \epsilon \Pi_{k-1}(\nabla p) \Pi_{k-1}(\nabla v_{h})&=& \int_{T} \epsilon \nabla p  \Pi_{k-1}(\nabla v_{h}) \nonumber \\
&=&\int_{T} \epsilon \nabla p \nabla v_{h} \nonumber \\
&=& \int_{T} -\epsilon \Delta p v_{h} +\int_{\partial T} \epsilon (\nabla p.\mathbf{n})v_{h}
\label{comp1}
\end{eqnarray}

$\int_{T} \Delta p v_{h}$ is computable using degrees of freedom of $v_{h}$ on triangle since degree$(\Delta p) <k-2$.

$\int_{\partial T} (\nabla p.\mathbf{n})v_{h} $ is computable using degrees of freedom of $v_{h}$ on boundary of triangle since degree$(\nabla p)<k-1$.

\begin{eqnarray}
\int_{T} \vec{b}.\Pi_{k-1}(\nabla p) \Pi_{k}(v_{h}) &=& \int_{T} \vec{b}.\nabla p \Pi_{k}(v_{h}) \nonumber \\
&=& \int_{T} (\vec{b}.\nabla p) v_{h}
\end{eqnarray}

Right hand side  $\int_{T} (\vec{b}.\nabla p) v_{h}$ is computable using elliptic projection operator $\Pi^{\nabla}_{k}$

\begin{eqnarray}
\int_{T} \vec{b}.\Pi_{k-1}(\nabla v_{h}) \Pi_{k}(p) &=& \int_{T} \vec{b}.\Pi_{k-1}(\nabla v_{h}) p \nonumber \\
&=& \int_{T} (\vec{b}. \nabla v_{h}) p +\int_{T} (\vec{b}.\Pi_{k-1}(\nabla v_{h})-\vec{b}.\nabla v_{h})p \nonumber \\
&\approx & \int_{T} (\vec{b}. \nabla v_{h}) p \nonumber \\
&=& -\int_{T} \nabla.(\vec{b}p) v_{h}+\int_{\partial T} (\vec{b}.\mathbf{n}) pv_{h}
\label{comp2}
\end{eqnarray}

