  \frac{\pi^2}{8 m_\Phi s_{pp}}
  \frac
  {\Gamma (\Phi\rightarrow gg) \Gamma (\Phi\rightarrow \gamma\gamma)}
  {\Gamma_\Phi}
  \int dx_1 dx_2 \delta (x_1 x_2 - m_\Phi^2/s_{pp}) g(x_1) g(x_2),
  \label{sigma(LHC)}
\end{align}

\begin{align}
\frac{1}{L_{ee}} \frac{d^2L_{\gamma\gamma}}{dy dy'} = 
  f(x, y) B(x, y) f(x, y') B(x, y'),
\end{align}

\begin{align}
  \sigma
  (\gamma\gamma\rightarrow \Phi \rightarrow F) 
  &= 
  \frac{1}{L_{ee}} 
  \int_0^{y_m} dy dy'
  \frac{d^2L_{\gamma\gamma}}{dy dy'}
  \hat{\sigma}
  (\gamma\gamma\rightarrow \Phi \rightarrow F; 
  s_{\gamma\gamma} = y y' s_{ee}).
  \label{sigma(photonphoton)}
\end{align}

\begin{align}
  \sigma (\gamma \gamma \rightarrow \Phi\rightarrow F)
  \simeq \sigma_{gg}
  \times
  \frac{{\rm Br}(\Phi\rightarrow F)}{{\rm Br}(\Phi\rightarrow gg)}
  \times
  \left[
    \frac{\sigma (pp\rightarrow \Phi\rightarrow\gamma\gamma)}
    {10\ {\rm fb}}
  \right],
  \label{sigma_fitting}
\end{align}

