e^{-B\Big(  (k_1^5+k_2^5+k_3^5)  (0^5-\tau_V^5 )\Big) }  \sin\Big((k_1+k_2+k_3 )\tau_V  \Big)      \Big]
 \nonumber \\
\mathcal{F}(k_2/k_1,k_3/k_1,\tau_0)_{\text{Non-BD}}^{\text{Loop}}&=& \frac{-3\lambda}{2H^2 \epsilon}  k_1 k_2 k_3    \Big[  \int_{\tau_0}^0 d\tau_V\; \tau_V ^2  c_{\bm k_1}
 e^{-B\Big(2k_1^5(\tau_V^5-\tau_0^5)+ (k_1^5+k_2^5+k_3^5)  (0^5-\tau_V^5 )\Big) }
  \nonumber\\&&\times
  \sin\Big((k_2+k_3-k_1)\tau_V-\theta_{\bm k_1} \Big) +2\text{ perm.}     \Big]
 \end{eqnarray}


