          \partial_t \underbrace{R(r,z,t)}_{\text{ROS}}=\underbrace{\frac{1}{r} \partial_{r}(r D_R  R_r) + \partial_{zz}D_RR}_{\text{diffusion}}-\underbrace{\delta_R R}_{\text{natural decay}}+\underbrace{\sigma_R S_T}_{\text{production by $S_T$}},
\end{align*}

\begin{align*}\label{DAMPs_eq}
        \tag{1b}
          \partial_t \underbrace{M(r,z,t)}_{\text{DAMPs}}=\underbrace{\frac{1}{r} \partial_{r}(r D_M  M_r)+ \partial_{zz}D_MM}_{\text{diffusion}}-\underbrace{\delta_M M}_{\text{natural decay}} +\underbrace{\sigma_M D_N}_{\text{production by $D_N$}} + \underbrace{\sigma_U U \frac{F}{\lambda_F +F}}_{\text{production by ECM}},
\end{align*}

\begin{align*}\label{PIC_eq}
      \tag{1c}
        \partial_t \underbrace{F(r,z,t)}_{\text{PIC}}=\underbrace{\frac{1}{r} \partial_{r}(r D_F  F_r)+ \partial_{zz}D_FF}_{\text{diffusion}}-\underbrace{\delta_F F}_{\text{natural decay}}+\underbrace{\sigma_F S_T}_{\text{production by $S_T$}},
\end{align*}

\begin{align*}\label{EPO_eq}
      \tag{1d}
      \partial_t \underbrace{P(r,z,t)}_{\text{EPO}}=\underbrace{\frac{1}{r} \partial_{r}(r D_P  P_r)+ \partial_{zz}D_PP}_{\text{diffusion}}-\underbrace{\delta_P P}_{\text{natural decay}} +\underbrace{\sigma_P C_E \frac{R}{\lambda_R+R} \frac{\Lambda}{\Lambda +F}}_{\text{production by $C_E$ controlled by PIC}},
\end{align*}

\begin{align*}\label{ECM_eq}
      \tag{2a}
        \partial_t \underbrace{U(r,z,t)}_{ECM}=\underbrace{-\delta_U U \frac{F}{\lambda_F +F} H(P_c - P)}_{\text{degradation by PIC under the control of EPO}},
\end{align*}

\begin{align*}\label{CU_eq}
        \tag{3a}
