\begin{equation}
\int^{S_{\rm 150, rand}}_{S_{\rm150,  min}} dS \, \frac{dN}{dS} =  R  \int^{S_{\rm 150, max}}_{S_{\rm 150, min}} dS \, \frac{dN}{dS}, 
\end{equation}

\begin{equation}
S_{\rm 150, rand} = \left[ \left( 1-R \right) S_{\rm 150, min}^{\alpha+1} + R \, S_{\rm 150, max}^{\alpha+1}\right]^{\frac{1}{\alpha+1}};
\label{eq:srand}
\end{equation}

