D \Psi_1 - \bar{\delta}\Psi_0 & =& -3\kappa \Psi_2  + (2\epsilon + 4\rho)\Psi_1 - (-\pi + 4 \alpha )\Psi_0 \label{b1} \\
D \Psi_2 - \bar{\delta}\Psi_1 & =& -2\kappa \Psi_3 + 3 \rho \Psi_2 - (-2\pi + 2 \alpha )\Psi_1 - \lambda \Psi_0 \label{b2} \\
D \Psi_3 - \bar{\delta}\Psi_2 & =& -\kappa \Psi_4 - (2\epsilon - 2 \rho )\Psi_3 +3\pi \Psi_2 -2 \lambda \Psi_1 \label{b3} \\
D \Psi_4 - \bar{\delta}\Psi_3 & =& -(4\epsilon -  \rho )\Psi_4 + (4\pi + 2 \alpha )\Psi_3 -  3\lambda \Psi_2 \label{b4} \\
\Delta \Psi_0 - \delta \Psi_1 & =& (4\gamma -  \mu )\Psi_0 - (4\tau + 2 \beta )\Psi_1 +  3\sigma \Psi_2 \label{b5} \\
\Delta \Psi_1 - \delta \Psi_2 & =& \nu \Psi_0 + (2\gamma -  2\mu )\Psi_1  -  3\tau \Psi_2 +2\sigma \Psi_3 \label{b6} \\
\Delta \Psi_2 - \delta \Psi_3 & =& 2\nu \Psi_1 -3\mu \Psi_2 + (-2\tau +  2\beta )\Psi_3  +\sigma \Psi_4 \label{b7} \\
