\keywords{fractional elliptic operator, critical exponent, existence,
nonexistence}

\begin{abstract}
Let $\mathcal{L} = \mathrm{div}(A(x) \nabla )$ be a uniformly elliptic
operator in divergence form in a bounded open subset $\Omega$ of $\mathbb{R}
^n$. We study the effect of the operator $\mathcal{L}$ on the existence and
nonexistence of positive solutions of the nonlocal Brezis-Nirenberg problem

text:
\begin{equation}  \label{H1}
A(x) \leq A(x_0) + C_0 |x - x_0|^\sigma I_n \ \ \mathrm{locally\ around} \
x_0
\end{equation}

\begin{equation*}
H^s = \left\{
\begin{array}{lcll}
H^s(\Omega), &  & \mathrm{if} \ 0 < s < 1/2, &  \\
H_{00}^{1/2}(\Omega), &  & \mathrm{if} \ s = 1/2, &  \\
H_0^s(\Omega), &  & \mathrm{if} \ 1/2 < s < 1 &
\end{array}
\right.
\end{equation*}

\begin{equation}
\left\{
\begin{array}{rrrll}
\mathrm{div}\left( y^{1-2s}B\left( x\right) \nabla w\left( x,y\right) \right)
& = & 0 & \text{in} & \mathcal{C}_{\Omega }, \\
w & = & 0 & \text{on} & \partial _{L}\mathcal{C}_{\Omega }, \\
w & = & u & \text{on} & \Omega \times \left\{ y=0\right\} .
\end{array}
\right.  \label{extension w}
\end{equation}

\begin{equation}
\left\{
\begin{array}{rrlll}
-\mathrm{div}\left( y^{1-2s}B\left( x\right) \nabla w\right) & = & 0 & \text{
in} & \mathcal{C}_{\Omega ,} \\
w & = & 0 & \text{on} & \partial \mathcal{C}_{\Omega }, \\
\partial^s_\nu w & = & w^{\frac{n+2s}{n-2s}}+\lambda w & \text{in} & \Omega
\times \left\{ y=0\right\} .
\end{array}
\right.  \label{fractionalbnw}
\end{equation}

\begin{equation}
J^{A}_\lambda \left( w\right) = c_{s} \iint_{\mathcal{C}_{\Omega
}}y^{1-2s}\left( \nabla w\right) ^{T}B\left( x\right) \nabla w\; dxdy-
\lambda \int_{\Omega }w^{2}\left( x,0\right) dx  \label{wenergy}
\end{equation}

\begin{equation}
\left( \int_{\Omega }\left\vert f\left( x,0\right) \right\vert ^{r}
dx\right) ^{\frac{2}{r}} \leq A \int_{\mathcal{C}_{\Omega
}}y^{1-2s}\left\vert \nabla f\left( x,y\right) \right\vert ^{2}dxdy
\label{soboinequality}
\end{equation}

\begin{equation*}
\leq \det \left( A\left( x_{0}\right) \right) ^{-\frac{s}{n}} K_{s}\left(
n\right) \int_{\mathcal{C}_{\Omega }}y^{1-2s}\left( \nabla f\left(
x,y\right) \right) ^{T}B\left( x_{0}\right) \nabla f\left( x,y\right) dxdy
\end{equation*}

nexttext:
Then, the infimum $S_{\lambda }^{A}$ of $I^A_\lambda$ in $E$ is achieved by
some nonnegative function $u$. Furthermore, if $\lambda <
\lambda_{1,s}\left(-\mathcal{L} \right)$, then $u$ is a nonnegative weak
solution of \eqref{fractionalbn}, module a suitable scaling.
\end{proposition}

\begin{proof}
As  noted above, it suffices to prove that the infimum of $J_{\lambda }^{A}$
in $F$, given also by $S_{\lambda }^{A}$, is assumed by some nonnegative
function $w$.

Let $\left\{ w_{m}\right\} \subset H_{0,L}^{s,A}\left( \mathcal{C}_{\Omega
}\right) $ be a minimizing sequence of $J_{\lambda }^{A}$ on $F$. Clearly, $
\left\{ w_{m}\right\} $ is bounded in $H_{0,L}^{s,A}\left( \mathcal{C}
_{\Omega }\right) $. Siince $\Omega $ is bounded, up to a subsequence, we
have

\begin{eqnarray*}
&&w_{m}\rightharpoonup w\text{ weakly in }H_{0,L}^{s,A}\left( \mathcal{C}
_{\Omega }\right) , \\
&&w_{m}\left( \cdot ,0\right) \rightarrow w\left( \cdot .0\right) \text{
strongly in }L^{q}\left( \Omega \right) \text{ for }1\leq q<\frac{2n}{n-2s},
\\
&&w_{m}\left( \cdot ,0\right) \rightarrow w\left( \cdot ,0\right) \text{
a.e. in }\Omega .
\end{eqnarray*}
A direct calculation, taking into account of the weak convergence, gives
\begin{eqnarray*}
\left\Vert w_{m}\right\Vert _{H_{0,L}^{s,A}}^{2} &=&\left\Vert
w_{m}-w\right\Vert _{H_{0,L}^{s,A}}^{2}+\left\Vert w\right\Vert
_{H_{0,L}^{s,A}}^{2} \\
&&+c_{s}\iint_{\mathcal{C}_{\Omega }}y^{1-2s}\left( \nabla w^{T}B\left(
x\right) \nabla \left( w_{m}-w\right) +\nabla ^{T}\left( w_{m}-w\right)
B\left( x\right) \nabla w\right) \;dxdy \\
&=&\left\Vert w_{m}-w\right\Vert _{H_{0,L}^{s,A}}^{2}+\left\Vert
w\right\Vert _{H_{0,L}^{s,A}}^{2}+o\left( 1\right) .
\end{eqnarray*}
Now using Brezis-Lieb Lemma (see \cite{BrLi}), (\ref{H2}) and (\ref
{soboinequality}), we obtain
\begin{eqnarray*}
S_{\lambda }^{A} &=&\left\Vert w_{m}\right\Vert _{H_{0,L}^{s,A}}^{2}-\lambda
\left\Vert w_{m}\left( \cdot ,0\right) \right\Vert _{L^{2}}^{2}+o(1) \\
&=&\left\Vert w_{m}-w\right\Vert _{H_{0,L}^{s,A}}^{2}+\left\Vert
w\right\Vert _{H_{0,L}^{s,A}}^{2}-\lambda \left\Vert w\left( \cdot ,0\right)
\right\Vert _{L^{2}}^{2}+o\left( 1\right)  \\
&\geq &c_{s}\iint_{\mathcal{C}_{\Omega }}y^{1-2s}\left( \nabla
(w_{m}-w)\right) ^{T}B\left( x_{0}\right) \nabla (w_{m}-w)\;dxdy+J_{\lambda
}^{A}(w)+o(1) \\
&\geq &c_{s}\det \left( A\left( x_{0}\right) \right) ^{\frac{s}{n}
}K_{s}\left( n\right) ^{-1}\left\Vert \left( w_{m}-w\right) \left( \cdot
,0\right) \right\Vert _{L^{\frac{2n}{n-2s}}}^{2}+J_{\lambda }^{A}(w)+o\left(
1\right)  \\
&\geq &c_{s}\det \left( A\left( x_{0}\right) \right) ^{\frac{s}{n}
}K_{s}\left( n\right) ^{-1}\left( 1-\int_{\Omega }|w(x,0)|^{\frac{2n}{n-2s}
}dx\right) ^{\frac{n-2s}{n}} \\
&&+S_{\lambda }^{A}\left( \int_{\Omega }|w(x,0)|^{\frac{2n}{n-2s}}dx\right)
^{\frac{n-2s}{n}}+o(1)\,
\end{eqnarray*}
so that

text:
\begin{equation}
S_{\lambda }^{A} < c_{s}\det \left( A\left( x_{0}\right) \right)^{\frac{s}{n}
} K_s(n)^{-1}.  \label{S upperbound}
\end{equation}

