\begin{equation}\label{HamiltonianInt}
\hat H_{\mathrm{R}}=\hbar \Delta_0 \hat{b}_2^{\dagger}\hat b_2+\hbar\sum_{i=1}^{2}\frac{\lambda_i}{2}\Big[2\hat b_i^{\dagger}\hat b_i+\hat b_i^{\dagger}\hat b_i^{\dagger}\hat b_i \hat b_i\Big]-\hbar G(\hat b_1^{\dagger}\hat b_2+\hat b_2^{\dagger}\hat b_1),
\end{equation}

