\title{Boundary control of a singular reaction-diffusion equation on a disk} 



\author[First]{Rafael Vazquez} 
\author[Second]{Miroslav Krstic} 


\address[First]{Department of Aerospace Engineering, Universidad de Sevilla, Camino de los Descubrimiento s.n., 41092 Sevilla, Spain (e-mail: rvazquez1@us.es).}                                              
