\begin{equation}
\frac{dq_\alpha}{d\lambda} = \omega_\alpha({\bf J}) + \varepsilon
g_\alpha(q_\theta, q_r, {\bf J})\;,\qquad
\frac{dJ_i}{d\lambda} =
\varepsilon G_i(q_\theta, q_r, {\bf J})\;.
\label{eq:actionangleevolve1}
