\begin{equation}
G_i(q_\theta, q_r, {\bf J}) = \langle G_i({\bf J})\rangle + \delta
G_i(q_\theta, q_r, {\bf J})\;,
\end{equation}

\begin{equation}
G_i(q_\theta, q_r, {\bf J}) = \sum_{kn} G_{i;kn}({\bf J})
e^{i(kq_\theta + nq_r)}\;.
\label{eq:Fourierforcing}
\end{equation}

\begin{equation}
\langle G_i({\bf J})\rangle_{\rm res} = G_{i;00}({\bf J})
+ \sum_{(k,n)} G_{i;kn}\;.
\end{equation}
