\\ {1\over4} & -{1\over4} & {1\over8} & {1\over4} & {1\over4} & 0 & 0 & 0
\\ {1\over2} & -{1\over2} & {1\over4} & -{1\over2} & -{1\over2} & {1\over2} & {1\over2} & -{1\over2}
