\section{}\label{sec_appendixA}
In this Appendix, we provide a proof of \emph{Theorem 1}. Recall that $\Gamma_{R}$ and $\Gamma_{D}$ are two random variables which are dependent on $Z_{2}$. In order to express \eqref{eq_Pout} in terms of the product of two independent random variables, we condition the term $\Pr\{\Gamma_{R} \geq \gamma_{th}, \Gamma_{D}\geq\gamma_{th}\}$ on $Z_{2}$. Thus,
\begin{eqnarray}\label{P_gamma_RD_appendixA}
\Pr \{ \left. {{\Gamma _R} \ge {\gamma _{th}},{\Gamma _D} \ge {\gamma _{th}}} \right|{Z_2}\} &=& \Pr\{\Gamma_{R} \geq \gamma_{th}|Z_{2}\}\times \nonumber\\
&&\Pr\{\Gamma_{D} \geq \gamma_{th}|Z_{2}\},
\end{eqnarray}


\noindent where $\Pr\{\Gamma_{R} \geq \gamma_{th}|Z_{2}=z_{2}\}$ and $\Pr\{\Gamma_{D} \geq \gamma_{th}|Z_{2}=z_{2}\}$ are two independent probabilities for a given $z_2$. We derive and simplify the expressions for $\Pr\{\Gamma_{R}\geq \gamma_{th}|Z_{2}=z_{2}\}$ and $\Pr\{\Gamma_{D}\geq \gamma_{th}|Z_{2}=z_{2}\}$. The term $\Pr\{\Gamma_{R}\geq \gamma_{th}|Z_{2}=z_{2}\}$ can be obtained as
\begin{eqnarray}
&&\Pr\{\Gamma_{R}\geq\gamma_{th}|Z_{2}\}\nonumber\\
\hspace{+.5cm}&=&\Pr\bigg\{\min\big( \rho Z_{1}, \frac{P_{\mathcal{I}}}{Y_{1}}\big)\frac{X_{1}}{Z_{2}}\geq\gamma_{th}\bigg\}\label{eq_CDF_gamma_R_s1_der_l1}\\
\hspace{+.5cm}&=&\underbrace{\Pr\bigg\{X_{1}\geq \frac{Z_{2}\gamma_{th}}{Z_{1}\rho}, Y_{1}\leq \frac{P_{\mathcal{I}}}{Z_{1}\rho}\bigg\}}_{\mathcal{J}_{R,I}} \nonumber\\
\hspace{+.5cm}&+& \underbrace{\Pr\bigg\{X_{1}\geq \frac{Y_{1}Z_{2}\gamma_{th} }{P_{\mathcal{I}}}, \frac{P_{\mathcal{I}}}{Y_{1}\rho}\leq Z_{1}\bigg\}}_{\mathcal{J}_{R,II}},
\label{eq_CDF_gamma_R_s1_der}
\end{eqnarray}
\noindent where \eqref{eq_CDF_gamma_R_s1_der_l1} presents the probability that SIR is greater than $\gamma_{th}$ based on the three important power constraints. We present \eqref{eq_CDF_gamma_R_s1_der_l1} in terms of the addition of $\mathcal{J}_{R,I}$ and $\mathcal{J}_{R,II}$ based on two possibilities: $\rho Z_{1}<\frac{P_{\mathcal{I}}}{Y_{1}}$ and $\rho Z_{1}>\frac{P_{\mathcal{I}}}{Y_{1}}$.
Conditioning $\mathcal{J}_{R,I}$ in (\ref{eq_CDF_gamma_R_s1_der}) on $Z_{1}$ and taking the expected value of the results over the distribution of $Z_{1}$, we have
\begin{eqnarray}
\mathcal{J}_{R,I}&=&\int_{0}^{\infty} e^{-\frac{Z_{2}\gamma_{th}}{z_{1}\rho \lambda_{1}}}(1-e^{-\frac{P_{\mathcal{I}}}{z_{1}\rho \omega_1}})^{M}f_{Z_{1}}(z_{1})dz_{1}\nonumber\\
&=& \int_{0}^{\infty} e^{-\frac{Z_{2}\gamma_{th}}{z_{1}\rho \lambda_{1}}}(1-e^{-\frac{P_{\mathcal{I}}}{z_{1}\rho \omega_1}})^{M}\nonumber\\
&& \;\;\;\;\;\;\times\frac{z_{1}^{N-1}e^{-\frac{z_{1}}{P_{PU_{tx}}\nu_{1}}}}{\Gamma(N)(P_{PU_{tx}}\nu_{1})^{N}}dz_{1}.
\label{eq_J_R_I}
\end{eqnarray}


Note that by conditioning $\mathcal{J}_{R,I}$ on $Z_{1}$, the two terms in $\mathcal{J}_{R,I}$ become independent with respect to one another and therefore, $\mathcal{J}_{R,I}$ can be presented as the product of the two terms.
Similarly, by conditioning $\mathcal{J}_{R,II}$ in (\ref{eq_CDF_gamma_R_s1_der}) on $Y_{1}$ and by taking the expected value of the result over the distribution of $Y_{1}$, we have
\begin{eqnarray}
\mathcal{J}_{R,II}&=& \int_{0}^{\infty}
e^{-\frac{y_{1}Z_{2}\gamma_{th} }{P_{\mathcal{I}}\lambda_{1}}} \big(1-F_{Z_{1}}(\frac{P_{\mathcal{I}}}{y_{1}\rho })\big) f_{Y_{1}}(y_{1})dy_{1}\nonumber\\
&=&  \int_{0}^{\infty}
e^{-\frac{y_{1}Z_{2}\gamma_{th}}{P_{\mathcal{I}}\lambda_{1}}} \big(1-\frac{\Gamma(N,\frac{P_{\mathcal{I}}}{P_{PU_{tx}}\nu_{1}y_{1}\rho })}{\Gamma(N)}\big)\nonumber\\ && \;\;\;\;\;\;\times\frac{M}{\omega_{1}} \sum_{k}^{M-1} {M-1 \choose k} (-1)^{k}e^{-(\frac{k+1}{\omega_{1}})y_{1}}dy_{1}.\nonumber\\
\label{eq_J_R_2}
\end{eqnarray}
In the same manner, the term $\Pr\{\Gamma_{D}\geq \gamma_{th}|Z_{2}\}$ in \eqref{eq_Pout_simplfied} can be obtained as
\begin{eqnarray}
&&\Pr\{\Gamma_{D}\geq\gamma_{th}|Z_{2}\}\nonumber\\
&=& \Pr\bigg\{\min\big( \rho Z_{2}, \frac{P_{\mathcal{I}}}{Y_{2}}\big)\frac{X_{2}}{Z_{3}}\geq\gamma_{th}\bigg\}\nonumber\\
&=&\underbrace{\Pr\bigg\{X_{2}\geq \frac{\gamma_{th} Z_{3}}{Z_{2}\rho}, Y_{2}\leq \frac{P_{\mathcal{I}}}{Z_{2}\rho}\bigg\}}_{\mathcal{J}_{D,I}} \nonumber\\
&+& \underbrace{\Pr\bigg\{X_{2}\geq \frac{\gamma_{th} Y_{2}Z_{3}}{P_{\mathcal{I}}}, Y_{2}\geq \frac{P_{\mathcal{I}}}{Z_{2}\rho}\bigg\}}_{\mathcal{J}_{D,II}}.
\label{eq_CDF_gamma_D_s1_der}
\end{eqnarray}
Since $\mathcal{J}_{D,I}$ is conditioned on $Z_{2}$, the joint probability can be written as the product of two marginal probabilities, i.e.,
\begin{eqnarray}
\mathcal{J}_{D,I}&=&\underbrace{\Pr\{X_{2}\geq \frac{\gamma_{th}Z_{3}}{\rho Z_{2}}\}}_{\mathcal{I}_{1}}\underbrace{\Pr\{Y_{2}\leq \frac{P_{\mathcal{I}}}{\rho Z_{2}}\}}_{\mathcal{I}_2}, \nonumber\\
&=& \frac{{{{\left( {1 - {e^{ - \frac{{{P_{\cal I}}}}{{{Z_2}\rho {\omega _2}}}}}} \right)}^M}}}{{{{\left( {1 + \frac{{{P_{P{U_{tx}}}}{\nu _3}{\gamma _{th}}}}{{\rho {Z_2}{\lambda _2}}}} \right)}^N}}},
\label{eq_J_D_I}
\end{eqnarray}
\noindent where $\mathcal{I}_{1}$ is obtained by applying \cite[Eq 3.326.2]{TablOfIntegrals}
\begin{eqnarray}
\mathcal{I}_{1}&=&\int_{0}^{\infty} e^{-\frac{\gamma_{th} z_{3}}{\rho Z_{2}\lambda_{2}}}f_{Z_{3}}(z_{3})dz_{3}\nonumber\\
&=& {\left( {1 + \frac{{{P_{P{U_{tx}}}}{\nu _3}{\gamma _{th}}}}{{\rho {Z_2}{\lambda _2}}}} \right)^{ - N}},
\label{eq_I_1}
\end{eqnarray}
and

nexttext:
Averaging $\mathcal{J}_{D,II}$ over the PDF of $Z_{3}$ we have,
\begin{eqnarray}
\mathcal{J}_{D,II}&=& \int_{0}^{\infty} \mathcal{J}_{D,II}|Z_{3}f_{Z_{3}}(z_{3})dz_{3}\nonumber\\
&=&\int_{\frac{P_{\mathcal{I}}}{\rho Z_{2}}}^{\infty} \frac{M}{\omega_{2}} \sum_{k}^{M-1} {M-1 \choose k}(-1)^{k}e^{-(\frac{k+1}{\omega_{2}})y_{2}} \nonumber\\  &&\;\;\times{\frac{1}{{{{\left( {1 + {y_2}\frac{{{\gamma _{th}}{P_{P{U_{tx}}}}{\nu _3}}}{{{P_{\cal I}}{\lambda _2}}}} \right)}^N}}}}dy_{2}.
\label{eq_J_D_II}
\end{eqnarray}
The outage probability can be evaluated using the results obtained from \eqref{eq_J_R_I}, \eqref{eq_J_R_2}, \eqref{eq_J_D_I}, and \eqref{eq_J_D_II} in \eqref{eq_Pout_simplfied}.


