\subsection{Parameter estimation via approximate development}
The objective function to be maximized is the expectation of log-likelihood \cite{Bishop:2006}:

text:
\begin{equation}
  \label{eq:joint}
	\text{\small $ p(\mathbf{D,Z}) = \left\{ \prod^{|D^1|}_{d=1} p(z^1_{d})\cdot p(d^1|z^1_{d}) \right\} \left\{ \prod^{T}_{t=2}\prod^{|D^t|}_{d=1} p(z^{t}_d) \cdot p(d^t|z^{t}_{d}, z^{t-1}_{d}) \right\}$}
\end{equation}

\begin{align}{}
	&\text{\small $ \mathbb{E}(\tilde{\mathcal{L}})	=   \sum_{i=1}^{|D^1|} \sum_{k=1}^{K} p(z^1_{i}=k | d^1=i) \log \{ p(z^1_{i}=k)p(d^1=i | z^1_{i}=k) \}$} \nonumber\\
	&\text{\small $ \setlength{\thickmuskip}{0mu} +  \sum_{t=2}^{T} \sum_{i=1}^{|D^t|} \sum_{k=1}^{K} \sum_{k'=1}^{K} p(z^t_{i}=k, z^{t-1}_{i}=k' | d^t=i) \log \{ p(z^t_{i}=k)p(d^t=i | z^t_{i}=k, z^{t-1}_{i}=k')  \}$}
\end{align}

\begin{align}{}
	\label{eq:bayes}
\text{$\small\setlength{\thickmuskip}{0mu}\setlength{\thinmuskip}{0mu}\setlength{\medmuskip}{0mu} p(d^t=i | z^t_i=k, z^{t-1}_i=k') = \prod_{m=1}^{M_{i}^{t}} \frac{p(z^t_{i}=k | w^t_{im}, z^{t-1}_i=k') p(z^{t-1}_i=k' | w^t_{im}) p(w^t_{im})}{p(z^t_{i}=k, z^{t-1}_i=k')}$}
\end{align}

