text:
\begin{equation}
d s^2 = \frac{\rho^2 \Delta}{\Sigma^2}\, d t^2 -
\frac{\sin^2\! \theta}{\rho^2} \Sigma^2 \, ( d \varphi - \omega\, d t)^2
\label{Kerr}
- \frac{ \rho^2}{\Delta}\, d r^2 - \rho^2 d \theta^2 .
\end{equation}

\begin{equation} \label{Delta}
\rho^2 = r^2 + a^2 \cos^2 \! \theta, \ \ \ \ \
\Delta = r^2 - 2 M r + a^2,
\end{equation}

\begin{equation} \label{geodKerr1}
\rho^2 \frac{d t}{d \lambda } = \frac{1}{\Delta}
\left( \Sigma^2 E - 2 M r a J \right), \ \ \
\rho^2 \frac{d \varphi}{d \lambda } = \frac{1}{\Delta}
\left( 2 M r a E + \frac{S\, J}{\sin^2\! \theta} \right),
\end{equation}

\begin{equation} \label{geodR}
R = \Sigma^2 E^2 - \frac{S\, J^2}{\sin^2 \theta } - 4 M r a E J -
\Delta \left[ m^2 \rho^2 + \Theta \right],
\end{equation}

\begin{equation}
p_i  = \frac{\partial L}{\partial \left( \frac{d x^i}{ d \lambda } \right) }
= g_{ik} \frac{d x^k}{d \lambda } ,
\label{Lpdef}
\end{equation}

\begin{equation}
p_t = E , \ \ \ p_r = - \sigma_r \frac{\sqrt{R}}{\Delta} , \ \ \
p_\theta = - \sigma_\theta \sqrt{\Theta} , \ \ \ p_\varphi = - J .
\label{pkovi}
\end{equation}

\begin{equation}
J_{\pm} (r,\theta) = \frac{\sin \theta}{S} \left[ - 2 r M a E \sin \theta \pm
\sqrt{ \Delta \left( \rho^4 E^2 - (m^2 \rho^2 + \Theta) S \right)} \right].
\label{Jpm}
\end{equation}

\begin{equation} \label{JgErg}
J \le E \left[ \frac{M r_1(\theta) }{a} + a \sin^2 \! \theta \left(
1 - \frac{m^2}{2 E^2} - \frac{\Theta}{4 M r_1(\theta) E^2} \right) \right].
\end{equation}

\begin{equation} \label{lHmdd}
J \le \frac{\sin \theta}{- S} \left[ 2 r M a E \sin \theta -
\sqrt{ \Delta \left( \rho^4 E^2 - (m^2 \rho^2 + \Theta) S \right)} \right].
\end{equation}

nexttext:
    The lower frontier $E$ corresponds to $R=0$.
    As it is seen from~(\ref{EVnHS}) negative energies in ergosphere can be
only in case of the negative value of $J$ of the angular momentum of
the particle.


\vspace{4mm}

{\centering \section{\large  Influence of the falling particles on
the rotation of \newline the black hole}
\label{secBReac}}

    In case of $r$ approaching the horizon $ r_H$ one obtains
(for $\theta \ne 0, \pi$) from~(\ref{lHmdd}) and~(\ref{EVnHS}) inequalities

text:
\begin{equation}
E \ge \frac{1}{\Sigma^2} \left[ 2 M r a J + \sqrt{ \Delta \left(
\frac{ \rho^4 J^2 }{\sin^2\! \theta } + \left( m^2 \rho^2 + \Theta \right)
\Sigma^2 \right) } \right].
\label{EVnHS}
\end{equation}

prevtext:
    The lower frontier $E$ corresponds to $R=0$.
    As it is seen from~(\ref{EVnHS}) negative energies in ergosphere can be
only in case of the negative value of $J$ of the angular momentum of
the particle.


\vspace{4mm}

{\centering \section{\large  Influence of the falling particles on
the rotation of \newline the black hole}
\label{secBReac}}

    In case of $r$ approaching the horizon $ r_H$ one obtains
(for $\theta \ne 0, \pi$) from~(\ref{lHmdd}) and~(\ref{EVnHS}) inequalities

text:
\begin{equation} \label{JgEH}
J \le J_H = \frac{ 2 M r_H E}{a}, \ \ \ \
E \ge \frac{a J}{ 2 M r_H}.
\end{equation}

\begin{equation} \label{AP2}
A' - A = \frac{ J - A E^2 - 2 A M E}{(M+E)^2 } \le
\frac{ J_H - A E^2 - 2 A M E}{(M+E)^2 } =
\frac{2 E \left( M \displaystyle \frac{r_H}{a} - a \right) -
\displaystyle E^2 \frac{a}{M}}{(M+E)^2 }.
\end{equation}

\begin{equation} \label{AP2d}
A' - A \approx \frac{2 E \left( M \displaystyle \frac{r_H}{a} - a \right) -
\displaystyle E^2 \frac{a}{M}}{(M+E)^2 } \approx
\frac{2 E \left( M \displaystyle \frac{r_H}{a} - a \right)}{(M+E)^2 }.
\end{equation}

\begin{equation} \label{AP2dd}
A' - A = \frac{2 (1+ \sqrt{1-A} ) m M - A m^2 - 2 A mM}{(M+m)^2 }
\approx \frac{2 mM}{(M+m)^2 } \left( 1 - A + \sqrt{1-A} \right) > 0,
\end{equation}

\begin{equation}
E =0, \ \ \ \rho^2 \frac{d t }{d \lambda } = -
\frac{2 r M a J }{\Delta} > 0 \ \ \ \Rightarrow \ \ J < 0 ,
\label{E0Qdtl}
\end{equation}

\begin{equation}
\frac{d \varphi}{d t}  = \frac{S(r)}{2 r M a \sin^2 \! \theta}
= \frac{r^2 - 2 r M + a^2 \cos^2\! \theta}{2 r M a \sin^2 \! \theta}.
\label{omE0}
\end{equation}

\begin{equation}
E =0 \ \ \Rightarrow \ \ \frac{d r}{d t} = \frac{ \sigma_r \Delta }{2 M r a}
\sqrt{ \frac{-S}{\sin^2\! \theta} - \Delta \frac{ m^2 \rho^2 + \Theta}{J^2} }
\label{rtE0}
\end{equation}

\begin{equation}
\frac{d r}{d t} \to \frac{\sigma_r \Delta \sqrt{-S} }{2 M r a \sin \theta},
\ \ \ \ J \to - \infty .
\label{rtE0li}
\end{equation}

