\begin{equation}
\bm{J} = p \bm{u},
\end{equation}

\begin{align}
N^2 = -\frac{g}{\rho_0} \frac{d \rho_0}{d z}.
\label{N}
\end{align}

\begin{align}
E =& \frac{\rho_0}{2} ( u^2 + w^2 ) - \frac{\rho^2 g}{ 2 \, d \rho_0 / d z} \label{energy},
\end{align}

\begin{align}
\frac{\partial E}{\partial t} + \nabla \cdot \bm{J} =& 0 \label{encons}.
\end{align}

\begin{align}
\bm{J} = u p \bm{\hat{x}} + w p \bm{\hat{z}}\,, \label{J}
\end{align}

\begin{equation}
\begin{split}
\frac{\partial}{\partial x} \frac{\partial u}{\partial t} + \frac{\partial}{\partial z} \frac{\partial w}{\partial t} = \\
&\phantom{=} \hspace{-2cm} \frac{\partial}{\partial x} \left( -\frac{1}{\rho_0} \frac{\partial p}{\partial x} \right) + \frac{\partial}{\partial z} \left( -\frac{1}{\rho_0} \frac{\partial p}{\partial z} - \frac{\rho}{\rho_0} g \right)=0\,
\end{split}
 \label{divacc}
\end{equation}

\begin{align}
\frac{\partial^2 p}{\partial x^2} + \frac{\partial^2 p}{\partial z^2} + \frac{N^2}{g} \frac{\partial p}{\partial z}
=& - N^2 \rho  -  g \frac{\partial \rho}{\partial z}. 
\label{peqn}
\end{align}

\begin{align}
p(x,z) = q(x,z) \exp{ \left[ -\frac{1}{2g} \int^{z}{dz' N^2(z')}\right] }\,.
\label{pressure}
\end{align}

\begin{align}
\begin{split}
\frac{\partial^2 q}{\partial x^2} + \frac{\partial^2 q}{\partial z^2} - \left( \frac{N}{g} \frac{\partial N}{\partial z} + \frac{N^4}{4g^2} \right) q  = \\
&\phantom{=} \hspace{-5cm} - \left( N^2 \rho  +  g \frac{\partial \rho}{\partial z} \right) \exp{ \left[ \frac{1}{2g} \int^{z}{dz' N^2(z')}\right] }\,,
\end{split}
 \label{qeqn}
\end{align}

\begin{align}
w = \frac{g}{N^2 \rho_0} \frac{\partial \rho}{\partial t}\,.
 \label{w}
\end{align}

\begin{align}
u = - \int^{x} \!\!\! dx\, \frac{\partial w}{\partial z}  = - \int^{x} \!\!\!  dx \,  \frac{\partial}{\partial z} \left( \frac{g}{N^2 \rho_0} \frac{\partial \rho}{\partial t} \right) \,.
 \label{u}
\end{align}

