\end{cases}
\label{Green}
\end{align}

\begin{align}
G_k^{z>z'}(z,z')\,\bigg|_{z=z'} = G_k^{z<z'}(z,z')\,\bigg|_{z=z'}\,, 
\label{Gk1} \\
\frac{\partial}{\partial z} G_k^{z>z'}(z,z') \, \bigg|_{z=z'} = 1 + \frac{\partial}{\partial z} G_k^{z<z'}(z,z') \, \bigg|_{z=z'}\,.
 \label{Gk2}
