\title{Regional boundary controllability of time fractional  diffusion processes}

\author[dhu]{Fudong Ge}
\ead{gefd2011@gmail.com}
\author[ucmerced]{YangQuan Chen\corref{cor}}
\ead{ychen53@ucmerced.edu}
\author[dhutwo]{Chunhai Kou}
