\title[Characterization of dual Riesz fusion bases by using approximate duals]
 {Characterization of dual Riesz fusion bases by using approximate duals}





\author[A. A. Arefijamaal]{Ali Akbar Arefijamaal}
\address{Department of Mathematics and Computer Sciences, Hakim Sabzevari University, Sabzevar, Iran.}
\email{arefijamaal@hsu.ac.ir; arefijamaal@gmail.com}




