   \title{Confronting uncertainties in stellar physics \\ II. exploring differences in main-sequence stellar evolution tracks}
	\titlerunning{CUSP II -- stellar tracks}

   \author{R. J. Stancliffe\inst{1} \and L. Fossati\inst{2,1} \and J.-C. Passy\inst{1} \and F. R. N. Schneider\inst{1,3}}


   \institute{Argelander-Institut f\"ur Astronomie, University of Bonn, Auf dem H\"ugel 71, D-53121 Bonn, Germany
