\subsection{Analyzing and solving}\label{Analyzing}
The constructed optimal control problem in section \ref{Modeling} is an optimization problem with partial differential equation constraints, and it can be analyzed using the adjoint method \cite{Hinze2009}. In this paper, the optimal control problem is solved by the finite element method. To use
the linear elements for the partial differential equations, the Navier-Stokes equations and convection-diffusion equation are stabilized using the generalized least squares (GLS) and the streamline upwind Petrov-Galerkin (SUPG) technologies, respectively \cite{Donea2003}. Then the stabilized weak forms are

text:
\begin{equation}\label{WeakNS}
\begin{split}
        & \int_\Omega \rho {\mathbf} u \cdot \nabla {\mathbf} u \cdot {\mathbf} v + \int_\Omega \left[-p {\mathbf} I + \eta \left( \nabla {\mathbf} u + \nabla {\mathbf} u^\mathrm{T} \right) \right] : \nabla {\mathbf} v \,\mathrm{d}\Omega - \int_\Omega {\varepsilon \over {\lambda_D^2}} \psi \nabla \phi \cdot {\mathbf} v \,\mathrm{d}\Omega - \int_\Omega q \nabla \cdot {\mathbf} u \,\mathrm{d}\Omega \\
        & + \sum_{i=1}^{N_e} \int_{\Omega_i} \tau_{GLS} \nabla p \cdot \nabla q \,\mathrm{d}\Omega = 0,
        ~\forall {\mathbf} v \in \mathbf{H}_1\left(\Omega\right),~\forall q \in \mathrm{L}_2\left(\Omega\right)\\
        & {\mathbf} u = {\mathbf} u_i, ~\mathrm{on}~\Gamma_i \\
        & {\mathbf} u = {\mathbf} 0,~\mathrm{on}~\Gamma_w
\end{split}
\end{equation}

\begin{equation}\label{WeakCD}
\begin{split}
        & \int_\Omega {\mathbf} u \cdot \nabla c~s \,\mathrm{d}\Omega + \int_\Omega D \nabla c \cdot \nabla s \,\mathrm{d}\Omega + \sum_{i=1}^{N_e} \int_{\Omega_i} \tau_{SUPG} {\mathbf} u \cdot \nabla s \left({\mathbf} u \cdot \nabla c - D\nabla^2 c \right) \,\mathrm{d}\Omega = 0, \\
        & \forall s \in \mathrm{H}_1\left(\Omega\right)\\
        & c = c_i,~\mathrm{on}~\Gamma_i
\end{split}
\end{equation}

\begin{equation}\label{AdjCD}
\begin{split}
        & \int_\Omega \left({\mathbf} u \cdot \nabla s~c_a + \nabla s \cdot \nabla c_a \right) \,\mathrm{d}\Omega + \sum_{i=1}^{N_e} \int_{\Omega_i} \tau_{SUPG} {\mathbf} u \cdot \nabla c_a \left( {\mathbf} u \cdot \nabla s - D\nabla^2 s \right) \,\mathrm{d}\Omega \\
        & + \int_{\Gamma_o} 2 \left(c - c_a\right)s\,\mathrm{d}\Gamma \bigg/ \int_{\Gamma_i} \left(c_r - c_a\right)^2\,\mathrm{d}\Gamma = 0,~\forall s \in \mathrm{H}_1\left(\Omega\right) \\
        & c_a = 0,~\mathrm{on}~\Gamma_i
\end{split}
\end{equation}

