\begin{eqnarray}
i \frac{d c_n}{dt} & = & \sum_{m} \mathcal{H}^{(S)}_{n,m} c_m+\sum_{\alpha=1}^{M} \tilde{\rho}_{n,\alpha} a_{\alpha} \\
i \frac{d a_{\alpha}}{dt} & = & \sum_{\beta=1}^M \mathcal{H}^{(A)}_{\alpha,\beta} a_{\beta}+\sum_{n} {\rho}_{\alpha,n} c_{n}. 
\end{eqnarray}
