&&\left(b_1 \Theta _f\left(-2 b_1{}^2 e^{b_1 \tau } \left(-1+e^{\tau  R}\right)+e^{\tau  \left(b_1+R\right)} \left(2 \Phi _f{}^2-\left(4+a_1\right)
\Phi _f \Phi _g+\left(2+a_1\right) \Phi _g{}^2+2 \left(4+a_1\right) \Theta _f\Theta _g-a_1 \Phi _g R\right)-\right.\right.\\
&&e^{b_1 \tau } \left(2 \Phi _f{}^2-\left(4+a_1\right) \Phi _f \Phi _g+\left(2+a_1\right) \Phi _g{}^2+2 \left(4+a_1\right) \Theta _f\Theta _g+a_1
\Phi _g R\right)+a_1 \left(-2 \Theta _f\Theta _g+\Phi _g \left(\Phi _f-\Phi _g+R\right)\right)+\\
&&\left.a_1 e^{\tau  R} \left(2 \Theta _f\Theta _g+\Phi _g \left(-\Phi _f+\Phi _g+R\right)\right)\right)+\\
&&4 a_1 e^{\frac{1}{2} \tau  \left(b_1+R\right)} \Theta _f\left(\left(-b_1{}^2+\Phi _f{}^2-\Phi _f \Phi _g+2 \Theta _f\Theta _g\right) R \text{Cosh}\left[\frac{1}{2}
\tau  R\right] \text{Sinh}\left[\frac{b_1 \tau }{2}\right]+\right.\\
&&\left.\left.\left.\left(b_1{}^2 \Phi _g \text{Cosh}\left[\frac{b_1 \tau }{2}\right]+\Phi _f \left(-b_1{}^2+R^2\right) \text{Sinh}\left[\frac{b_1
\tau }{2}\right]\right) \text{Sinh}\left[\frac{1}{2} \tau  R\right]\right)\right)\right)/\left(2 b_1 R \left(-b_1{}^2+R^2\right)\right),
\end{eqnarray*}


\begin{eqnarray*}
&&k_{gf}=\hspace{14 cm}\text{(B.3)}\\
&&\left(2 e^{\frac{1}{2} \left(-b_1+\Phi _f+\Phi _g\right) \tau } \Theta _g \left(-a_1 \left(\left(b_1+\Phi _f-\Phi _g\right) \left(b_1+\Phi _g\right)-2
\Theta _f \Theta _g\right) R \text{Cosh}\left[\frac{1}{2} \tau  R\right] \text{Sinh}\left[\frac{b_1 \tau }{2}\right]+\right.\right.\\
&&\left(b_1 \left(-\left(b_1+\Phi _f-\Phi _g\right) \left(b_1-\left(1+a_1\right) \Phi _f+\Phi _g\right)+2 \left(2+a_1\right) \Theta _f \Theta
_g\right) \text{Cosh}\left[\frac{b_1 \tau }{2}\right]-\right.\\
&&\left.\left.\left.\left(b_1+a_1 \Phi _g\right) \left(b_1{}^2-\left(\Phi _f-\Phi _g\right){}^2-4 \Theta _f \Theta _g\right) \text{Sinh}\left[\frac{b_1
\tau }{2}\right]\right) \text{Sinh}\left[\frac{1}{2} \tau  R\right]\right)\right)/\left(b_1 R \left(-b_1{}^2+R^2\right)\right),
\end{eqnarray*}


\begin{eqnarray*}
&&k_{gg}=\hspace{14 cm}\text{(B.4)}\\
&&\left(e^{\frac{1}{2} \tau  \left(-2 b_1+\Phi _f+\Phi _g-R\right)} \right.\\
&&\left(b_1 \left(a_1 \Theta _f \Theta _g \left(-\Phi _f-\Phi _g+R\right)+a_1 e^{\tau  R} \Theta _f \Theta _g \left(\Phi _f+\Phi _g+R\right)+b_1{}^2
e^{b_1 \tau } \left(\left(-1+e^{\tau  R}\right) \Phi _f+\Phi _g-R-e^{\tau  R} \left(\Phi _g+R\right)\right)+\right.\right.\\
&&e^{b_1 \tau } \left(\Phi _f{}^3-\Phi _g{}^3-\left(4+a_1\right) \Phi _g \Theta _f \Theta _g+\Phi _g{}^2 R-\left(-4+a_1\right) \Theta _f \Theta
_g R+\Phi _f{}^2 \left(-3 \Phi _g+R\right)+\right.\\
&&\left.\Phi _f \left(3 \Phi _g{}^2-\left(-4+a_1\right) \Theta _f \Theta _g-2 \Phi _g R\right)\right)+\\
&&e^{\tau  \left(b_1+R\right)} \left(-\Phi _f{}^3+\Phi _g{}^3+\left(4+a_1\right) \Phi _g \Theta _f \Theta _g+\Phi _g{}^2 R-\left(-4+a_1\right)
\Theta _f \Theta _g R+\Phi _f{}^2 \left(3 \Phi _g+R\right)+\right.\\
&&\left.\left.\Phi _f \left(-3 \Phi _g{}^2+\left(-4+a_1\right) \Theta _f \Theta _g-2 \Phi _g R\right)\right)\right)+\\
&&4 a_1 e^{\frac{1}{2} \tau  \left(b_1+R\right)} \left(R \left(-b_1{}^2 \Phi _g+\left(\Phi _f-\Phi _g\right){}^2 \Phi _g-\left(\Phi _f-3 \Phi
_g\right) \Theta _f \Theta _g\right) \text{Cosh}\left[\frac{1}{2} \tau  R\right] \text{Sinh}\left[\frac{b_1 \tau }{2}\right]+\right.\\
&&\left.\left.\left.\left.\left(b_1{}^2 \Theta _f \Theta _g \text{Cosh}\left[\frac{b_1 \tau }{2}\right]+\left(b_1{}^2-\left(\Phi _f-\Phi _g\right){}^2-4
\Theta _f \Theta _g\right) \left(\left(\Phi _f-\Phi _g\right) \Phi _g-\Theta _f \Theta _g\right) \text{Sinh}\left[\frac{b_1 \tau }{2}\right]\right)
\text{Sinh}\left[\frac{1}{2} \tau  R\right]\right)\right)\right)\right/\\
&&\left(2 b_1 R \left(-b_1{}^2+R^2\right)\right) \,,
\end{eqnarray*}

where $R$ is defined as,
\begin{eqnarray*}
R = \sqrt{\left(\Phi _f-\Phi _g\right){}^2+4 \Theta _f \Theta _g}\,. \hspace{8 cm}\text{(B.5)}
\end{eqnarray*}

