\section{Preliminaries}
This section deals with some preliminaries on tangent and normal cones, rough differential equations, and a notion of invariant set for the rough Equation (\ref{main_equation}).





\subsection{Tangent and normal cones}
The definitions and propositions stated in this subsection come from Hiriart-Urrut and Lemar\'echal \cite{HUL01}, Chapter A, and Aubin et al. \cite{ABSP11}, Chapter 18.





\begin{definition}\label{polar_cone}
The polar set of a closed cone $K\subset\mathbb R^d$ is the closed cone

\begin{displaymath}
K^{\circ} =
\{s\in\mathbb R^m :
\forall\delta\in K
\textrm{$,$ }
\langle s,\delta\rangle\leqslant 0\}.
\end{displaymath}

\end{definition}





\begin{definition}\label{bipolar_cone}
The bipolar set of a closed cone $K\subset\mathbb R^d$ is the closed cone $K^{\circ\circ} := (K^{\circ})^{\circ}$.
\end{definition}

\noindent
Let $S\subset\mathbb R^m$ be a nonempty closed set with $m\in\mathbb N^*$, and consider $x\in S$.





\begin{definition}\label{tangent_cone}
A vector $\delta\in\mathbb R^m$ is tangent to $S$ at $x$ if and only if there exists a sequence $(x_n)_{n\in\mathbb N}$ of elements of $S$, and a real sequence $(t_n)_{n\in\mathbb N}$ such that when $n\rightarrow\infty$,

\begin{displaymath}
\|x_n - x\|\rightarrow 0
\textrm{$,$ }
t_n\downarrow 0
\textrm{ and }
\left\|
\frac{x_n - x}{t_n} -\delta\right\|
\rightarrow 0.
\end{displaymath}

The set of the tangent vectors to $S$ at $x$ is a closed cone of $\mathbb R^m$, called the tangent cone to $S$ at $x$, and denoted by $T_S(x)$.
\end{definition}





\begin{remark}\label{tangent_cone_interior}
If $x\in\normalfont{\textrm{int}}(S)$, then $T_S(x) = T_S(x)^{\circ\circ} =\mathbb R^m$.
\end{remark}





\begin{proposition}\label{orthogonal_projections_closed}
For every $y\in\mathbb R^m$ and $y^*\in\Pi_S(y)$ (see (\ref{set_of_projections}) for a definition), $y - y^*\in T_S(y^*)^{\circ}$.
\end{proposition}

\noindent
See \cite {AUBIN91}, Proposition 3.2.3 p. 85.

















































\begin{definition}\label{normal_cone}
A vector $s\in\mathbb R^m$ is normal to $S$ at $x$ if and only if,

\begin{displaymath}
\langle s,\delta\rangle\leqslant 0
\textrm{ $;$ }
\forall\delta\in T_S(x).
\end{displaymath}

The set of the normal vectors to $S$ at $x$ is the normal cone to $S$ at $x$, denoted by $N_S(x)$.
\end{definition}

\noindent
Let $C\subset\mathbb R^m$ be a nonempty closed convex set, and consider $x\in C$.





\begin{proposition}\label{tangent_cone_properties}
The tangent cone $T_C(x)$ is a closed convex cone such that $C\subset\{x\} + T_C(x)$.
\end{proposition}





\begin{proposition}\label{normal_cone_convex}
A vector $s\in\mathbb R^m$ is normal to $C$ at $x$ if and only if,

\begin{displaymath}
\langle s,y - x\rangle
\leqslant 0
\textrm{ $;$ }
\forall y\in C.
\end{displaymath}

\end{proposition}





\begin{proposition}\label{polar_characterizations_cones}
$T_C(x)^{\circ} = N_C(x)$, $N_C(x)^{\circ} = T_C(x)$ and $T_C(x)^{\circ\circ} = T_C(x)$.
\end{proposition}





\begin{proposition}\label{orthogonal_projection_convex}
Consider $y\in\mathbb R^m$.

\begin{enumerate}
 \item The set $\Pi_C(y)$ has only one element, denoted by $p_C(y)$.
 \item For every $z\in\mathbb R^m$, $z = p_C(y)$ if and only if, $z\in C$ and $y - z\in N_C(z)$.
 \item The map $z\in\mathbb R^m\longmapsto p_C(z)$ is Lipschitz continuous from $\mathbb R^m$ into itself.
\end{enumerate}

\end{proposition}





\begin{remark}\label{convex_R_m}
A nonempty closed subset $S$ of $\mathbb R^m$ is convex if and only if $|\Pi_S(y)| = 1$ for every $y\in\mathbb R^m$ (see Klee \cite{KLEE61}).
\end{remark}





\subsection{Rough differential equations}
The definitions and propositions stated in the major part of this subsection come from Lyons and Qian \cite{LQ02}, Friz and Victoir \cite{FV10}, or Friz and Hairer \cite{FH14}.
\\
\\
First, let's state the definitions and propositions which are necessary to introduce the notion of geometric rough path (see Friz and Victoir \cite{FV10}, chapters 7 and 8).
\\
\\
Consider $\alpha\in ]0,1]$, $l,m,N\in\mathbb N^*$ and

\begin{displaymath}
\Delta_T :=\{(s,t)\in\mathbb R_{+}^{2} : 0\leqslant s < t\leqslant T\}.
\end{displaymath}





\begin{proposition}\label{Holder_functions}
A function $x : [0,T]\rightarrow\mathbb R^m$ is $\alpha$-H\"older continuous if and only if,

\begin{displaymath}
\sup_{(s,t)\in\Delta_T}\frac{\|x_t - x_s\|}{|t - s|^{\alpha}} <\infty.
\end{displaymath}

The set of all such functions is denoted by $C^{\alpha\normalfont{\textrm{-H\"ol}}}([0,T],\mathbb R^m)$. The map

\begin{displaymath}
x\in C^{\alpha\normalfont{\textrm{-H\"ol}}}([0,T],\mathbb R^m)
\longmapsto
\|x\|_{\alpha\normalfont{\textrm{-H\"ol}},T} :=
\sup_{(s,t)\in\Delta_T}\frac{\|x_t - x_s\|}{|t - s|^{\alpha}}
\end{displaymath}

is a semi-norm on $C^{\alpha\normalfont{\textrm{-H\"ol}}}([0,T],\mathbb R^m)$.
\end{proposition}

\noindent
\textbf{Notations :}

\begin{itemize}
 \item The tensor product on $\mathbb R^m$ is denoted by $\otimes$ :

 \begin{displaymath}
 (x\otimes y)_{i,j} :=
 x^{(i)}y^{(j)}
 \textrm{ $;$ }
 \forall i,j\in\llbracket 1,m\rrbracket.
 \end{displaymath}

 \item The truncated tensor algebra of level $N$ over $\mathbb R^m$ is denoted by \mbox{$T^N(\mathbb R^m)$ :}

 \begin{displaymath}
 T^{N}(\mathbb R^m) :=
 \bigoplus_{k = 0}^{N}
 (\mathbb R^m)^{\otimes k}
 \end{displaymath}

 with $(\mathbb R^m)^{\otimes 0} :=\mathbb R$. For every $k\in\llbracket 0,N\rrbracket$, the canonical projection of any $x\in T^{N}(\mathbb R^m)$ onto $(\mathbb R^m)^{\otimes k}$ is denoted by $x^{(k)}$.
\end{itemize}





\begin{definition}\label{step_N_signature}
Consider $x\in C^{1\normalfont{\textrm{-H\"ol}}}([0,T],\mathbb R^m)$. The step-$N$ signature of $x$ is the map $S_N(x) : [0,T]\rightarrow T^N(\mathbb R^m)$ defined by

\begin{displaymath}
S_N(x)_t :=
\left(1,\int_{0 < u < t}dx_u,\dots,\int_{0 < u_1 <\dots < u_N < t}dx_{u_1}\otimes
\dots\otimes dx_{u_N}\right)
\end{displaymath}

for every $t\in [0,T]$.
\end{definition}





\begin{proposition}\label{step_N_group}
Equipped with the tensor product $\otimes$, the subset

\begin{displaymath}
G^N(\mathbb R^m) :=
\{S_N(\gamma)_1
\textrm{ $;$ }
\gamma\in C^{1\normalfont{\textrm{-H\"ol}}}([0,1],\mathbb R^m)\}
\end{displaymath}

of $T^N(\mathbb R^m)$ is a Lie group, called the step-$N$ free nilpotent group over $\mathbb R^m$.
\end{proposition}

\noindent
\textbf{Notation.} For any map $\mathbf x : [0,T]\rightarrow G^N(\mathbb R^m)$,

\begin{displaymath}
\mathbf x_{s,t} :=\mathbf x_{s}^{-1}\otimes\mathbf x_t
\textrm{ $;$ }
\forall (s,t)\in\Delta_T.
\end{displaymath}





\begin{proposition}\label{Carnot_norm}
The map

\begin{displaymath}
d_{\normalfont\textrm C} : (x,y)\in (G^N(\mathbb R^m))^2
\longmapsto
\|x^{-1}\otimes y\|_{\normalfont\textrm C}
\end{displaymath}

with

\begin{displaymath}
\|x\|_{\normalfont\textrm C} :=
\inf\left\{
\int_{0}^{1}\|d\gamma_t\|
\textrm{ $;$ }
\gamma\in C^{1\normalfont{\textrm{-H\"ol}}}([0,1],\mathbb R^m)
\textrm{ with }
S_N(\gamma)_1 = x\right\}
\end{displaymath}

for every $x\in G^N(\mathbb R^m)$, is a Carnot-Carath\'eodory metric on the Lie group $G^N(\mathbb R^m)$.
\end{proposition}





\begin{proposition}\label{Holder_enhanced_paths}
A map $\mathbf x : [0,T]\rightarrow G^{[1/\alpha]}(\mathbb R^m)$ is $\alpha$-H\"older continuous if and only if,

\begin{displaymath}
\|\mathbf x\|_{\alpha\normalfont\textrm{-H\"ol},T} :=
\sup_{(s,t)\in\Delta_T}\frac{d_{\normalfont\textrm C}(\mathbf x_s,\mathbf x_t)}{|t - s|^{\alpha}} <\infty.
\end{displaymath}

The set of all such maps is denoted by $C^{\alpha\normalfont{\textrm{-H\"ol}}}([0,T],G^{[1/\alpha]}(\mathbb R^m))$. The map

\begin{displaymath}
d_{\alpha\normalfont{\textrm{-H\"ol}},T} :
(\mathbf x,\mathbf y)\in
(C^{\alpha\normalfont{\textrm{-H\"ol}}}([0,T],G^{[1/\alpha]}(\mathbb R^m)))^2
\longmapsto
\sup_{(s,t)\in\Delta_T}\frac{d_{\normalfont\textrm C}(\mathbf x_{s,t},\mathbf y_{s,t})}{|t - s|^{\alpha}}
\end{displaymath}

is a metric on $C^{\alpha\normalfont{\textrm{-H\"ol}}}([0,T],G^{[1/\alpha]}(\mathbb R^m))$.
\end{proposition}





\begin{definition}\label{geometric_rough_paths}
Equipped with the metric $d_{\alpha\normalfont{\textrm{-H\"ol}},T}$, the subset

\begin{displaymath}
G\Omega_{\alpha,T}(\mathbb R^m) :=
\overline{\{S_{[1/\alpha]}(x)
\textrm{ $;$ }x\in C^{1\normalfont{\textrm{-H\"ol}}}([0,T],\mathbb R^m)\}}^{d_{\alpha\normalfont{\textrm{-H\"ol}},T}}
\end{displaymath}

of $C^{\alpha\normalfont{\textrm{-H\"ol}}}([0,T],G^{[1/\alpha]}(\mathbb R^m))$ is the space of the geometric $\alpha$-rough paths from $[0,T]$ into $G^{[1/\alpha]}(\mathbb R^m)$.
\end{definition}

\noindent
Now, let's focus on the rough differential equations.
\\
\\
Consider a map $f :\mathbb R^l\rightarrow\mathcal M_{l,m}(\mathbb R)$ and $\mathbf x\in G\Omega_{\alpha,T}(\mathbb R^m)$.





\begin{definition}\label{RDE_solutions}
A function $y\in C^{\alpha\normalfont{\textrm{-H\"ol}}}([0,T],\mathbb R^l)$ is a solution on $[0,T]$ of the rough differential equation $dy = f(y)d\mathbf x$ with $y_0\in\mathbb R^l$ as initial condition, if and only if there exists a sequence $(x^n)_{n\in\mathbb N}$ of elements of $C^{1\normalfont{\textrm{-H\"ol}}}([0,T],\mathbb R^l)$ such that

\begin{displaymath}
\lim_{n\rightarrow\infty}
d_{\alpha\normalfont{\textrm{-H\"ol}},T}(S_{[1/\alpha]}(x^n),\mathbf x) = 0
\end{displaymath}

and

\begin{displaymath}
\lim_{n\rightarrow\infty}
\|y - y^n\|_{\infty,T} = 0
\end{displaymath}

where, for every $n\in\mathbb N$, $y^n$ is a solution on $[0,T]$ of the ordinary differential equation $dy^n = f(y^n)dx^n$ with $y_0$ as initial condition.
\end{definition}

\noindent
\textbf{Notation.} For every $\gamma > 0$, $\lfloor\gamma\rfloor$ is the largest integer strictly smaller than $\gamma$.





\begin{definition}\label{gamma_Lipschitz_maps}
Consider $\gamma > 0$ and a non empty closed set $V\subset\mathbb R^l$. The map $f$ is $\gamma$-Lipschitz continuous (in the sense of Stein) from $V$ into $\mathcal M_{l,m}(\mathbb R)$ if and only if :

\begin{enumerate}
 \item $f|_{V}\in C^{\lfloor\gamma\rfloor}(V,\mathcal M_{l,m}(\mathbb R))$.
 \item $f,Df,\dots,D^{\lfloor\gamma\rfloor}f$ are bounded on $V$.
 \item $D^{\lfloor\gamma\rfloor}f$ is $(\gamma -\lfloor\gamma\rfloor)$-H\"older continuous from $\mathbb R^l$ into $\mathcal L(V^{\otimes\lfloor\gamma\rfloor},\mathcal M_{l,m}(\mathbb R))$ (i.e. there exists $C > 0$ such that for every $x,y\in V$,

 \begin{displaymath}
 \|D^{\lfloor\gamma\rfloor}f(y) -
 D^{\lfloor\gamma\rfloor}f(x)\|_{\mathcal L(V^{\otimes\lfloor\gamma\rfloor},\mathcal M_{l,m}(\mathbb R))}
 \leqslant
 C\|y - x\|^{\gamma -\lfloor\gamma\rfloor}\textrm{).}
 \end{displaymath}

\end{enumerate}

The set of all such maps is denoted by $\normalfont{\textrm{Lip}}^{\gamma}(V,\mathcal M_{l,m}(\mathbb R))$.
\end{definition}





\begin{definition}\label{locally_gamma_Lipschitz_maps}
Consider $\gamma > 0$. The map $f$ is locally $\gamma$-Lipschitz continuous from $\mathbb R^l$ into $\mathcal M_{l,m}(\mathbb R)$, when for every nonempty compact set $K\subset\mathbb R^l$, $f$ is $\gamma$-Lipschitz continuous from $K$ into $\mathcal M_{l,m}(\mathbb R)$. The set of all such maps is denoted by $\normalfont{\textrm{Lip}}_{\normalfont{\textrm{loc}}}^{\gamma}(\mathbb R^l,\mathcal M_{l,m}(\mathbb R))$.
\end{definition}





\begin{theorem}\label{RDE_local_existence}
If there exists $\gamma > 1/\alpha$ such that $f\in\normalfont{\textrm{Lip}}_{\normalfont{\textrm{loc}}}^{\gamma - 1}(\mathbb R^l,\mathcal M_{l,m}(\mathbb R))$, then the rough differential equation $dy = f(y)d\mathbf x$ with $y_0\in\mathbb R^l$ as initial condition has at least one solution $y$ on $[0,T]$, or on $[0,\tau[$ with $\tau\in ]0,T]$ and

\begin{displaymath}
\lim_{t\uparrow\tau}
\|y_t\| =\infty.
\end{displaymath}

\end{theorem}

\noindent
In the sequel, the map $f$ satisfies the following assumption.





\begin{assumption}\label{non_explosion}
There exists $\gamma\in ]1/\alpha,[1/\alpha] + 1[$ such that :

\begin{enumerate}
 \item $f\in\normalfont{\textrm{Lip}}_{\normalfont{\textrm{loc}}}^{\gamma - 1}(\mathbb R^l,\mathcal M_{l,m}(\mathbb R))$.
 \item $f$ is Lipschitz continuous from $\mathbb R^l$ into $\mathcal M_{l,m}(\mathbb R)$.
 \item $D^{[1/\alpha]}f$ is $(\gamma - [1/\alpha])$-H\"older continuous from $\mathbb R^l$ into $\mathcal L(V^{\otimes [1/\alpha]},\mathcal M_{l,m}(\mathbb R))$.
\end{enumerate}

\end{assumption}





\begin{theorem}\label{RDE_global_existence}
Under Assumption \ref{non_explosion}, the rough differential equation $dy = f(y)d\mathbf x$ with $y_0\in\mathbb R^l$ as initial condition has at least one solution on $[0,T]$. Moreover, if $f\in\normalfont{\textrm{Lip}}_{\normalfont{\textrm{loc}}}^{\gamma}(\mathbb R^l,\mathcal M_{l,m}(\mathbb R))$, that solution is unique and denoted by $\pi_f(0,y_0,\mathbf x)$.
\end{theorem}





\begin{remark}\label{uniqueness_alpha_1}
Note that if $\alpha = 1$, then Assumption \ref{non_explosion}.(2) ensures the existence and uniqueness of the solution on $[0,T]$ of the ordinary differential equation $dy = f(y)d\mathbf x^{(1)}$ with $y_0\in\mathbb R^l$ as initial condition (see Friz and Victoir \cite{FV10}, Theorem 3.8).
\end{remark}





\begin{definition}\label{Euler_scheme}
Let $D := (t_0,\dots,t_n)$ be a dissection of $[0,T]$ with $n\in\mathbb N^*$, and assume that $f\in C^{[1/\alpha]}(\mathbb R^l,\mathcal M_{l,m}(\mathbb R))$. The Euler scheme $\widehat y^n := (\widehat y_{t_0}^{n},\dots,\widehat y_{t_n}^{n})$ for the rough differential equation $dy = f(y)d\mathbf x$ with $y_0\in\mathbb R^l$ as initial condition along the dissection $D$ is defined by

\begin{displaymath}
\widehat y_{t_k}^{n} :=
\mathfrak E^{\mathbf x_{t_{k - 1},t_k}}\circ
\dots\circ\mathfrak E^{\mathbf x_{t_0,t_1}}y_0
\textrm{ $;$ }
\forall k\in\llbracket 1,n\rrbracket
\end{displaymath}

with

\begin{displaymath}
\mathfrak E^gx :=
x +\mathcal E_f(x,g)
\end{displaymath}

and

\begin{displaymath}
\mathcal E_f(x,g) :=
\sum_{k = 1}^{[1/\alpha]}
\sum_{i_1,\dots,i_k = 1}^{m}
f_{i_1}\dots f_{i_k}I(x)g^{(k),i_1,\dots,i_k}
\end{displaymath}

for every $g\in G^{[1/\alpha]}(\mathbb R^m)$ and $x\in\mathbb R^l$, and where $I$ denotes the identity map from $\mathbb R^l$ into itself.
\end{definition}





\begin{theorem}\label{Euler_scheme_convergence}
Let $D := (t_0,\dots,t_n)$ be a dissection of $[0,T]$ with $n\in\mathbb N^*$. Under Assumption \ref{non_explosion}, if $f\in\normalfont{\textrm{Lip}}_{\normalfont{\textrm{loc}}}^{\gamma}(\mathbb R^d,\mathcal M_{d,e}(\mathbb R))$, then there exists $C > 0$ and $\theta > 1$ such that :

\begin{displaymath}
\|\pi_f(0,y_0;\mathbf x)_T -\widehat y_{T}^{n}\|
\leqslant CT|D|^{\theta - 1}.
\end{displaymath}

\end{theorem}





\subsection{Rough differential equations with a drift term}
In this subsection, the previous results are applied to Equation (\ref{main_equation}).
\\
\\
In the sequel, the signal $w$, and the functions $b$ and $\sigma$ satisfy the following assumptions.





\begin{assumption}\label{geometric_rough_path_w}
There exists $\mathbf w\in G\Omega_{p,T}(\mathbb R^e)$ such that $\mathbf w^{(1)} = w$.
\end{assumption}





\begin{assumption}\label{non_explosion_b_sigma}
There exists $\gamma\in ]1/\alpha,[1/\alpha] + 1[$ such that :

\begin{enumerate}
 \item $b\in\normalfont{\textrm{Lip}}_{\normalfont{\textrm{loc}}}^{\gamma - 1}(\mathbb R^d)$ and $\sigma\in\normalfont{\textrm{Lip}}_{\normalfont{\textrm{loc}}}^{\gamma - 1}(\mathbb R^d,\mathcal M_{d,e}(\mathbb R))$.
 \item $b$ (resp. $\sigma$) is Lipschitz continuous from $\mathbb R^d$ into itself (resp. $\mathcal M_{d,e}(\mathbb R)$).
 \item $D^{[1/\alpha]}b$ (resp. $D^{[1/\alpha]}\sigma$) is locally $(\gamma - [1/\alpha])$-H\"older continuous from $\mathbb R^d$ into $\mathcal L((\mathbb R^d)^{\otimes [1/\alpha]},\mathbb R^d)$ (resp. $\mathcal L((\mathbb R^d)^{\otimes [1/\alpha]},\mathcal M_{d,e}(\mathbb R))$).
\end{enumerate}

\end{assumption}

\noindent
At Subsection 3.2, in order to use Theorem \ref{Euler_scheme_convergence}, the following assumption on $b$ and $\sigma$ will be required.





\begin{assumption}\label{gamma_Lipschitz_b_sigma}
$b\in\normalfont{\textrm{Lip}}_{\normalfont{\textrm{loc}}}^{\gamma}(\mathbb R^d)$ and $\sigma\in\normalfont{\textrm{Lip}}_{\normalfont{\textrm{loc}}}^{\gamma}(\mathbb R^d,\mathcal M_{d,e}(\mathbb R))$.
\end{assumption}

\noindent
Let $f_{b,\sigma} :\mathbb R^d\rightarrow\mathcal M_{d,e + 1}(\mathbb R)$ be the map defined by :

\begin{displaymath}
f_{b,\sigma}(x) :=
\sum_{k = 1}^{d}
b^{(k)}(x)e_{k,1} +
\sum_{l = 2}^{e + 1}
\sum_{k = 1}^{d}
\sigma_{k,l}(x)e_{k,l}
\textrm{ $;$ }
\forall x\in\mathbb R^d.
\end{displaymath}

By Assumption \ref{non_explosion_b_sigma}, $f_{b,\sigma}$ satisfies Assumption \ref{non_explosion} with $l = d$ and $m = e + 1$.
\\
\\
Let $W : [0,T]\rightarrow\mathbb R^{e + 1}$ be the signal defined by :

\begin{displaymath}
W_t :=
te_1 +
\sum_{k = 2}^{e + 1}
w_{t}^{(k - 1)}e_k
\textrm{ $;$ }
\forall t\in [0,T].
\end{displaymath}

By Friz and Victoir \cite{FV10}, Theorem 9.26, there exists at least one $\mathbb W\in G\Omega_{p,T}(\mathbb R^{e + 1})$ such that $\mathbb W^{(1)} = W$.
\\
\\
In the rough paths framework, $dy = f_{b,\sigma}(y)d\mathbb W$ with $y_0\in\mathbb R^d$ as initial condition is the appropriate formulation of Equation (\ref{main_equation}).
\\
\\
Under assumptions \ref{geometric_rough_path_w} and \ref{non_explosion_b_sigma}, by Theorem \ref{RDE_global_existence}, the rough differential equation $dy = f_{b,\sigma}(y)d\mathbb W$ with $y_0\in\mathbb R^d$ as initial condition has at least one solution $y$ on $[0,T]$. Precisely, there exists a sequence $(W^n)_{n\in\mathbb N}$ of elements of $C^{1\textrm{-H\"ol}}([0,T],\mathbb R^{e + 1})$ such that

prevtext:

 \item The distance between $x\in\mathbb R^m$ and a nonempty closed set $S\subset\mathbb R^d$ is

 \begin{displaymath}
 d_S(x) :=
 \inf_{y\in S}\|x - y\|.
 \end{displaymath}

 \item The space of the matrices of size $d\times e$ is denoted by $\mathcal M_{d,e}(\mathbb R)$. The (euclidean) matrix norm on $\mathcal M_{d,e}(\mathbb R)$ is denoted by $\|.\|_{\mathcal M_{d,e}(\mathbb R)}$. If $d = e$, then $\mathcal M_d(\mathbb R) :=\mathcal M_{d,e}(\mathbb R)$. The canonical basis of $\mathcal M_{d,e}(\mathbb R)$ is denoted by $(e_{k,l})_{(k,l)\in\llbracket 1,d\rrbracket\times\llbracket 1,e\rrbracket}$.
 \item Let $E$ and $F$ be two vector spaces. The space of the linear maps from $E$ into $F$ is denoted by $\mathcal L(E,F)$. If $E = F$, then $\mathcal L(E) :=\mathcal L(E,F)$.
 \item The space of the continuous functions from $[0,T]$ into $\mathbb R^d$ is denoted by $C^0([0,T],\mathbb R^d)$ and equipped with the uniform norm $\|.\|_{\infty,T}$.
 \item The space of the continuous functions $l$ from $]0,t_0[$ into $]0,\infty[$ with $t_0 > 0$, and such that

 \begin{displaymath}
