text:
\begin{equation}
\label{eq:wpKcorrect}
w_p^{\rm true}(r_p, r_{\parallel,  \rm max}) = w_p(r_p, r_{\parallel, \rm max}) + 2Kr_{\parallel, \rm max} \, .
\end{equation}

\begin{equation}
\label{eq:Kest}
K \simeq \frac{\sum_i RR(r_i)\xi^{\rm model}(r_i)}{\sum_i RR(r_i)} = \frac{\sum_i RR(r_i)\xi^{\rm model}(r_i)}{N_R(N_R-1)} \, .
\end{equation}

