\[
 t_e(X) =
  \begin{cases}
   X\cup\{e\} & \text{if } e\notin X \text{ and } X\cup\{e\}\in\mathcal{L} \\
   X\setminus\{e\} & \text{if } e\in X \text{ and } X\setminus\{e\}\in\mathcal{L} \\
   X       & \text{otherwise}
  \end{cases}
\]

