\end{equation}

\begin{equation}
t = \tilde t-h(\tilde r), \qquad  \tilde r=\frac{r}{{\bar\Omega}}  \qquad\textrm{and}\qquad ds^2 = \Omega^2d\tilde s^2.
\end{equation}

