In \cite{biha11} A. Bejancu introduced the notion of CR-submanifolds, which is the generalization of invariant and anti-invariant submanifolds. In  \cite{biha3} B. Y. Chen introduced  the  notion of slant submanifolds as a generalization of CR-submanifolds.
\begin{definition}
A submanifold $M^{n}$ of a Bochner Kahler manifold $\overline{M}^{2m}$ is said to be a slant submanifold if for any $x \in M$ and $X \in T_{x}M$, the angle between $JX$ and $T_{x}M$ is constant, i.e., the angle does not depend on the choice of $p \in M$ and $X \in T_{p}M$. The angle $\theta \in [0, \frac{\pi}{2}]$ is called the slant angle of $M$ in $\overline{M}$.

Invariant and anti-invariant submanifolds are the slant submanifolds with slant angle $\theta = 0$ and $\theta =\frac{\pi}{2}$ respectively and when $0< \theta < \frac{\pi}{2}$, then slant submanifold is called proper slant submanifold.
\end{definition}

\section{B. Y. Chen inequalities}
In this section, we obtain B. Y. Chen inequalities for submanifolds of Bochner Kahler manifolds.

First we have,
\begin{theorem}
Let $M^{n}$ be a submanifold of  a Bochner Kahler manifold $\overline{M}^{2m}$. then, for each point $p \in M^{n}$ and each plane section $\pi \subset T_{p}M$, we have
\begin{eqnarray}\label{t1}
\nonumber k(\pi) \geq \left(\frac{5n^{2}+31n+26+3\|P\|^{2}}{2(2n+2)(2n+4)}\right)\rho - \frac{n^{2}(n-2)}{2(n-1)}\|H\|^{2} - \frac{6}{2(2n+4)}Ric(e_{i},Je_{j})g(e_{i},Je_{j}).{\cr}
\end{eqnarray}
Equality  holds if and only if there exists an orthonormal basis $\{e_{1} ,e_{2}, ... , e_{n}\}$ of $T_{p}M$ and orthonormal basis $\{e_{n+1}, e_{n+2} , ... , e_{2m}\}$ of $T^{\perp}M$ such that the shape operator takes the following forms

\begin{eqnarray*}\label{t2}
A_{n+1} =
 \begin{pmatrix}
  a & 0 & 0 & \cdots & 0 {\cr}
  0 & b & 0 & \cdots & 0 {\cr}
  0 & 0 & \mu &\cdots & 0 {\cr}
  \vdots & \vdots & \vdots  & \ddots & \vdots  {\cr}
  0 & 0 & 0 & \cdots & \mu
 \end{pmatrix}  , a+b = \mu
\end{eqnarray*}
and
\begin{eqnarray*}\label{t3}
A_{r} =
 \begin{pmatrix}
  h_{11}^{r} &  h_{12}^{r} & 0 & \cdots & 0 {\cr}
   h_{12}^{r} & - h_{11}^{r} & 0 & \cdots & 0 {\cr}
  0 & 0 & 0 &\cdots & 0 {\cr}
  \vdots & \vdots & \vdots  & \ddots & \vdots  {\cr}
  0 & 0 & 0 & \cdots & 0
 \end{pmatrix}  , r = n+2, ..., 2m.
\end{eqnarray*}
\end{theorem}
\begin{proof}
Using Gauss equation, the Riemannian curvature tensor of $M$ is given  by
\begin{eqnarray*}\label{p1}
\nonumber R(X,Y,Z,W) &=& L(Y,Z)g(X,W) - L(X,Z)g(Y,W) + L(X,W)g(Y,Z)
                          \nonumber {\cr} && - L(Y,W)g(X,Z)  + M(Y,Z)g(JX,W) - M(X,Z)g(JY,W)
                          \nonumber {\cr} && - M(X,W)g(JY,Z) - M(Y,W)g(JX,Z)  - 2M(X,Y)(JZ,W)
                           \nonumber {\cr} && - 2M(Z,W)g(JX,Y) + g(h(X,W), h(Y,Z)) - g(h(X,Z), h(Y,W))
\end{eqnarray*}
for any  X, Y, Z, W $\in$ TM.
\begin{eqnarray*}\label{p2}
\sum_{i,j} R(e_{i}, e_{j}, e_{j}, e_{i}) &=& L(e_{j},e_{j})g(e_{i},e_{i}) - L(e_{i},e_{j})g(e_{j},e_{i}) + L(e_{i},e_{i})g(e_{j},e_{j})
                          \nonumber {\cr} && - L(e_{j},e_{i})g(e_{i},e_{j})  + M(e_{j},e_{j})g(Je_{i},e_{i}) - M(e_{i},e_{j})g(Je_{j},e_{i})
                          \nonumber {\cr} && - M(e_{i},e_{i})g(Je_{j},e_{j}) - M(e_{j},e_{i})g(Je_{i},e_{j})  - 2M(e_{i},e_{j})(Je_{j},e_{i})
                           \nonumber {\cr} && - 2M(e_{j},e_{i})g(Je_{i},e_{j}) + g(h(e_{i},e_{i}), h(e_{j},e_{j})) - g(h(e_{i},e_{j}), h(e_{j},e_{i}))
\end{eqnarray*}
\begin{eqnarray*}\label{p3}
 \hspace{3cm}                       &=& L(e_{j},e_{j})g(e_{i},e_{i}) - L(e_{i},e_{j})g(e_{j},e_{i}) + L(e_{i},e_{i})g(e_{j},e_{j})
                         \nonumber {\cr} && - L(e_{j},e_{i})g(e_{i},e_{j})  - L(e_{j},Je_{j})g(Je_{i},e_{i}) + L(e_{i},Je_{j})g(Je_{j},e_{i})
                          \nonumber {\cr} && + L(e_{i},Je_{i})g(Je_{j},e_{j}) + L(e_{j},Je_{i})g(Je_{i},e_{j}) + 2L(e_{i},Je_{j})(Je_{j},e_{i})
                           \nonumber {\cr} && + 2L(e_{j},Je_{i})g(Je_{i},e_{j}) + g(h(e_{i},e_{i}), h(e_{j},e_{j})) - g(h(e_{i},e_{j}), h(e_{j},e_{i})).
\end{eqnarray*}
Using (\ref{a8}), (\ref{a9}) and (\ref{a11}), we have
\begin{eqnarray*}\label{p4}
\sum_{i,j} R(e_{i}, e_{j}, e_{j}, e_{i})&=&  2nL(e_{i}, e_{i}) - 2L(e_{i}, e_{j})g(e_{i}, e_{j}) + 6L(e_{i}, Je_{j})g(e_{i}, Je_{j})
                                    \nonumber {\cr} &&  + n^{2}\|H\|^{2} - \|h\|^{2}.
\end{eqnarray*}
 Last equation simplifies to,
\begin{eqnarray}\label{p5}
2\rho  &=&  2(n-1)L(e_{i}, e_{i}) + 6L(e_{i}, Je_{j})g(e_{i}, Je_{j}) + n^{2}\|H\|^{2} - \|h\|^{2}.
\end{eqnarray}
 Combining (\ref{a6}) and (\ref{p5}), we have
\begin{eqnarray*}\label{p6}
\hspace{2cm}2\rho  &=& \frac{2(n-1)}{2n+4}Ric(e_{i}, e_{i}) - \frac{2(n-1)\rho}{2(2n+2)(2n+4)}g(e_{i}, e_{i})
        \nonumber {\cr} &&   + \frac{6}{2n+4} Ric(e_{i}, Je_{j})g(e_{i}, Je_{j}) - \frac{6 \rho}{2(2n+2)(2n+4)} g(e_{i}, Je_{j})g(e_{i}, Je_{j})
        \nonumber {\cr} &&   + n^{2}\|H\|^{2} - \|h\|^{2}.
\end{eqnarray*}










or,
\begin{eqnarray*}\label{p9}
\hspace{2cm}2\rho  &=& \frac{6n^{2} + 2n - 8 - 6\|P\|^{2}}{2(2n + 2)(2n + 4)}\rho + \frac{6}{2n+4} Ric(e_{i}, Je_{j})g(e_{i}, Je_{j})
       \nonumber {\cr} && + n^{2}\|H\|^{2} - \|h\|^{2}.
\end{eqnarray*}
or,

\begin{eqnarray*}\label{p10}
(2 - \frac{6n^{2} + 2n - 8 - 6\|P\|^{2}}{2(2n + 2)(2n + 4)})\rho = \frac{6}{2n + 4}Ric(e_{i}, Je_{j})g(e_{i}, Je_{j}) + n^{2}\|H\|^{2} - \|h\|^{2}.
\end{eqnarray*}
Denoting by
\begin{eqnarray*}\label{p11}
\epsilon = (2 - \frac{6n^{2} + 2n - 8 - 6\|P\|^{2}}{2(2n + 2)(2n + 4)})\rho - \frac{n^{2}(n - 2)}{n - 1}\|H\|^{2} - \frac{6}{2n + 4}Ric(e_{i}, Je_{j})g(e_{i}, Je_{j}).
\end{eqnarray*}
We obtain
\begin{eqnarray*}\label{p12}
\epsilon =  n^{2}\|H\|^{2} - \|h\|^{2} - \frac{n^{2}(n - 2)}{n - 1}\|H\|^{2}.
\end{eqnarray*}
or,
\begin{eqnarray}\label{p13}
n^{2}\|H\|^{2} = (n - 1)(\epsilon + \|h\|^{2}).
\end{eqnarray}

For chosen orthonormal basis, the above equation takes the form

\begin{eqnarray}\label{p14}
\left(\sum_{i=1}^{n}h_{ii}^{n+1}\right)^{2} = (n-1)\left[\sum_{i=1}^{n}(h_{ii}^{n+1})^{2} + \sum_{i\ne j}(h_{ij}^{n+1})^{2} + \sum_{r=n+1}^{2m}\sum_{i,j=1}^{n}(h_{ij})^{2} + \epsilon \right].
\end{eqnarray}

Using lemma 1 in (\ref{p14}), we have

\begin{eqnarray}\label{p15}
2h_{11}^{n+1}h_{22}^{n+1} \geq \sum_{i\ne j}(h_{ij}^{n+1})^{2} + \sum_{r=n+1}^{2m}\sum_{i,j=1}^{n}(h_{ij}^{r})^{2} + \epsilon.
\end{eqnarray}

On the other hand, from Gauss equation we obtain

\begin{eqnarray}\label{p16}
k(\pi) = L(e_{2}, e_{2}) + L(e_{1},e_{1}) + g(h(e_{1}, e_{1}), h(e_{2},e_{2}) - g(h(e_{1},e_{2}), h(e_{2},e_{1})).
\end{eqnarray}
Combing (\ref{a6}) and (\ref{p16}), we derive
\begin{eqnarray}\label{p17}
k(\pi) = \frac{4n+3}{(2n+2)(2n+4)}\rho + h_{11}^{n+1}h_{22}^{n+1} + \sum_{r=n+2}^{2m}h_{11}^{r}h_{22}^{r} - \sum_{r=n+1}^{2m}(h_{12}^{r})^{2}.
\end{eqnarray}


Incorporating (\ref{p15}) in (\ref{p17}), we arrive at the inequality
\begin{eqnarray*}\label{p18}
k(\pi) &\geq& \frac{1}{2}\sum_{i\ne j}(h_{ij}^{n+1})^{2} + \frac{1}{2}\sum_{r=n+1}^{2m}\sum_{i,j=1}^{n}(h_{ij}^{r})^{2} + \frac{1}{2}\epsilon  \nonumber {\cr} && + \frac{4n+3}{(2n+2)(2n+4)}\rho + \sum_{r=n+2}^{2m}h_{11}^{r}h_{22}^{r} - \sum_{r=n+1}^{2m}(h_{12}^{r})^{2}.
\end{eqnarray*}
Which implies that
\begin{eqnarray*}\label{p19}
k(\pi) \geq \frac{4n+3}{(2n+2)(2n+4)}\rho + \frac{1}{2}\epsilon.
\end{eqnarray*}
Or,

\begin{eqnarray}\label{p20}
\nonumber k(\pi) \geq \left(\frac{5n^{2}+31n+26+3\|P\|^{2}}{2(2n+2)(2n+4)}\right)\rho - \frac{n^{2}(n-2)}{2(n-1)}\|H\|^{2} - \frac{6}{2(2n+4)}Ric(e_{i},Je_{j})g(e_{i},Je_{j}). {\cr}
\end{eqnarray}
if the equality in (\ref{t1}) at a point $p$ holds, then the inequality (\ref{p20}) become equality. In this case, we have

$\begin{cases}
h_{1j}^{n+1} = h_{2j}^{n+1} = h_{ij}^{n+1} = 0, \hspace{1cm} i \neq j >2,{\cr}
h_{ij}^{r} = 0, \forall i \neq j,\hspace{.5cm}i,j = 3, ..., 2m, \hspace{.5cm}r= n+1, ..., 2m,{\cr}
h_{11}^{r}+h_{22}^{r} = 0, \forall r=n+2, ..., 2m,\hspace{2cm}{\cr}
h_{11}^{n+2}+h_{22}^{n+1} = ... = h_{11}^{m}+h_{22}^{m}=0.{\cr}
\end{cases}$

Now, if we choose $e_{1},e_{2}$ such that $h_{12}^{n+1}$= 0 and we denote by $a = h_{11}^{r}, b = h_{22}^{r}$, $\mu = h_{33}^{n+1} = ... = h_{33}^{r}$. Therefore by choosing the suitable orthonormal basis the shape operators take the desired forms.
\end{proof}
