\begin{equation}\label{H}
\vec{H} = \left(
              \begin{array}{c}
                k_{-a}(d^2+l^2) -a(2k_n + k_a(d+l))+ k_d(d+l) \\
                -k_{-a}d^2 + a(k_n + k_a d) -k_d d\\
                -k_{-a}l^2 + a(k_n + k_a l) -k_d l \\
              \end{array}
            \right),
\end{equation}

\begin{equation}\label{G}
\bm{G} = \frac{1}{\sqrt{V}} \left(
           \begin{array}{cc}
             \sqrt{k_{-a}d^2 + a(k_a d + k_n) + k_d d} & \sqrt{k_{-a}l^2 + a(k_a l + k_n) + k_d l} \\
             -\sqrt{k_{-a}d^2 + a(k_a d + k_n) + k_d d} &  0\\
             0 & -\sqrt{k_{-a}l^2 + a(k_a l + k_n) + k_d l} \\
           \end{array}
         \right),
\end{equation}

\begin{equation}\label{Ito-change}
df(\vec{x}) = [\sum_i H_i(\vec{x}) \partial_i f(\vec{x}) +
\frac{1}{2}\sum_{i,j} [\bm{G}\bm{G}^T]_{ij} \partial_i \partial_j
f(\vec{x})] dt + \sum_{ij} \bm{G}(\vec{x})_{ij} \partial_i
f(\vec{x}) dW_j(t).
\end{equation}
