\begin{equation}
 \Pi_E(k) = \sum_{k=1}^K \sum_{k \leq |\bm k| < k+1} \widehat{\bm u}^*(\bm k) {\cdot} \widehat{(\bm u \times \bm \omega)}(\bm k).
\end{equation}

