\begin{eqnarray}
\frac{dn_{\chi}}{dt} + 3Hn_{\chi}=-\langle \sigma_{\mbox{\tiny{eff}}} v\rangle\left(n^2_{\chi}-n^2_{eq}\right),
\end{eqnarray} 
