\section{The quantities entering the uncertainty relation (\ref{unc-rel 2p 2})}
\label{E(A,A2,B,B2,[A,B])}

We write explicitly the quantities entering the uncertainty relation 
(\ref{unc-rel 2p 2}) for the case of a general ``density operator" $\hat{\pi}$, 
{\em not necessarily positive definite or Hermitian}:

\numparts
\begin{eqnarray}
\langle \hat{A} \rangle_{\hat{\pi}}
&=& \sum_{q_1,q_2}\langle q_1 q_2|\hat{\pi} |q_1 q_2 \rangle 
\sin \frac{2\pi(q_1 - q_2)}{N} \; ,
\label{<A>} \\
\langle \hat{A^2} \rangle_{\hat{\pi}} 
&=& \sum_{q_1,q_2}\langle q_1 q_2|\hat{\pi} |q_1 q_2 \rangle 
\sin^2 \frac{2\pi(q_1 - q_2)}{N} \; ,
\label{<A2>} \\
\langle \hat{B} \rangle_{\hat{\pi}}
&=& \frac{i}{2}\sum_{q_1,q_2}
\left[\left\langle q_1 q_2|\hat{\pi} |q_1+1, q_2-1 \right\rangle 
-\left\langle q_1 q_2|\hat{\pi} |q_1-1, q_2+1 \right\rangle \right] \; ,
\label{<B>} \\
\langle \hat{B}^2 \rangle_{\hat{\pi}}
&=& \frac{1}{2} {\mathrm{Tr}} \, \hat{\pi}
-\frac{1}{4}\sum_{q_1,q_2}
\left[\left\langle q_1 q_2|\hat{\pi} |q_1+2, q_2-2 \right\rangle 
+ \left\langle q_1 q_2|\hat{\pi} |q_1-2, q_2+2 \right\rangle \right] \; ,
\nonumber \\
\label{<B2>} 
\end{eqnarray}
\label{<A>,...,<B2>}
\endnumparts

and

\numparts
\begin{eqnarray}
\langle \hat{x} \hat{z} \rangle_{\hat{\pi}}
&=& \sum_{q_1,q_2}
\left\langle q_1 q_2|\hat{\pi} |q_1+1, q_2-1 \right\rangle 
\omega^{q_1-q_2} \; ,
\label{<xz>} \\
\langle \hat{z}^{\dagger} \hat{x} \rangle_{\hat{\pi}}
&=& \sum_{q_1,q_2}
\left\langle q_1 q_2|\hat{\pi} |q_1+1, q_2-1 \right\rangle 
\omega^{q_2-q_1-2} \; ,
\label{<z+x>} \\
\langle \hat{x}^{\dagger} \hat{z} \rangle_{\hat{\pi}}
&=& \sum_{q_1,q_2}
\left\langle q_1 q_2|\hat{\pi} |q_1-1, q_2+1 \right\rangle 
\omega^{q_1-q_2} \; ,
\label{<x+z>}  \\
\langle \hat{z}^{\dagger} \hat{x}^{\dagger} \rangle_{\hat{\pi}}
&=& \sum_{q_1,q_2}
\left\langle q_1 q_2|\hat{\pi} |q_1-1, q_2+1 \right\rangle 
\omega^{-q_1+q_2+2} \; .
\label{<z+x+>}
\end{eqnarray}
\label{<XZ>,<Z+X>}
\endnumparts




