\begin{align}
  \label{keeling_eq:8}
  \mathcal{Z}[{\mathbf{{f}}}, {\mathbf{{g}}}]
  &=
  \int \mathcal{D}(\bar \psi, \psi) \exp\left(
    i S[\bar \psi, \psi]
    + 
    i  S_j[\bar \psi, \psi] 
  \right), \\
  S_j[\bar \psi, \psi]  &=
  \sum_{{\mathbf{k}}, {\mathbf{q}}}
  \bar{\Psi}^T_{{\mathbf{{k+q}}}}
  \begin{pmatrix}
    g_i({\mathbf{q}}) & f_i({\mathbf{q}}) + g_i({\mathbf{q}}) \\
    f_i({\mathbf{q}}) - g_i({\mathbf{q}}) & - g_i({\mathbf{q}})
  \end{pmatrix}
  \Psi_{{\mathbf{k}}} \;
  \gamma_i(2{\mathbf{k}} + {\mathbf{q}}),
  \nonumber
\end{align}
